CREATE TABLE `blog` (
  `id` bigint NOT NULL,
  `content` longtext ,
  `created_date` date DEFAULT NULL,
  `description` varchar(3000)  DEFAULT NULL,
  `image_banner` varchar(255) DEFAULT NULL,
  `num_view` int DEFAULT NULL,
  `primary_blog` bit(1) DEFAULT NULL,
  `title` varchar(3000)  DEFAULT NULL,
  `user_id` bigint DEFAULT NULL
) ;

--
-- Đang đổ dữ liệu cho bảng `blog`
--

INSERT INTO `blog` (`id`, `content`, `created_date`, `description`, `image_banner`, `num_view`, `primary_blog`, `title`, `user_id`) VALUES
(3, '<p><strong>1. Tham khảo&nbsp;y&ecirc;u cầu&nbsp;kiểm so&aacute;t dịch bệnh ở địa phương trước chuyến&nbsp;bay</strong></p>\n<p>Trong m&ugrave;a dịch, mỗi địa phương sẽ c&oacute; những y&ecirc;u cầu kh&aacute;c nhau đối với kh&aacute;ch đến từ ngoại tỉnh, do vậy việc cập nhật th&ocirc;ng tin nhanh v&agrave; ch&iacute;nh x&aacute;c th&ocirc;ng tin&nbsp;kiểm so&aacute;t dịch bệnh từng địa phương trước khi thực hiện chuyến bay l&agrave;&nbsp; rất quan trọng.</p>\n<p>Hiện nay, hầu hết c&aacute;c tỉnh đều đ&atilde; bỏ y&ecirc;u cầu c&aacute;ch ly tập trung, h&agrave;nh kh&aacute;ch c&oacute; thể thuận tiện hơn trong việc di chuyển. Theo đ&oacute;, qu&yacute; kh&aacute;ch c&oacute; thể tham khảo th&ocirc;ng tin kiểm so&aacute;t dịch bệnh tại c&aacute;c địa phương trong nước cũng như quy định c&aacute;ch ly được iVIVU cập nhật li&ecirc;n tục&nbsp;<a href=\"https://www.ivivu.com/quy-dinh-tham-gia-chuyen-bay-giai-doan-covid\" target=\"_blank\" rel=\"noopener\"><strong>TẠI Đ&Acirc;Y</strong></a><a href=\"https://docs.google.com/spreadsheets/d/1omYjhDTLoMlVBjO-HMKNrT9qhM2j8y6H38SEXZvaCos/edit#gid=1650040966\" target=\"_blank\" rel=\"noopener\"><strong>&nbsp;</strong></a>&nbsp;để biết th&ecirc;m th&ocirc;ng tin nh&eacute;.</p>\n<table width=\"839\">\n<tbody>\n<tr>\n<td width=\"95\"><strong>Khu vực</strong></td>\n<td width=\"744\"><strong>Nội dung</strong></td>\n</tr>\n<tr>\n<td width=\"95\">H&agrave; Nội</td>\n<td width=\"744\">Đối với h&agrave;nh kh&aacute;ch đủ điều kiện theo quy định của Bộ GTVT v&agrave; Bộ Y tế th&igrave; thực hiện theo d&otilde;i sức khỏe tại nh&agrave; hoặc nơi lưu tr&uacute; v&agrave; thực hiện c&aacute;c biện ph&aacute;p đảm bảo ph&ograve;ng chống dịch Covid-19 theo quy định của Trung ương v&agrave; Th&agrave;nh phố.</td>\n</tr>\n<tr>\n<td width=\"95\">Đ&agrave; Nẵng</td>\n<td width=\"744\">H&agrave;nh kh&aacute;ch đăng k&yacute; trực tuyến tr&ecirc;n app Danang Smart City hoặc&nbsp;<a href=\"https://khaibaoyte.danang.gov.vn/\"><strong>tại đ&acirc;y</strong></a>&nbsp;v&agrave; lưu lại m&atilde; QR được cấp để sử dụng khi di chuyển về TP Đ&agrave; Nẵng.&nbsp;Thực hiện c&aacute;ch ly tại nh&agrave;/nơi lưu tr&uacute; trong v&ograve;ng 14 ng&agrave;y.</td>\n</tr>\n<tr>\n<td width=\"95\">Huế</td>\n<td width=\"744\">Tất cả h&agrave;nh kh&aacute;ch đủ điều kiện theo quy định của Bộ Giao th&ocirc;ng vận tải tại Quyết định số 1776/QĐ-BGTVT được đến/trở về tỉnh Thừa Thi&ecirc;n Huế; đồng thời thực hiện biện ph&aacute;p tự theo d&otilde;i sức khoẻ hoặc c&aacute;ch ly tại nơi cư tr&uacute;, lưu tr&uacute; 07 ng&agrave;y theo quy định của Bộ Y tế tại Quyết định số 879/QĐ-BYT ng&agrave;y 12 th&aacute;ng 3 năm 2020 về việc ban h&agrave;nh &ldquo;Hướng dẫn c&aacute;ch ly tại nơi cư tr&uacute;, lưu tr&uacute; ph&ograve;ng chống dịch COVID-19&Prime; hoặc c&aacute;ch ly tập trung 07 ng&agrave;y nếu kh&ocirc;ng đảm bảo c&aacute;c điều kiện c&aacute;ch ly tại nơi cư tr&uacute;, lưu tr&uacute; theo Quyết định số 879/QĐ-BYT.<br>H&agrave;nh kh&aacute;ch trước khi đến Huế cần khai b&aacute;o&nbsp;<a href=\"https://tuongtac.thuathienhue.gov.vn/khaibao\" rel=\"nofollow\"><strong>tại đ&acirc;y</strong></a>&nbsp;v&agrave; phải được ph&ecirc; duyệt trước khi mua v&eacute;.</td>\n</tr>\n<tr>\n<td width=\"95\">B&igrave;nh Định</td>\n<td width=\"744\">H&agrave;nh kh&aacute;ch thực hiện tự theo d&otilde;i sức khoẻ tại nh&agrave;, nơi lưu tr&uacute; trong 7 ng&agrave;y kể từ ng&agrave;y đến/về địa phương.</td>\n</tr>\n<tr>\n<td width=\"95\">Hải Ph&ograve;ng</td>\n<td width=\"744\">H&agrave;nh kh&aacute;ch đến s&acirc;n bay C&aacute;t Bi phải khai b&aacute;o y tế bằng bản giấy theo mẫu của CDC Hải Ph&ograve;ng&nbsp;<a href=\"https://www.vietnamairlines.com/~/media/Files/VNANew/Travel%20Information/Covid-19/20211011-To-khai-y-te-HPH.pdf\"><strong>tại đ&acirc;y</strong></a><br>Tất cả h&agrave;nh kh&aacute;ch xuống Cảng HKQT C&aacute;t bi tr&ecirc;n c&aacute;c chuyến bay thương mại nội địa v&agrave; lưu tr&uacute; tại Hải Ph&ograve;ng kh&ocirc;ng phải &aacute;p dụng c&aacute;ch ly y tế tập trung.</td>\n</tr>\n<tr>\n<td width=\"95\">Thanh Ho&aacute;</td>\n<td width=\"744\">H&agrave;nh kh&aacute;ch tự c&aacute;ch ly, theo d&otilde;i sức khỏe tại nh&agrave; 7 ng&agrave;y; thực hiện x&eacute;t nghiệm v&agrave; chi trả ph&iacute; x&eacute;t nghiệm 2 lần v&agrave;o ng&agrave;y thứ nhất v&agrave; ng&agrave;y thứ 7 trong thời gian theo d&otilde;i sức khỏe</td>\n</tr>\n<tr>\n<td width=\"95\">Nghệ An</td>\n<td width=\"744\">Đối với những người đến/về từ c&aacute;c khu vực đang thực hiện c&aacute;ch ly y tế (phong tỏa), c&aacute;ch ly x&atilde; hội theo Chỉ thị số 16/CT-TTg: C&aacute;ch ly y tế tại nh&agrave;, nơi lưu tr&uacute; trong 14 ng&agrave;y kể từ ng&agrave;y đến/về địa phương.&nbsp;Tiếp tục tự theo d&otilde;i sức khỏe 14 ng&agrave;y sau khi ho&agrave;n th&agrave;nh c&aacute;ch ly.</td>\n</tr>\n<tr>\n<td width=\"95\">Ki&ecirc;n Giang</td>\n<td width=\"744\">Những người đ&atilde; ti&ecirc;m đủ liều vắc xin ph&ograve;ng COVID-19:&nbsp;tự theo d&otilde;i sức khỏe tại nh&agrave;, nơi lưu tr&uacute; trong 7 ng&agrave;y.</td>\n</tr>\n<tr>\n<td width=\"95\">Quảng Ng&atilde;i</td>\n<td width=\"744\">Đối với người ti&ecirc;m đủ liều vaccine qua 14 ng&agrave;y: &aacute;p dụng c&aacute;ch ly tập trung 7 ng&agrave;y v&agrave; c&aacute;ch ly tại nh&agrave; 7 ng&agrave;y.</td>\n</tr>\n<tr>\n<td width=\"95\">Kh&aacute;nh Ho&agrave;</td>\n<td width=\"744\">H&agrave;nh kh&aacute;ch tự theo d&otilde;i sức khoẻ tại nơi cư tr&uacute;, lưu tr&uacute; 7 ng&agrave;y kể từ ng&agrave;y về địa phương,&nbsp;chủ động th&ocirc;ng b&aacute;o với ch&iacute;nh quyền địa phương nơi cư tr&uacute;, lưu tr&uacute;.</td>\n</tr>\n<tr>\n<td width=\"95\">H&agrave; Tĩnh</td>\n<td width=\"744\">Thực hiện c&aacute;ch ly tập trung hoặc c&aacute;ch ly tại nh&agrave; 7 ng&agrave;y kể từ ng&agrave;y đến/về địa phương; sau khi hết thời gian c&aacute;ch ly tiếp tục tự theo d&otilde;i sức khoẻ tại nh&agrave;, nơi lưu tr&uacute; trong 7 ng&agrave;y tiếp theo.</td>\n</tr>\n<tr>\n<td width=\"95\">C&ocirc;n Đảo</td>\n<td width=\"744\">C&aacute;ch ly y tế tại nh&agrave;, nơi lưu tr&uacute; (nếu điều kiện đảm bảo) hoặc cơ sở y tế tập trung trong 07 ng&agrave;y kể từ ng&agrave;y đến/về địa phương v&agrave; hạn chế tiếp x&uacute;c, tự theo d&otilde;i sức khỏe trong 14 ng&agrave;y tiếp theo.</td>\n</tr>\n<tr>\n<td width=\"95\">Gia Lai</td>\n<td width=\"744\">Đối với h&agrave;nh kh&aacute;ch từ TP.HCM: thực hiện c&aacute;ch ly 7 ng&agrave;y tại kh&aacute;ch sạn, sau khi c&aacute;ch ly tập trung tiếp tục c&aacute;ch ly, tự theo d&otilde;i sức khoẻ tại nh&agrave;/nơi lưu tr&uacute; trong v&ograve;ng 7 ng&agrave;y tiếp theo.</td>\n</tr>\n<tr>\n<td width=\"95\">C&agrave; Mau</td>\n<td width=\"744\">C&aacute;ch ly y tế tại nh&agrave; 21 ng&agrave;y v&agrave; tự theo d&otilde;i sức khoẻ tại nh&agrave; th&ecirc;m 7 ng&agrave;y sau khi c&aacute;ch ly.</td>\n</tr>\n</tbody>\n</table>\n<div><iframe src=\"https://www.ivivu.com/hothotel/?placeslug=viet-nam&amp;number=1&amp;width=481\" width=\"100%\" height=\"315\" scrolling=\"no\"></iframe></div>\n<p>&nbsp;</p>\n<p><strong>C&Aacute;C BƯỚC CẦN THỰC HIỆN KHI THỰC HIỆN CHUYẾN BAY NỘI ĐỊA GIAI ĐOẠN DỊCH COVID-19</strong></p>\n<p>Để đảm bảo t&igrave;nh h&igrave;nh dịch tễ cho c&aacute;c địa phương tiếp nhận h&agrave;nh kh&aacute;ch, v&igrave; sức khoẻ của h&agrave;nh kh&aacute;ch v&agrave; những chuyến bay an to&agrave;n, đi bay giai đoạn th&iacute; điểm của Cục H&agrave;ng kh&ocirc;ng Việt Nam từ 10/10 &ndash; 20/10/2021, h&agrave;nh kh&aacute;ch cần lưu &yacute; một số th&ocirc;ng tin đặc biệt để đảm bảo h&agrave;nh tr&igrave;nh của m&igrave;nh được th&ocirc;ng suốt như sau:</p>\n<p><strong>Bước 1: Trước khi thực hiện chuyến bay:</strong></p>\n<ol>\n<li><strong>Khai b&aacute;o trung thực, đầy đủ th&ocirc;ng tin ngay sau khi mua v&eacute; m&aacute;y bay</strong>.</li>\n</ol>\n<ul>\n<li>Vietnam Airlines:&nbsp;<a href=\"https://www.vietnamairlines.com/vn/vi/register-before-flight\">Khai b&aacute;o th&ocirc;ng tin tại đ&acirc;y</a></li>\n<li>Vietjet Air :&nbsp;<a href=\"https://vietnamkhoemanh.vn/khaibao-yte?step=0\">Khai b&aacute;o th&ocirc;ng tin tại đ&acirc;y</a></li>\n<li>Bamboo Airways :&nbsp;<a href=\"https://forms.office.com/pages/responsepage.aspx?id=VrKSVK6hN0SKKY-nqJMhF0FAPxoMwNBMgKLh-fL9Gp9UQUg4WjFER1YyTVFVQUVRS1hJRUNLOVU2NS4u\">Khai b&aacute;o th&ocirc;ng tin tại đ&acirc;y</a></li>\n</ul>\n<p>H&agrave;nh kh&aacute;ch cần thực hiện khai b&aacute;o trong v&ograve;ng 24 tiếng trước khi khởi h&agrave;nh.</p>\n<ol start=\"2\">\n<li><strong>Khai b&aacute;o y tế trước khi l&agrave;m thủ tục bay</strong>&nbsp;<a href=\"https://tokhaiyte.vn/\"><strong>tại đ&acirc;y</strong></a>&nbsp;trước khi thực hiện chuyến bay.</li>\n<li><strong>Ho&agrave;n thiện bản cam kết ph&ograve;ng chống dịch Covid-19 trước khi l&agrave;m thủ tục bay.</strong></li>\n</ol>\n<ul>\n<li>H&agrave;nh kh&aacute;ch tải mẫu *Bản cam kết*&nbsp;<a href=\"https://d3jyiu4jpn0ihr.cloudfront.net/wp-content/uploads/sites/6/20211009001601/ban-cam-ket.jpg\">tại đ&acirc;y</a></li>\n<li>In, điền đầy đủ v&agrave; ch&iacute;nh x&aacute;c th&ocirc;ng tin, nộp bản cứng cho nh&acirc;n vi&ecirc;n l&agrave;m thủ tục bay của H&atilde;ng tại s&acirc;n bay.</li>\n<li>Đối với kh&aacute;i niệm địa chỉ nơi đến được thể hiện trong bảng cam kết của h&agrave;nh kh&aacute;ch, cần phải điền theo mẫu</li>\n</ul>\n<p>+ Đối với địa phương y&ecirc;u cầu tự theo d&otilde;i: Địa chỉ nơi đến l&agrave; n&oacute;i kh&aacute;ch sẽ lưu tr&uacute;, cư tr&uacute;</p>\n<p>+ Đối với địa phương y&ecirc;u cầu c&aacute;ch ly tập trung: Địa chỉ nơi đến l&agrave; địa điểm c&aacute;ch ly tập trung + phương tiện đ&oacute;n từ s&acirc;n bay được địa phương chấp thuận thể hiện qua c&aacute;c x&aacute;c nhận đặt dịch vụ c&aacute;ch ly, xe đ&oacute;n đ&atilde; c&oacute; ph&ecirc; duyệt cụ thể của địa phương nơi đến.</p>\n<p><strong>Bước 2: L&agrave;m thủ tục tại s&acirc;n bay</strong></p>\n<ul>\n<li>H&agrave;nh kh&aacute;ch cần đảm bảo sức khỏe v&agrave; kh&ocirc;ng tham gia chuyến bay khi c&oacute; c&aacute;c triệu chứng ho, sốt, kh&oacute; thở, đau mỏi cơ, đau r&aacute;t họng&hellip;</li>\n<li>Lu&ocirc;n tu&acirc;n thủ nguy&ecirc;n tắc 5K v&agrave; chuẩn bị c&aacute;c giấy tờ cần thiết trước khi đến quầy l&agrave;m thủ tục tại s&acirc;n bay</li>\n<li>Chuẩn bị sẵn c&aacute;c giấy tờ cần thiết:</li>\n</ul>\n<ol>\n<li><strong>C&aacute;c giấy chứng nhận li&ecirc;n quan đến Covid-19:</strong></li>\n</ol>\n<ul>\n<li>Chứng nhận đ&atilde; ti&ecirc;m đủ 2 mũi vaccine ph&ograve;ng Covid-19 của cơ quan c&oacute; thẩm quyền cấp với liều cuối c&ugrave;ng đ&atilde; được ti&ecirc;m &iacute;t nhất 14 ng&agrave;y trước ng&agrave;y khởi h&agrave;nh. (Thẻ xanh tr&ecirc;n Sổ Sức khoẻ điện tử/PC-COVID Hoặc giấy chứng nhận do cơ sở tiểm chủng cấp) hoặc;</li>\n<li>X&aacute;c nhận của bệnh nh&acirc;n đ&atilde; khỏi bệnh Covid-19 trong v&ograve;ng 6 th&aacute;ng t&iacute;nh đến thời điểm về địa phương.</li>\n</ul>\n<ol start=\"2\">\n<li><strong>Kết quả x&eacute;t nghiệm &acirc;m t&iacute;nh SARS-CoV-2</strong>: bằng phương ph&aacute;p RT-PCR hoặc x&eacute;t nghiệm nhanh kh&aacute;ng nguy&ecirc;n &acirc;m t&iacute;nh trong v&ograve;ng 72 giờ trước khi l&ecirc;n m&aacute;y bay.</li>\n<li><strong>X&aacute;c nhận đ&atilde; mua dịch vụ c&aacute;ch ly trước chuyến bay</strong>: (&Aacute;p dụng với c&aacute;c h&agrave;nh kh&aacute;ch bay đến c&aacute;c địa phương y&ecirc;u cầu c&aacute;ch ly tập trung)</li>\n</ol>\n<p>Đối với c&aacute;c h&agrave;nh kh&aacute;ch hạ c&aacute;nh v&agrave; di chuyển từ s&acirc;n bay đến về lại c&aacute;c địa phương l&acirc;n cận, kh&aacute;ch cần c&oacute; x&aacute;c nhận hợp đồng vận chuyển xe từ c&aacute;c tỉnh l&ecirc;n s&acirc;n bay đ&oacute;n kh&aacute;ch v&agrave; nơi lưu tr&uacute; c&oacute; x&aacute;c nhận của địa phương</p>\n<ol start=\"4\">\n<li><strong>Bảng cam kết ph&ograve;ng chống dịch Covid-19</strong></li>\n</ol>\n<p><strong>&nbsp;</strong><strong>Bước 3: Tr&ecirc;n chuyến bay</strong></p>\n<ul>\n<li><strong>H&agrave;nh kh&aacute;ch lu&ocirc;n đ</strong>eo khẩu trang, s&aacute;t khuẩn tay, hạn chế tiếp x&uacute;c.</li>\n</ul>\n<p><strong>&nbsp;</strong><strong>Bước 3: Sau khi kết th&uacute;c chuyến bay</strong></p>\n<ul>\n<li>Trong qu&aacute; tr&igrave;nh di chuyển từ cảng h&agrave;ng kh&ocirc;ng về nơi cư tr&uacute;, lưu tr&uacute;: lu&ocirc;n thực hiện 5K, sử dụng ứng dụng PC-COVID, hạn chế dừng v&agrave; kh&ocirc;ng tiếp x&uacute;c nơi đ&ocirc;ng người;</li>\n<li>Chủ động th&ocirc;ng b&aacute;o với ch&iacute;nh quyền địa phương nơi cư tr&uacute;, lưu tr&uacute;;</li>\n<li>Tự theo d&otilde;i sức khỏe hoặc thực hiện c&aacute;ch ly tại nơi cư tr&uacute;, lưu tr&uacute; theo quy định cụ thể của từng địa phương về c&aacute;c biện ph&aacute;p ph&ograve;ng chống dịch COVID-19 kể từ ng&agrave;y về địa phương, thực hiện 5K;</li>\n<li>Trường hợp c&oacute; dấu hiệu bất thường về sức khỏe như ho, sốt, kh&oacute; thở, đau r&aacute;t họng, mất vị gi&aacute;c&hellip;th&igrave; th&ocirc;ng b&aacute;o ngay cho cơ quan y tế để theo d&otilde;i v&agrave; triển khai quy tr&igrave;nh xử l&yacute; dịch bệnh theo quy định.</li>\n</ul>\n<p><strong>2.&nbsp;C&acirc;n nhắc&nbsp;điểm đến an to&agrave;n</strong></p>\n<p>Việc ưu ti&ecirc;n cần l&agrave;m khi l&ecirc;n kế hoạch du lịch m&ugrave;a dịch n&agrave;y ch&iacute;nh l&agrave; bạn h&atilde;y c&acirc;n nhắc điểm đến an to&agrave;n. Để biết nơi bạn sắp đến c&oacute; an to&agrave;n hay kh&ocirc;ng, bạn cần phải theo d&otilde;i, cập nhật th&ocirc;ng tin về diễn biến dịch ở địa phương đ&oacute; tr&ecirc;n c&aacute;c phương tiện truyền th&ocirc;ng như ti vi, b&aacute;o đ&agrave;i. H&atilde;y t&igrave;m hiểu về số ca mắc, c&aacute;c ch&iacute;nh s&aacute;ch ph&ograve;ng ngừa dịch v&agrave; khả năng xử l&yacute; khi dịch c&oacute; nguy cơ b&ugrave;ng ph&aacute;t&hellip; sẽ gi&uacute;p bạn đưa ra quyết định tốt nhất cho việc l&ecirc;n kế hoạch sắp tới nh&eacute;.</p>\n<div id=\"attachment_351326\" class=\"wp-caption aligncenter\"><img class=\"wp-image-351326 size-full\" title=\"Ảnh: Mai Ch&acirc;u Lodge\" src=\"https://cdn3.ivivu.com/2021/06/nhung-luu-y-quan-trong-khi-du-lich-mua-dich-ivivu-5.jpg\" alt=\"Ảnh: Mai Ch&acirc;u Lodge\" width=\"1736\" height=\"1157\">\n<p class=\"wp-caption-text\">Ảnh: Mai Ch&acirc;u Lodge</p>\n</div>\n<p><strong>3. Lựa chọn nơi lưu tr&uacute;&nbsp;ph&ugrave;&nbsp;hợp</strong></p>\n<p>Ngo&agrave;i những ti&ecirc;u ch&iacute; về tiện nghi, gi&aacute; cả để lựa chọn cho bạn nơi lưu tr&uacute; ph&ugrave; hợp, du lịch m&ugrave;a dịch bạn cần t&igrave;m hiểu c&aacute;c ch&iacute;nh s&aacute;ch bảo vệ kh&aacute;ch h&agrave;ng v&agrave; nh&acirc;n vi&ecirc;n của kh&aacute;ch sạn,&nbsp;khu nghỉ dưỡng. Cụ thể trước khi đặt ph&ograve;ng, bạn h&atilde;y hỏi bộ phận chăm s&oacute;c kh&aacute;ch h&agrave;ng về những quy định ph&ograve;ng chống dịch ở nơi lưu tr&uacute;. Hiện tại đa số c&aacute;c kh&aacute;ch sạn, khu nghỉ dưỡng ở Việt Nam đều c&oacute; quy định đeo khẩu trang khi đến những&nbsp;khu vực chung như lễ t&acirc;n; bắt buộc khai b&aacute;o y tế khi check-in; ra v&agrave;o kh&aacute;ch sạn, khu nghỉ dưỡng phải thường xuy&ecirc;n rửa tay s&aacute;t khuẩn&hellip;</p>\n<p>Tuy nhi&ecirc;n, bạn cũng cần lưu &yacute; th&ecirc;m về việc kh&aacute;ch sạn c&oacute; cung cấp khẩu trang cho du kh&aacute;ch hay kh&ocirc;ng, biện ph&aacute;p gi&atilde;n c&aacute;ch x&atilde; hội n&agrave;o được thực hiện tại kh&aacute;ch sạn, c&aacute;c khu vực chung tại kh&aacute;ch sạn c&oacute; được thường xuy&ecirc;n s&aacute;t khuẩn hay kh&ocirc;ng, kh&aacute;ch sạn c&oacute; thực hiện check-in kh&ocirc;ng tiếp x&uacute;c hay kh&ocirc;ng&hellip; T&ugrave;y theo mỗi nơi sẽ c&oacute; những ch&iacute;nh s&aacute;ch kh&aacute;c nhau, bạn cần nắm r&otilde; c&aacute;c th&ocirc;ng tin n&agrave;y để bảo vệ tốt nhất cho bản th&acirc;n cũng như người th&acirc;n nh&eacute;.</p>\n<div id=\"attachment_351321\" class=\"wp-caption aligncenter\"><img class=\"wp-image-351321 size-full\" title=\"Ảnh: Avana Retreat Mai Ch&acirc;u\" src=\"https://cdn3.ivivu.com/2021/06/nhung-luu-y-quan-trong-khi-du-lich-mua-dich-ivivu-6.jpg\" alt=\"Ảnh: Avana Retreat Mai Ch&acirc;u\" width=\"1080\" height=\"1350\">\n<p class=\"wp-caption-text\">Ảnh: Avana Retreat Mai Ch&acirc;u</p>\n</div>\n<p>Một điều quan trọng nữa trong thời điểm b&ugrave;ng ph&aacute;t dịch Covid-19 l&agrave; h&atilde;y hỏi kh&aacute;ch sạn, khu nghỉ dưỡng về dịch vụ y tế. Nắm th&ocirc;ng tin về ph&ograve;ng kh&aacute;m, bệnh viện gần kh&aacute;ch sạn nhất sẽ gi&uacute;p bạn chủ động hơn khi gặp những&nbsp;sự cố sức khỏe ngo&agrave;i &yacute; muốn l&uacute;c&nbsp;du lịch m&ugrave;a dịch.</p>\n<p><strong>4. Sử dụng phương tiện giao th&ocirc;ng được cấp ph&eacute;p</strong></p>\n<p>Tại Việt Nam, c&aacute;c h&atilde;ng bay nội địa như&nbsp;<em><strong>Vietnam Airlines, Bamboo Airways, Vietjet Air</strong></em>,&hellip; đều chủ động chủ động vệ sinh to&agrave;n bộ khoang m&aacute;y bay, ghế ngồi, b&agrave;n ăn, dụng cụ xe đẩy, khu vệ sinh tr&ecirc;n m&aacute;y bay,&hellip; bằng dung dịch khử tr&ugrave;ng chuy&ecirc;n dụng được c&aacute;c nh&agrave; sản xuất m&aacute;y bay Airbus, Boeing chấp thuận. Quy tr&igrave;nh được c&aacute;c h&atilde;ng thực hiện ngay từ thời điểm dịch c&oacute; xu hướng gia tăng, gi&uacute;p hạn chế tối đa nguy cơ l&acirc;y nhiễm dịch bệnh, bảo vệ sức khỏe h&agrave;nh kh&aacute;ch, phi h&agrave;nh đo&agrave;n.</p>\n<div id=\"attachment_351322\" class=\"wp-caption aligncenter\"><img class=\"wp-image-351322 size-full\" title=\"Ảnh: Vietjet Air\" src=\"https://cdn3.ivivu.com/2021/06/nhung-luu-y-quan-trong-khi-du-lich-mua-dich-ivivu-1.jpg\" alt=\"Ảnh: Vietjet Air\" width=\"1732\" height=\"1154\">\n<p class=\"wp-caption-text\">Ảnh: Vietjet Air</p>\n</div>\n<p>Nhiều chuy&ecirc;n gia cho biết,&nbsp;c&oacute; rất &iacute;t nguy cơ l&acirc;y nhiễm tr&ecirc;n chuyến bay, ngược lại&nbsp;khả năng nhiễm virus cao l&agrave; ở quầy thủ tục an ninh s&acirc;n bay, cửa ra m&aacute;y bay hoặc ở&nbsp;ph&ograve;ng chờ. Bạn cần khai b&aacute;o y tếkhi đến quầy check-in, đeo khẩu trang xuy&ecirc;n suốt h&agrave;nh tr&igrave;nh v&agrave;&nbsp;kh&ocirc;ng qu&ecirc;n rửa tay s&aacute;t khuẩn khi tiếp x&uacute;c v&agrave;o những bề mặt c&oacute; tiềm ẩn chứa nhiều virus như ghế ngồi, b&agrave;n ăn, cửa ra v&agrave;o toilet&hellip;</p>\n<div id=\"attachment_351323\" class=\"wp-caption aligncenter\"><img class=\"wp-image-351323 size-full\" title=\"Ảnh: Vietnam Airlines\" src=\"https://cdn3.ivivu.com/2021/06/nhung-luu-y-quan-trong-khi-du-lich-mua-dich-ivivu-2.jpg\" alt=\"Ảnh: Vietnam Airlines\" width=\"1960\" height=\"1307\">\n<p class=\"wp-caption-text\">Ảnh: Vietnam Airlines</p>\n</div>\n<p>Cũng như m&aacute;y bay, c&aacute;c phương tiện vận tải đ&ocirc;ng người như xe bu&yacute;t, xe kh&aacute;ch đường d&agrave;i, t&agrave;u hỏa cũng c&oacute; những quy định ph&ograve;ng dịch như y&ecirc;u cầu h&agrave;nh kh&aacute;ch đeo khẩu trang, khai b&aacute;o y tế v&agrave; s&aacute;t khuẩn, khử tr&ugrave;ng. Tuyệt đối kh&ocirc;ng sử dụng dịch vụ của c&aacute;c nh&agrave; xe chưa được cấp ph&eacute;p hoạt động v&igrave; khả năng l&acirc;y nhiễm rất cao. Với những cu</p>', '2023-11-18', 'Trước tình hình diễn biến dịch Covid-19 vẫn còn đang phức tạp, để đảm bảo an toàn cho những chuyến du lịch sắp tới, bạn có thể tham khảo những lưu ý dưới đây.  ', 'http://res.cloudinary.com/dxqh3xpza/image/upload/v1700276397/fwuj4wb4fd3erwilejkb.jpg', 3, b'0', 'Những điều quan trọng cần lưu ý khi du lịch trong mùa dịch', 1),
(4, 'Rất nhiều điểm đến vừa quen vừa lạ ở Việt Nam lý tưởng để bạn nắm tay ai đó cùng tận hưởng và hít hà không khí lạnh mùa đông.', '2024-04-03', 'Rất nhiều điểm đến vừa quen vừa lạ ở Việt Nam lý tưởng để bạn nắm tay ai đó cùng tận hưởng và hít hà không khí lạnh mùa đông.', 'http://res.cloudinary.com/dxqh3xpza/image/upload/v1700276489/tnjngbhdgvwaghkcqiif.png', 52, b'1', '8 nơi lý tưởng để nắm tay ai đó tận hưởng không khí lạnh mùa đông', 1),
(5, '<p>Carrie Miller, người c&oacute; 21 năm kinh nghiệm du lịch độc h&agrave;nh sẽ cho bạn th&ecirc;m năng lượng v&agrave; sự tự tin để thực hiện những chuyện du lịch một m&igrave;nh tuyệt vời như c&ocirc; ấy.</p>\n<p><strong>1. Mọi thứ chẳng qu&aacute; kh&oacute; khăn như bạn tưởng</strong></p>\n<p><img class=\"aligncenter size-full wp-image-170686\" src=\"https://cdn3.ivivu.com/2016/10/du-lich-mot-minh-ivivu-1.jpg\" alt=\"du-lich-mot-minh-ivivu-1\" width=\"570\" height=\"380\"></p>\n<p>Lời cảnh b&aacute;o của mọi người về những kh&oacute; khăn, bất trắc, lu&ocirc;n l&agrave;m bạn hoang mang khi quyết định đơn lẻ l&ecirc;n đường. Nếu d&aacute;m bước qua r&agrave;o cản đ&oacute;, bạn sẽ nhận thấy một điều rằng sự ph&ecirc; ph&aacute;n, &aacute;p đặt hay can thiệp của mọi người đ&ocirc;i khi kh&ocirc;ng quan trọng bằng kh&aacute;t khao kh&aacute;m ph&aacute; của bản th&acirc;n.</p>\n<p>C&ocirc; đ&atilde; chia sẻ rằng &ldquo;&hellip; Trong chuyến đi đầu ti&ecirc;n mở ra 21 năm du lịch độc h&agrave;nh, c&oacute; một người đ&atilde; đến v&agrave; n&oacute;i với t&ocirc;i rằng: &ldquo;Xin lỗi v&igrave; đ&atilde; l&agrave;m phiền, nhưng t&ocirc;i nghĩ rằng bạn thật l&agrave; một người dũng cảm&rdquo;.</p>\n<p><strong>2. Tận hưởng mọi thứ một m&igrave;nh cũng l&agrave; một điều tuyệt vời</strong></p>\n<p>Những lần đầu khi suy nghĩ về việc đi du lịch một m&igrave;nh, bạn sẽ cảm thấy v&ocirc; c&ugrave;ng lo lắng, tưởng như nếu kh&ocirc;ng c&oacute; một người bạn đồng h&agrave;nh, bạn sẽ mắc kẹt trong sự sợ h&atilde;i v&agrave; bất trắc tr&ecirc;n đường. Nhưng dần dần bạn sẽ hiểu d&ugrave; c&oacute; một m&igrave;nh, bạn vẫn l&agrave;m tốt mọi thứ, bạn hiểu ch&iacute;nh nhịp độ tiết tấu của m&igrave;nh, sở th&iacute;ch c&aacute; nh&acirc;n của m&igrave;nh v&agrave; chẳng cần chiều theo ai cả.</p>\n<p><img class=\"aligncenter size-full wp-image-170687\" src=\"https://cdn3.ivivu.com/2016/10/du-lich-mot-minh-ivivu-2.jpg\" alt=\"du-lich-mot-minh-ivivu-2\" width=\"994\" height=\"649\"><br>Nhưng h&atilde;y nhớ giữ li&ecirc;n lạc thường xuy&ecirc;n với gia đ&igrave;nh v&agrave; bạn b&egrave; chỉ l&agrave;m bạn th&ecirc;m muốn về khi chặng đường c&ograve;n chưa bắt đầu. H&atilde;y cho họ biết lịch tr&igrave;nh của bạn v&agrave; gi&uacute;p họ y&ecirc;n t&acirc;m về bạn bằng những lần li&ecirc;n lạc ngắn gọn. Kh&ocirc;ng nhất thiết phải cập nhật li&ecirc;n tục bằng h&igrave;nh ảnh đ&acirc;u.</p>\n<div><iframe src=\"https://www.ivivu.com/hothotel/?placeslug=muon-mau&amp;number=1&amp;width=481\" width=\"100%\" height=\"312\" scrolling=\"no\"></iframe></div>\n<p><strong>3. L&agrave;m quen với những người mới kh&ocirc;ng kh&oacute;</strong></p>\n<p>Du lịch độc h&agrave;nh buộc bạn bước ra khỏi v&ugrave;ng an to&agrave;n của bản th&acirc;n, chắc chắn thế. V&agrave; điều đ&oacute; l&agrave; cơ hội gi&uacute;p ta giao tiếp nhiều hơn với thế giới b&ecirc;n ngo&agrave;i, thu về những kinh nghiệm sống, d&ugrave; với những người ta chẳng bao giờ gặp lại. Ch&iacute;nh ra đi một m&igrave;nh lại l&agrave; phương ph&aacute;p tiếp cận cuộc sống tốt hơn hẳn đi du lịch nh&oacute;m.</p>\n<p><img class=\"aligncenter size-full wp-image-170688\" src=\"https://cdn3.ivivu.com/2016/10/du-lich-mot-minh-ivivu-3.jpg\" alt=\"du-lich-mot-minh-ivivu-3\" width=\"626\" height=\"477\"><br>Một v&agrave;i kinh nghiệm để l&agrave;m quen những người bạn mới l&agrave;: Đặt điện thoại xuống, cầm một cuốn s&aacute;ch l&ecirc;n; Đăng k&yacute; một tour du lịch cuối tuần v&agrave; gặp một nh&oacute;m người m&agrave; bạn chưa bao giờ gặp. Biến lạ th&agrave;nh quen thật dễ khi bạn kh&ocirc;ng biết ai trong nh&oacute;m nhỏ đ&oacute;; H&atilde;y đến những nh&agrave; h&agrave;ng bởi trong một buổi ăn uống, người ta dễ d&agrave;ng quen nhau hơn, nhưng đừng động v&agrave;o rượu.</p>\n<p><strong>4. T&igrave;m ra h&igrave;nh mẫu bản th&acirc;n m&agrave; bạn muốn trở th&agrave;nh</strong></p>\n<p>Đ&ocirc;i khi, du lịch một m&igrave;nh sẽ gi&uacute;p &iacute;ch cho ch&iacute;nh sự nghiệp của bạn. Tr&ecirc;n những h&agrave;nh tr&igrave;nh trải nghiệm, bạn c&oacute; thể kiệt sức đ&acirc;u đ&oacute; hoặc c&oacute; những điều bất ngờ thay đổi ho&agrave;n to&agrave;n định hướng của bạn. Bạn sẽ v&ocirc; t&igrave;nh gặp những người bạn c&oacute; thể muốn cộng t&aacute;c, đồng h&agrave;nh trong tương lai. Thế giới vốn nhỏ b&eacute;. Bạn lại c&oacute; thời gian để lắng nghe t&acirc;m tư về điều m&igrave;nh thực sự muốn l&agrave;m trong đời, tự ph&ecirc; b&igrave;nh bản th&acirc;n. Học c&aacute;ch lắng nghe v&agrave; tin ch&iacute;nh m&igrave;nh, tr&aacute;nh xa những phiền nhiễu &aacute;p lực, bạn sẽ kh&aacute;m ph&aacute; ra điều m&igrave;nh muốn.</p>\n<p><img class=\"aligncenter size-full wp-image-170689\" src=\"https://cdn3.ivivu.com/2016/10/du-lich-mot-minh-ivivu-4.jpg\" alt=\"du-lich-mot-minh-ivivu-4\" width=\"720\" height=\"547\"><br><strong>5. Thế giới kh&ocirc;ng ho&agrave;n to&agrave;n m&agrave;u x&aacute;m</strong></p>\n<p>Một du kh&aacute;ch chia sẻ thế n&agrave;y &ldquo;T&ocirc;i từng bị cướp tại CH S&eacute;c, tr&ecirc;n h&agrave;nh tr&igrave;nh từ Prague tới Brno. T&ocirc;i bị cướp hết tiền v&agrave; điện thoại. Rất may t&ocirc;i vẫn c&ograve;n hộ chiếu v&agrave; đồ đạc gửi tại kh&aacute;ch sạn Prague. T&ocirc;i ngồi tr&ecirc;n phố Brno, nhấm nh&aacute;p v&agrave;i ngụm nước, v&agrave; b&oacute;p tr&aacute;n nghĩ xem phải l&agrave;m g&igrave; b&acirc;y giờ, th&igrave; c&oacute; ba người d&acirc;n địa phương tới. Họ mời t&ocirc;i tham gia v&agrave;o nh&oacute;m bạn b&egrave; họ v&agrave; cho t&ocirc;i đồ uống. T&ocirc;i n&oacute;i về t&igrave;nh cảnh kh&oacute; khăn của m&igrave;nh v&agrave; lập tức họ gi&uacute;p t&ocirc;i li&ecirc;n lạc với cảnh s&aacute;t, thu xếp chỗ ngủ qua đ&ecirc;m cho t&ocirc;i ở nh&agrave; một người họ h&agrave;ng. Người ấy mến kh&aacute;ch đến nỗi mời t&ocirc;i một ly rượu truyền thống tuyệt ngon được cất dưới tầng hầm trong nh&agrave; từ thời Thế chiến Thứ 2. H&ocirc;m sau, người b&agrave; con nọ đi c&ugrave;ng t&ocirc;i tới trạm xe bu&yacute;t v&agrave; mua v&eacute; cho t&ocirc;i trở về Prague.</p>\n<p><img class=\"aligncenter size-full wp-image-170690\" src=\"https://cdn3.ivivu.com/2016/10/du-lich-mot-minh-ivivu-5.jpg\" alt=\"du-lich-mot-minh-ivivu-5\" width=\"1024\" height=\"682\"><br>Thế đấy, d&ugrave; c&ograve;n nhiều bất cập nhưng những người xung quanh đều muốn gi&uacute;p đỡ m&igrave;nh khi kh&oacute; khăn nếu họ c&oacute; thể. H&atilde;y cứ l&ecirc;n đường, điều tốt đẹp vẫn lu&ocirc;n xung quanh bạn, đừng lo lắng. Việc h&atilde;y cẩn trọng một ch&uacute;t, đừng say sưa la c&agrave; qu&aacute;, đừng bao giờ để điện thoại cạn pin sẽ b&aacute;o hại bạn trong những trường hợp khẩn cấp. V&agrave; h&atilde;y mang đồ đạc gọn nhẹ th&ocirc;i. C&oacute; những l&uacute;c bạn cần phải đi nhanh, thậm ch&iacute; chạy nhanh.</p>\n<p><strong>6. Độc lập v&agrave; chẳng phụ thuộc v&agrave;o ai cả</strong></p>\n<p><img class=\"aligncenter size-full wp-image-170691\" src=\"https://cdn3.ivivu.com/2016/10/du-lich-mot-minh-ivivu-6.jpg\" alt=\"du-lich-mot-minh-ivivu-6\" width=\"620\" height=\"387\"></p>\n<p>Đi du lịch một m&igrave;nh đến một nơi xa lạ, chẳng hề quen biết ai cả, bạn sẽ học được c&aacute;ch tự chủ hơn, v&agrave; nhận ra rằng kh&ocirc;ng g&igrave; c&oacute; thể cản trở m&igrave;nh. Chắc chắn bất ngờ với khả năng th&iacute;ch ứng của bản th&acirc;n, từ th&iacute;ch ứng với kh&iacute; hậu, đồ ăn cho đến phong tục&hellip; ở đấy. Đặc biệt di du lịch một m&igrave;nh, bạn sẽ ho&agrave;n to&agrave;n chủ động, chẳng cần phải chiều theo &yacute; th&iacute;ch của người đi c&ugrave;ng, nghỉ ở bất cứ đ&acirc;u bạn muốn. Cũng th&uacute; vị đấy chứ phải kh&ocirc;ng?</p>', '2023-11-18', 'Đi du lịch một mình, bạn sẽ được tự do hoàn toàn để làm bất cứ cái gì bạn thích mà chẳng cần phụ thuộc vào ai. Chưa kể bạn khám phá ra những khả năng mới của mình.', 'http://res.cloudinary.com/dxqh3xpza/image/upload/v1700276583/qxwrcfbo7e7by7bfl3ju.jpg', 5, b'0', '6 lợi ích tuyệt vời bạn sẽ có được từ những chuyến du lịch một mình', 1),
(6, '<h4><a href=\"https://www.ivivu.com/khach-san-da-lat?utm_source=blog_ivivu&amp;utm_medium=post_link&amp;utm_campaign=internal\" target=\"_blank\" rel=\"noopener\"><strong>KH&Aacute;CH SẠN Đ&Agrave; LẠT</strong></a></h4>\n<p><a href=\"https://www.ivivu.com/khach-san-da-lat/khach-san-pho-nui-da-lat?utm_source=blog_ivivu&amp;utm_medium=post_link&amp;utm_campaign=internal\" target=\"_blank\" rel=\"noopener\"><strong>Kh&aacute;ch sạn Phố N&uacute;i Đ&agrave; Lạt</strong></a></p>\n<p>Nằm ở vị tr&iacute; đắc địa ngay trung t&acirc;m th&agrave;nh phố Đ&agrave; Lạt, tr&ecirc;n ngọn đồi đối diện Hồ Xu&acirc;n Hương xinh đẹp, gần c&aacute;c khu ăn uống, chợ&hellip; kh&aacute;ch sạn Phố N&uacute;i l&agrave; một trong những điểm dừng ch&acirc;n được nhiều kh&aacute;ch du lịch trong v&agrave; ngo&agrave;i nước lựa chọn khi đến với th&agrave;nh phố Đ&agrave; Lạt.</p>\n<div id=\"attachment_134818\" class=\"wp-caption aligncenter\"><a href=\"https://cdn3.ivivu.com/2016/04/khach-san-pho-nui-ivivu-3.jpg\"><img class=\"wp-image-134818 size-full\" title=\"C&aacute;c ph&ograve;ng ngủ đều&nbsp;c&oacute; cửa sổ ngập tr&agrave;n &aacute;nh s&aacute;ng, nh&igrave;n xuống c&ocirc;ng vi&ecirc;n th&agrave;nh phố, gi&uacute;p du kh&aacute;ch tận hưởng cảm gi&aacute;c an l&agrave;nh giữa l&ograve;ng phố n&uacute;i.\" src=\"https://cdn3.ivivu.com/2016/04/khach-san-pho-nui-ivivu-3.jpg\" alt=\"C&aacute;c ph&ograve;ng ngủ đều&nbsp;c&oacute; cửa sổ ngập tr&agrave;n &aacute;nh s&aacute;ng, nh&igrave;n xuống c&ocirc;ng vi&ecirc;n th&agrave;nh phố, gi&uacute;p du kh&aacute;ch tận hưởng cảm gi&aacute;c an l&agrave;nh giữa l&ograve;ng phố n&uacute;i.\" width=\"650\" height=\"435\"></a>\n<p class=\"wp-caption-text\">C&aacute;c ph&ograve;ng ngủ đều&nbsp;c&oacute; cửa sổ ngập tr&agrave;n &aacute;nh s&aacute;ng, nh&igrave;n xuống c&ocirc;ng vi&ecirc;n th&agrave;nh phố, gi&uacute;p du kh&aacute;ch tận hưởng cảm gi&aacute;c an l&agrave;nh giữa l&ograve;ng phố n&uacute;i.</p>\n</div>\n<div><iframe src=\"https://www.ivivu.com/hothotel/?placeslug=moi-ngay-mot-khach-san&amp;number=1&amp;width=481\" width=\"100%\" height=\"312\" scrolling=\"no\"></iframe></div>\n<div id=\"attachment_134817\" class=\"wp-caption aligncenter\"><a href=\"https://cdn3.ivivu.com/2016/04/khach-san-pho-nui-ivivu-2.jpg\"><img class=\"wp-image-134817 size-full\" title=\"Giường ngủ rộng r&atilde;i, nội thất được trang bị chu đ&aacute;o, đầy đủ đến từng chi tiết.&nbsp;\" src=\"https://cdn3.ivivu.com/2016/04/khach-san-pho-nui-ivivu-2.jpg\" alt=\"Giường ngủ rộng r&atilde;i, nội thất được trang bị chu đ&aacute;o, đầy đủ đến từng chi tiết.&nbsp;\" width=\"650\" height=\"435\"></a>\n<p class=\"wp-caption-text\">Giường ngủ rộng r&atilde;i, nội thất được trang bị chu đ&aacute;o, đầy đủ đến từng chi tiết.</p>\n</div>\n<p>Kh&aacute;ch sạn Phố N&uacute;i c&oacute; tất cả 33 ph&ograve;ng bao gồm 6 loại ph&ograve;ng l&agrave; Double Standard, Double Superior, Triple Superior, Double Deluxe, Family Deluxe v&agrave; Romatic Queen được trang bị đầy đủ tiện nghi hiện đại đạt ti&ecirc;u chuẩn quốc tế 3 sao, nội thất hiện đại, m&agrave;u sắc ấm &aacute;p, giường ngủ rộng r&atilde;i, kh&ocirc;ng kh&iacute; thoải m&aacute;i v&agrave; ấm c&uacute;ng. C&ugrave;ng với đ&oacute; l&agrave; c&aacute;c dịch vụ v&agrave; cơ sở vật chất ho&agrave;n to&agrave;n mới, gi&uacute;p du kh&aacute;ch tận hưởng kỳ nghỉ với t&acirc;m thế thoải m&aacute;i, y&ecirc;n b&igrave;nh sau những bộn bề, lo toan của cuộc sống.</p>\n<div id=\"attachment_134816\" class=\"wp-caption aligncenter\"><a href=\"https://cdn3.ivivu.com/2016/04/khach-san-pho-nui-ivivu-1.jpg\"><img class=\"wp-image-134816 size-full\" title=\"Chỉ cần mở cửa sổ, khung cảnh Đ&agrave; Lạt đ&atilde; ở trong tầm mắt của bạn\" src=\"https://cdn3.ivivu.com/2016/04/khach-san-pho-nui-ivivu-1.jpg\" alt=\"Chỉ cần mở cửa sổ, khung cảnh Đ&agrave; Lạt đ&atilde; ở trong tầm mắt của bạn\" width=\"840\" height=\"460\"></a>\n<p class=\"wp-caption-text\">Chỉ cần mở cửa sổ, khung cảnh Đ&agrave; Lạt đ&atilde; ở trong tầm mắt của bạn</p>\n</div>\n<p><a href=\"https://www.ivivu.com/khach-san-da-lat/khach-san-reveto-villa-da-lat?utm_source=blog_ivivu&amp;utm_medium=post_link&amp;utm_campaign=internal\" target=\"_blank\" rel=\"noopener\"><strong>Kh&aacute;ch sạn Reveto Villa Đ&agrave; Lạt</strong></a></p>\n<p>Nằm ở 35 Trần Đại Nghĩa, Reveto c&oacute; vẻ hơi xa trung t&acirc;m so với c&aacute;c kh&aacute;ch sạn kh&aacute;c. Nhưng thật ra, n&oacute; cũng chẳng&hellip; xa lắm, bạn chỉ cần đi xe m&aacute;y hoặc ngồi taxi 10 ph&uacute;t l&agrave; tới chợ Đ&agrave; Lạt. B&ugrave; lại, Reveto cực kỳ y&ecirc;n tĩnh, view tr&ocirc;ng ra một vườn rau xinh xắn, kh&ocirc;ng ồn &agrave;o, x&ocirc; bồ như kh&aacute;ch sạn ở khu vực trung t&acirc;m. Th&ecirc;m nữa, Reveto rất gần thung lũng T&igrave;nh Y&ecirc;u, đại học Đ&agrave; Lạt, đồi Mộng Mơ, XQ sử qu&aacute;n hay qua Langbiang cũng chỉ tốn 15 ph&uacute;t đi xe.</p>\n<div id=\"attachment_134822\" class=\"wp-caption aligncenter\"><a href=\"https://cdn3.ivivu.com/2016/04/Reveto-Villa-da-lat-ivivu-6.jpg\"><img class=\"wp-image-134822 size-full\" title=\"Kh&aacute;ch sạn Reveto Villa sẽ mang đến cho bạn v&agrave; người th&acirc;n một trải nghiệm mới mẻ khi du lịch Đ&agrave; Lạt.\" src=\"https://cdn3.ivivu.com/2016/04/Reveto-Villa-da-lat-ivivu-6.jpg\" alt=\"Kh&aacute;ch sạn Reveto Villa sẽ mang đến cho bạn v&agrave; người th&acirc;n một trải nghiệm mới mẻ khi du lịch Đ&agrave; Lạt.\" width=\"800\" height=\"600\"></a>\n<p class=\"wp-caption-text\">Kh&aacute;ch sạn Reveto Villa sẽ mang đến cho bạn v&agrave; người th&acirc;n một trải nghiệm mới mẻ khi du lịch Đ&agrave; Lạt.</p>\n</div>\n<div id=\"attachment_134821\" class=\"wp-caption aligncenter\"><a href=\"https://cdn3.ivivu.com/2016/04/Reveto-Villa-da-lat-ivivu-4.jpg\"><img class=\"wp-image-134821 size-full\" title=\"Từ ph&ograve;ng nghỉ của kh&aacute;ch sạn bạn sẽ ph&oacute;ng tầm mắt nh&igrave;n ra vườn rau nhỏ nhỏ xinh xinh.\" src=\"https://cdn3.ivivu.com/2016/04/Reveto-Villa-da-lat-ivivu-4.jpg\" alt=\"Từ ph&ograve;ng nghỉ của kh&aacute;ch sạn bạn sẽ ph&oacute;ng tầm mắt nh&igrave;n ra vườn rau nhỏ nhỏ xinh xinh.\" width=\"840\" height=\"460\"></a>\n<p class=\"wp-caption-text\">Từ ph&ograve;ng nghỉ của kh&aacute;ch sạn bạn sẽ ph&oacute;ng tầm mắt nh&igrave;n ra vườn rau nhỏ nhỏ xinh xinh.</p>\n</div>\n<p>Kiến tr&uacute;c của villa n&agrave;y nh&igrave;n từ ngo&agrave;i hết sức dễ thương: một ng&ocirc;i nh&agrave; 3 tầng sơn trắng với m&aacute;i ng&oacute;i đen l&ocirc; nh&ocirc;, h&agrave;ng r&agrave;o v&agrave; gạch l&aacute;t cũng đồng m&agrave;u. Thỉnh thoảng, xen giữa kh&ocirc;ng gian trắng tinh đ&oacute; l&agrave; những mảng xanh đ&oacute;n &aacute;nh mặt trời. Ch&iacute;nh kiến tr&uacute;c v&agrave; sự nhỏ xinh của Reveto khiến bạn c&oacute; cảm gi&aacute;c như đang về nh&agrave; chứ kh&ocirc;ng phải đang đến một nơi tạm tr&uacute; xa lạ n&agrave;o đ&oacute;.</p>\n<div id=\"attachment_134823\" class=\"wp-caption aligncenter\"><a href=\"https://cdn3.ivivu.com/2016/04/Reveto-Villa-da-lat-ivivu-7.jpg\"><img class=\"wp-image-134823 size-full\" title=\"Ph&ograve;ng nghỉ ấm &aacute;p với ban c&ocirc;ng rộng.\" src=\"https://cdn3.ivivu.com/2016/04/Reveto-Villa-da-lat-ivivu-7.jpg\" alt=\"Ph&ograve;ng nghỉ ấm &aacute;p với ban c&ocirc;ng rộng.\" width=\"800\" height=\"450\"></a>\n<p class=\"wp-caption-text\">Ph&ograve;ng nghỉ ấm &aacute;p với ban c&ocirc;ng rộng.</p>\n</div>\n<div id=\"attachment_134820\" class=\"wp-caption aligncenter\"><a href=\"https://cdn3.ivivu.com/2016/04/Reveto-Villa-da-lat-ivivu-3.jpg\"><img class=\"wp-image-134820 size-full\" title=\"Bộ b&agrave;n ghế xinh xắn đặt trong s&acirc;n kh&aacute;ch sạn, nơi để du kh&aacute;ch nghỉ ch&acirc;n ngắm cảnh.\" src=\"https://cdn3.ivivu.com/2016/04/Reveto-Villa-da-lat-ivivu-3.jpg\" alt=\"Bộ b&agrave;n ghế xinh xắn đặt trong s&acirc;n kh&aacute;ch sạn, nơi để du kh&aacute;ch nghỉ ch&acirc;n ngắm cảnh.\" width=\"840\" height=\"460\"></a>\n<p class=\"wp-caption-text\">Bộ b&agrave;n ghế xinh xắn đặt trong s&acirc;n kh&aacute;ch sạn, nơi để du kh&aacute;ch nghỉ ch&acirc;n ngắm cảnh.</p>\n</div>\n<p><a href=\"https://www.ivivu.com/khach-san-da-lat/khach-san-tam-dung-1-da-lat?utm_source=blog_ivivu&amp;utm_medium=post_link&amp;utm_campaign=internal\" target=\"_blank\" rel=\"noopener\"><strong>Kh&aacute;ch sạn T&acirc;m Dung 1 Đ&agrave; Lạt</strong></a></p>\n<p>Kh&aacute;ch sạn T&acirc;m Dung 1 tọa lạc ngay trung t&acirc;m th&agrave;nh phố, c&aacute;ch chợ Đ&agrave; Lạt 5-7 ph&uacute;t đi bộ bao gồm hai khu: Khu cũ x&acirc;y dựng năm 2006 v&agrave; mới được n&acirc;ng cấp, sửa chữa v&agrave;o th&aacute;ng 4/2014 (sơn sửa, thay ga, thay r&egrave;m trong ph&ograve;ng).</p>\n<div id=\"attachment_134824\" class=\"wp-caption aligncenter\"><a href=\"https://cdn3.ivivu.com/2016/04/khach-san-tam-dung-1-ivivu-1.jpg\"><img class=\"wp-image-134824 size-full\" title=\"Kh&aacute;ch sạn rất gần c&aacute;c địa điểm du lịch ở Đ&agrave; Lạt.\" src=\"https://cdn3.ivivu.com/2016/04/khach-san-tam-dung-1-ivivu-1.jpg\" alt=\"khach-san-tam-dung-1-ivivu-1\" width=\"650\" height=\"433\"></a>\n<p class=\"wp-caption-text\">Kh&aacute;ch sạn rất gần c&aacute;c địa điểm du lịch ở Đ&agrave; Lạt.</p>\n</div>\n<p><a href=\"https://cdn3.ivivu.com/2016/04/khach-san-tam-dung-1-ivivu-2.jpg\"><img class=\"aligncenter size-full wp-image-134825\" src=\"https://cdn3.ivivu.com/2016/04/khach-san-tam-dung-1-ivivu-2.jpg\" alt=\"khach-san-tam-dung-1-ivivu-2\" width=\"630\" height=\"419\"></a></p>\n<p>Khu mới đưa v&agrave;o hoạt động th&aacute;ng 4/2014, ph&ograve;ng thiết kế đẹp, trang thiết bị hiện đại, ấm c&uacute;ng đảm bảo du kh&aacute;ch sẽ cảm thấy h&agrave;i l&ograve;ng khi nghỉ ngơi tại đ&acirc;y. Đa số số lượng ph&ograve;ng của kh&aacute;ch sạn đều nằm ở khu mới gồm, được trang bị đầy đủ nội thất v&agrave; c&aacute;c tiện nghi theo ti&ecirc;u chuẩn 2 sao nhằm mang đến cho du kh&aacute;ch cảm gi&aacute;c thoải m&aacute;i v&agrave; ấm c&uacute;ng như đang ở trong ch&iacute;nh ng&ocirc;i nh&agrave; của m&igrave;nh.</p>\n<h4><a href=\"https://www.ivivu.com/khach-san-vung-tau?utm_source=blog_ivivu&amp;utm_medium=post_link&amp;utm_campaign=internal\" target=\"_blank\" rel=\"noopener\"><strong>KH&Aacute;CH SẠN VŨNG T&Agrave;U</strong></a></h4>\n<p><a href=\"https://www.ivivu.com/khach-san-vung-tau/biet-thu-viva-vung-tau?utm_source=blog_ivivu&amp;utm_medium=post_link&amp;utm_campaign=internal\" target=\"_blank\" rel=\"nofollow noopener\"><strong>Biệt thự Viva Vũng T&agrave;u</strong></a></p>\n<p>Biệt thự ViVa tọa lạc tại khu biệt thự cao cấp Phan Chu Trinh v&agrave; Lạc Long Qu&acirc;n thuộc th&agrave;nh phố Vũng T&agrave;u được x&acirc;y dựng theo ti&ecirc;u chuẩn cao cấp đầy đủ tiện nghi. Từ biệt thự, chỉ mất 10 ph&uacute;t đi bộ bạn sẽ đến với B&atilde;i Sau tr&ecirc;n đường Th&ugrave;y V&acirc;n để tự do h&ograve;a m&igrave;nh v&agrave;o gi&oacute; v&agrave; s&oacute;ng biển vỗ về. Hơn nữa, kh&ocirc;ng gian biệt thự rộng r&atilde;i, tiện nghi sẽ lu&ocirc;n mang đến cho bạn cảm gi&aacute;c tự do, thoải m&aacute;i như đ&oacute; l&agrave; nh&agrave; của m&igrave;nh.</p>\n<div id=\"attachment_134839\" class=\"wp-caption aligncenter\"><a href=\"https://cdn3.ivivu.com/2016/04/biet-thu-viva-ivivu-2.jpg\"><img class=\"wp-image-134839 size-full\" title=\"Hồ bơi sạch sẽ ph&iacute;a b&ecirc;n trong biệt thự.\" src=\"https://cdn3.ivivu.com/2016/04/biet-thu-viva-ivivu-2.jpg\" alt=\"Hồ bơi sạch sẽ ph&iacute;a b&ecirc;n trong biệt thự.\" width=\"700\" height=\"466\"></a>\n<p class=\"wp-caption-text\">Hồ bơi sạch sẽ ph&iacute;a b&ecirc;n trong biệt thự.</p>\n</div>\n<div id=\"attachment_134838\" class=\"wp-caption aligncenter\"><a href=\"https://cdn3.ivivu.com/2016/04/biet-thu-viva-ivivu-1.jpg\"><img class=\"wp-image-134838 size-full\" title=\"Ph&ograve;ng ngủ c&oacute; kh&ocirc;ng gian tho&aacute;ng đ&atilde;ng.\" src=\"https://cdn3.ivivu.com/2016/04/biet-thu-viva-ivivu-1.jpg\" alt=\"Ph&ograve;ng ngủ c&oacute; kh&ocirc;ng gian tho&aacute;ng đ&atilde;ng.\" width=\"700\" height=\"395\"></a>\n<p class=\"wp-caption-text\">Ph&ograve;ng ngủ c&oacute; kh&ocirc;ng gian tho&aacute;ng đ&atilde;ng.</p>\n</div>\n<p>Biệt thự cao cấp ViVa, được thiết kế sang trọng với khu&ocirc;n vi&ecirc;n rộng r&atilde;i cho 10 người lớn v&agrave; 4 trẻ em, gi&uacute;p du kh&aacute;ch tận hưởng một h&igrave;nh thức nghỉ dưỡng mới mẻ. Bao quanh biệt thự ViVa l&agrave; một bầu kh&ocirc;ng kh&iacute; tho&aacute;ng đ&atilde;ng, gần gũi với thi&ecirc;n nhi&ecirc;n với nắng ấm v&agrave; gi&oacute; biển m&aacute;t lạnh. Từ ch&iacute;nh căn ph&ograve;ng của m&igrave;nh, bạn c&oacute; thể ngắm nh&igrave;n vẻ đẹp b&igrave;nh minh tr&ecirc;n đại dương bao la hay cảnh n&ecirc;n thơ khi chiều bu&ocirc;ng xuống. Khi đến với biệt thự ViVa Vũng T&agrave;u những lo &acirc;u, phiền muộn trong bạn sẽ dần tan biến hết, chỉ c&ograve;n lại gi&acirc;y ph&uacute;t thanh b&igrave;nh, thoải m&aacute;i trong t&acirc;m hồn.</p>\n<div id=\"attachment_134840\" class=\"wp-caption aligncenter\"><a href=\"https://cdn3.ivivu.com/2016/04/biet-thu-viva-ivivu-3.jpg\"><img class=\"wp-image-134840 size-full\" title=\"Biệt thự c&ograve;n sở hữu rất nhiều tiện &iacute;ch đi k&egrave;m như ph&ograve;ng kh&aacute;ch, hồ bơi, nh&agrave; bếp, m&aacute;y giặt c&ugrave;ng những tiện &iacute;ch cơ bản kh&aacute;c. Ri&ecirc;ng nh&agrave; bếp được trang bị đầy đủ c&aacute;c vật dụng nấu nướng, gia vị cần thiết, c&oacute; sẵn bếp than để gia đ&igrave;nh bạn c&oacute; thể tổ chức BBQ ngo&agrave;i trời bất kỳ l&uacute;c n&agrave;o.\" src=\"https://cdn3.ivivu.com/2016/04/biet-thu-viva-ivivu-3.jpg\" alt=\"Biệt thự c&ograve;n sở hữu rất nhiều tiện &iacute;ch đi k&egrave;m như ph&ograve;ng kh&aacute;ch, hồ bơi, nh&agrave; bếp, m&aacute;y giặt c&ugrave;ng những tiện &iacute;ch cơ bản kh&aacute;c. Ri&ecirc;ng nh&agrave; bếp được trang bị đầy đủ c&aacute;c vật dụng nấu nướng, gia vị cần thiết, c&oacute; sẵn bếp than để gia đ&igrave;nh bạn c&oacute; thể tổ chức BBQ ngo&agrave;i trời bất kỳ l&uacute;c n&agrave;o.\" width=\"700\" height=\"466\"></a>\n<p class=\"wp-caption-text\">Biệt thự c&ograve;n sở hữu rất nhiều tiện &iacute;ch đi k&egrave;m như ph&ograve;ng kh&aacute;ch, hồ bơi, nh&agrave; bếp, m&aacute;y giặt c&ugrave;ng những tiện &iacute;ch cơ bản kh&aacute;c. Ri&ecirc;ng nh&agrave; bếp được trang bị đầy đủ c&aacute;c vật dụng nấu nướng, gia vị cần thiết, c&oacute; sẵn bếp than để gia đ&igrave;nh bạn c&oacute; thể tổ chức BBQ ngo&agrave;i trời bất kỳ l&uacute;c n&agrave;o.</p>\n</div>', '2023-11-19', 'Nếu bạn đang có kế hoạch du lịch Đà Lạt, Vũng Tàu, hay Sapa dịp giổ Tổ tới đây, thì đừng chần chờ nữa, hãy nhấc điện thoại lên và gọi 1900 1870 để book phòng và nhận được giảm giá 20%, bởi theo thông tin của chúng tôi, lượng phòng còn lại đang khan hiếm dần vào những ngày cận lễ.', 'http://res.cloudinary.com/dxqh3xpza/image/upload/v1700366980/eyzryuaf4izxvul1ud0h.jpg', 3, b'0', 'Nhanh tay đặt phòng khách sạn giổ Tổ ngay hôm nay để được giảm giá 20%', 1);
INSERT INTO `blog` (`id`, `content`, `created_date`, `description`, `image_banner`, `num_view`, `primary_blog`, `title`, `user_id`) VALUES
(7, '<p><strong>Những thứ cần chuẩn bị trước chuyến đi</strong></p>\n<p>- Passport (tất nhi&ecirc;n rồi, v&igrave; m&igrave;nh ra nước ngo&agrave;i m&agrave;).</p>\n<p>- Xin Visa Trung Quốc, c&oacute; rất nhiều c&ocirc;ng ty du lịch nhận l&agrave;m dịch vụ visa với gi&aacute; khoảng 70 đ&ocirc; la.</p>\n<p>- Tư trang h&agrave;nh l&yacute; c&agrave;ng gọn c&agrave;ng tốt (bởi v&igrave; qua đ&oacute; bạn sẽ vung tiền như nước để mua đồ).</p>\n<p>- N&ecirc;n c&oacute; &aacute;o lạnh, v&igrave; thời thiết ở Trung Quốc kh&aacute; lạnh, đặc biệt l&agrave; những nơi m&igrave;nh sẽ đi.</p>\n<p>- Tiền Trung Quốc (c&oacute; nhiều nơi để đổi, nếu ở S&agrave;i G&ograve;n th&igrave; bạn l&ecirc;n đường Đồng Khởi quận 1, H&agrave; Nội th&igrave; phố H&agrave; Trung, nhưng để đổi tiền được gi&aacute; tốt hơn th&igrave; c&aacute;c bạn n&ecirc;n đổi ở ga Đồng Đăng, Lạng Sơn).</p>\n<p>- Khả năng giao tiếp Tiếng Trung, điều n&agrave;y l&agrave; bắt buộc, v&igrave; đa số người Trung Quốc giao tiếp tiếng Anh kh&ocirc;ng tốt.</p>\n<p>- Tốt nhất l&agrave; n&ecirc;n c&oacute; một người bạn ở Trung Quốc, hoặc đi c&ugrave;ng với nh&oacute;m bạn c&oacute; người biết n&oacute;i tiếng Trung.</p>\n<div id=\"attachment_134360\" class=\"wp-caption aligncenter\"><a href=\"https://cdn3.ivivu.com/2016/04/phuong-hoang-co-tran-ivivu1.jpg\"><img class=\"wp-image-134360 size-full\" title=\"Một g&oacute;c Phượng Ho&agrave;ng Cổ Trấn nh&igrave;n từ tr&ecirc;n cao. Ảnh: Nguyễn Trường Sơn\" src=\"https://cdn3.ivivu.com/2016/04/phuong-hoang-co-tran-ivivu1.jpg\" alt=\"Một g&oacute;c Phượng Ho&agrave;ng Cổ Trấn nh&igrave;n từ tr&ecirc;n cao. Ảnh: Nguyễn Trường Sơn\" width=\"960\" height=\"540\"></a>\n<p class=\"wp-caption-text\">Một g&oacute;c Phượng Ho&agrave;ng Cổ Trấn nh&igrave;n từ tr&ecirc;n cao. Ảnh: Nguyễn Trường Sơn</p>\n</div>\n<p><strong>Bắt đầu cuộc h&agrave;nh tr&igrave;nh</strong></p>\n<p>Đầu ti&ecirc;n, m&igrave;nh sẽ bay từ S&agrave;i G&ograve;n ra H&agrave; Nội. Sau đ&oacute; sẽ bắt chuyến t&agrave;u đi từ ga Gia L&acirc;m sang Trung Quốc. Chuyến t&agrave;u duy nhất từ ga Gia L&acirc;m sang Nam Ninh (th&agrave;nh phố thuộc tỉnh Quảng T&acirc;y, Trung Quốc) sẽ khởi h&agrave;nh v&agrave;o l&uacute;c 21h00 h&agrave;ng ng&agrave;y. Theo kinh nghiệm của m&igrave;nh th&igrave; c&aacute;c bạn n&ecirc;n đến sớm l&uacute;c tầm 19h00, ngồi đợi coi c&aacute;c bạn Việt Nam hoặc Trung Quốc đi chung, n&oacute;i với họ mua v&eacute; nh&oacute;m, chung ph&ograve;ng. Nếu c&aacute;c bạn mua ri&ecirc;ng, gi&aacute; v&eacute; cho 1 người l&agrave; 750.000 đồng, mua chung nh&oacute;m 6 người chỉ c&oacute; 530.000 đồng.</p>\n<p>C&aacute;c bạn c&oacute; thể nhờ người b&aacute;n v&eacute; n&oacute;i d&ugrave;m, nếu kh&ocirc;ng biết tiếng Trung, nhưng đa phần người Trung qua Việt Nam đều n&oacute;i tiếng Việt rất sỏi.</p>\n<p>Từ ga Gia L&acirc;m, mua v&eacute; đi Nam Ninh thủ tục cực kỳ đơn giản, bạn chỉ cần đưa passport v&agrave; tiền l&agrave; xong.</p>\n<div><iframe src=\"https://www.ivivu.com/hothotel/?placeslug=china&amp;number=1&amp;width=481\" width=\"100%\" height=\"312\" scrolling=\"no\"></iframe></div>\n<p>21h00, t&agrave;u xuất ph&aacute;t, t&agrave;u đi Nam Ninh l&agrave; t&agrave;u giường nằm, c&oacute; nước s&ocirc;i, n&ecirc;n c&aacute;c bạn c&oacute; thể mua mỳ ly mang l&ecirc;n pha ăn. Ngo&agrave;i ra, để đường đi bớt nh&agrave;m ch&aacute;n bạn c&oacute; thể mang theo s&aacute;ch, hoặc ipad c&oacute; tải sẵn s&aacute;ch để đọc.</p>\n<div id=\"attachment_134453\" class=\"wp-caption aligncenter\"><a href=\"https://cdn3.ivivu.com/2016/04/ga-nam-ninh-ivivu.jpg\"><img class=\"wp-image-134453 size-large\" title=\"Nh&agrave; ga Nam Ninh. Ảnh: Nguyễn Trường Sơn\" src=\"https://cdn3.ivivu.com/2016/04/ga-nam-ninh-ivivu-1024x577.jpg\" alt=\"Nh&agrave; ga Nam Ninh. Ảnh: Nguyễn Trường Sơn\" width=\"1024\" height=\"577\"></a>\n<p class=\"wp-caption-text\">Nh&agrave; ga Nam Ninh. Ảnh: Nguyễn Trường Sơn</p>\n</div>\n<p>0h00, t&agrave;u đến ga Đồng Đăng, tại đ&acirc;y c&aacute;c bạn sẽ phải l&agrave;m thủ tục để đ&oacute;ng dấu passport. V&agrave;o ga, c&aacute;c bạn xuống t&agrave;u n&ecirc;n mang to&agrave;n bộ h&agrave;nh l&yacute; xuống, hoặc giấu kỹ v&igrave; sẽ c&oacute; người đi kiểm tra bắt x&aacute;ch xuống hết. Xuống dưới, bạn cứ đi theo đo&agrave;n người v&agrave;o ga, trong đ&oacute; sẽ c&oacute; một ph&ograve;ng nho nhỏ để qu&eacute;t h&agrave;nh l&yacute;, bạn bỏ h&agrave;nh l&yacute; v&agrave;o qu&eacute;t. Sau khi qu&eacute;t xong, bạn đi tới nơi c&oacute; người gi&aacute;m s&aacute;t đang đứng, đưa passport cho họ, rồi đi ra ngo&agrave;i chờ gọi t&ecirc;n, mất khoảng 15 ph&uacute;t. Ở ga Đồng Đăng cũng c&oacute; nh&agrave; vệ sinh nhưng kh&ocirc;ng được sạch lắm.</p>\n<p>Một điều lưu &yacute; nữa, c&aacute;c bạn n&ecirc;n đổi tiền ở ga Đồng Đăng, v&igrave; thường sẽ rẻ hơn khi đổi ở S&agrave;i G&ograve;n, H&agrave; Nội. Sau khi kiểm tra, đ&oacute;ng dấu to&agrave;n bộ passport, họ sẽ gọi t&ecirc;n đến lấy, bạn n&ecirc;n ch&uacute; &yacute;, v&igrave; giọng họ đọc lơ lớ Tiếng Trung kh&oacute; nghe. N&ecirc;n mở passport ra kiểm tra lại xem dấu mộc, đ&atilde; đ&oacute;ng chưa.</p>\n<p>Xong thủ tục, bạn l&ecirc;n lại t&agrave;u, kiểm tra lại h&agrave;nh l&yacute;, sau đ&oacute; tiếp tục ngủ. Khi t&agrave;u tiếp tục chạy khoảng 30 ph&uacute;t, điện thoại của bạn sẽ bị mất s&oacute;ng v&igrave; đ&atilde; qua bi&ecirc;n giới. L&uacute;c n&agrave;y, bạn n&ecirc;n chỉnh lại đồng hồ, v&igrave; Trung Quốc ch&ecirc;nh lệch Việt Nam 1 m&uacute;i giờ.</p>\n<div id=\"attachment_134504\" class=\"wp-caption aligncenter\"><a href=\"https://cdn3.ivivu.com/2016/04/phuong-hoang-co-tran-ivivu-9.jpg\"><img class=\"wp-image-134504 size-full\" title=\"Trung Quốc c&oacute; rất nhiều thắng cảnh đẹp l&agrave;m n&iacute;u ch&acirc;n du kh&aacute;ch.&nbsp;Ảnh: Nguyễn Trường Sơn\" src=\"https://cdn3.ivivu.com/2016/04/phuong-hoang-co-tran-ivivu-9.jpg\" alt=\"Trung Quốc c&oacute; rất nhiều thắng cảnh đẹp l&agrave;m n&iacute;u ch&acirc;n du kh&aacute;ch.&nbsp;Ảnh: Nguyễn Trường Sơn\" width=\"960\" height=\"640\"></a>\n<p class=\"wp-caption-text\">Trung Quốc c&oacute; rất nhiều thắng cảnh đẹp l&agrave;m n&iacute;u ch&acirc;n du kh&aacute;ch.&nbsp;Ảnh: Nguyễn Trường Sơn</p>\n</div>\n<p>2h00, t&agrave;u đến ga Bằng Tường (Trung Quốc). C&aacute;c bạn phải xuống l&agrave;m thủ tục như b&ecirc;n ga Đồng Đăng, nh&acirc;n vi&ecirc;n ho&agrave;n to&agrave;n l&agrave; người Trung Quốc (nhưng biết n&oacute;i tiếng Việt). N&ecirc;n chuẩn bị t&acirc;m l&yacute;, v&igrave; v&agrave;o đ&acirc;y l&agrave; gặp lực lượng cảnh s&aacute;t, qu&acirc;n đội&hellip; nh&igrave;n rất sợ. Họ y&ecirc;u cầu bạn điền 1 tờ giấy khai nho nhỏ. Bạn điền đầy đủ, sau đ&oacute; mang h&agrave;nh l&yacute; sang b&agrave;n b&ecirc;n cạnh để kiểm tra, tại đ&acirc;y sẽ c&oacute; một anh qu&acirc;n đội lục so&aacute;t đồ của bạn. Rồi bạn đi tới quầy kiểm tra passport đ&oacute;ng dấu! N&oacute;i chung l&agrave; kh&aacute; đơn giản th&ocirc;i, người ta l&agrave;m sao th&igrave; m&igrave;nh y vậy, ch&uacute; &yacute; quan s&aacute;t. Rồi qua qu&eacute;t h&agrave;nh l&yacute; lần nữa. Xong xu&ocirc;i, giờ bạn c&oacute; thể d&agrave;nh thời gian đi toilet.</p>\n<p>10h30 s&aacute;ng theo giờ Trung Quốc, bạn sẽ xuống ga Nam Ninh. L&uacute;c n&agrave;y, bạn n&ecirc;n đi theo những người chung ph&ograve;ng tr&ecirc;n t&agrave;u v&agrave; chuẩn bị t&acirc;m l&yacute;, bởi v&igrave; l&uacute;c n&agrave;y sẽ hiểu thế n&agrave;o l&agrave; từ &ldquo;biển người&rdquo;.</p>\n<p>Từ giờ, c&aacute;c bạn sẽ phải đối diện với 2 chữ lạc l&otilde;ng. Xung quanh l&agrave; những người xa lạ, thứ ng&ocirc;n ngữ xa lạ. C&aacute;c bạn ra khỏi ga, nếu đặt trước kh&aacute;ch sạn c&oacute; thể gọi taxi đưa bạn đến đ&oacute;, hoặc lang thang xung quanh t&igrave;m kh&aacute;ch sạn. Ở Trung Quốc, t&igrave;m kh&aacute;ch sạn với qu&aacute;n ăn cực dễ kh&ocirc;ng g&igrave; l&agrave; kh&oacute; khăn.</p>\n<p>&nbsp;</p>\n<div class=\"ltt-contentbox white\"><strong>Tham khảo th&ecirc;m&nbsp;<a href=\"https://www.ivivu.com/du-lich/tour-trung-quoc-7n6d-bac-kinh-thuong-hai-hang-chau-to-chau/487?utm_source=blog_ivivu&amp;utm_medium=post_link&amp;utm_campaign=internal\" target=\"_blank\" rel=\"noopener\">Tour Trung Quốc 7N6Đ: Bắc Kinh &ndash; Thượng Hải &ndash; H&agrave;ng Ch&acirc;u &ndash; T&ocirc; Ch&acirc;u</a>&nbsp;gi&aacute; ưu đ&atilde;i cực tốt từ iVIVU.com</strong></div>\n<p>&nbsp;</p>\n<p><strong>* Ng&agrave;y 1</strong></p>\n<p>C&aacute;c bạn n&ecirc;n ở kh&aacute;ch sạn nghỉ ngơi sau chuyến đi d&agrave;i, sau đ&oacute; đi lang thang ăn uống buổi chiều, khu vực gần gần nơi ở. Tối đến, c&aacute;c bạn c&oacute; thể đi l&ecirc;n khu trung t&acirc;m mua sắm ở Nam Ninh để đi dạo v&agrave; tham quan. Theo &yacute; kiến của m&igrave;nh th&igrave; c&aacute;c bạn chưa n&ecirc;n mua sắm l&uacute;c n&agrave;y v&igrave; đồ đạc sẽ cồng kềnh v&agrave; đợi khi về rồi mua tiện hơn. Khu trung t&acirc;m Nam Ninh c&oacute; rất nhiều trung t&acirc;m thương mại v&agrave; si&ecirc;u thị lớn, c&oacute; c&aacute;c nh&atilde;n th&ograve;i trang nổi tiến v&agrave; cả khu chợ đ&ecirc;m. Khu chợ đ&ecirc;m ở Nam Ninh b&aacute;n đầy đủ c&aacute;c mặt h&agrave;ng quần &aacute;o, gi&agrave;y d&eacute;p, nước hoa, đồ trang sức cực rẻ, gi&agrave;y chỉ khoảng từ 70 -100.000 đồng/đ&ocirc;i, &aacute;o quần từ 30-90.000 đồng/c&aacute;i. Kế b&ecirc;n khu mua sắm l&agrave; con đường ẩm thực t&ecirc;n &ldquo;Phố ẩm thực Trung Sơn&rdquo;, đồ ăn cực kỳ đa dạng.</p>\n<div id=\"attachment_134411\" class=\"wp-caption aligncenter\"><a href=\"https://cdn3.ivivu.com/2016/04/nam-ninh-ivivu.jpg\"><img class=\"wp-image-134411 size-full\" title=\"Th&agrave;nh phố Nam Ninh lung linh trong đ&ecirc;m. Ảnh: Nguyễn Trường Sơn\" src=\"https://cdn3.ivivu.com/2016/04/nam-ninh-ivivu.jpg\" alt=\"Th&agrave;nh phố Nam Ninh lung linh trong đ&ecirc;m. Ảnh: Nguyễn Trường Sơn\" width=\"960\" height=\"640\"></a>\n<p class=\"wp-caption-text\">Th&agrave;nh phố Nam Ninh lung linh trong đ&ecirc;m. Ảnh: Nguyễn Trường Sơn</p>\n</div>\n<p><strong>* Ng&agrave;y 2</strong></p>\n<p>C&aacute;c bạn n&ecirc;n thử ăn s&aacute;ng với m&oacute;n b&aacute;nh bao, uống sữa đậu n&agrave;nh. B&aacute;nh bao ở Nam Ninh kh&ocirc;ng to như ở Việt Nam, c&oacute; nhiều loại nh&acirc;n lắm, gi&aacute; tầm khoảng 5.000 đồng/c&aacute;i.</p>\n<p>Ăn s&aacute;ng xong, bạn c&oacute; thể t&igrave;m đường đến Thanh T&uacute; Sơn, đ&acirc;y l&agrave; ng&ocirc;i ch&ugrave;a linh thi&ecirc;ng c&oacute; tiếng ở Nam Ninh, v&agrave; cũng c&oacute; cảnh vật rất đẹp xung quanh. Bạn n&ecirc;n d&agrave;nh 1 buổi để tham quan v&agrave; cầu nguyện ở đ&acirc;y.</p>\n<p>Chiều về, bạn c&oacute; thể đi qua Nam Hồ &ndash; một hồ nh&acirc;n tạo rộng lớn, nằm giữa l&ograve;ng th&agrave;nh phố. Hồ được bao quanh bởi một rừng c&acirc;y n&ecirc;n rất m&aacute;t, Nam Ninh được mệnh danh l&agrave; th&agrave;nh phố xanh v&agrave; trong l&agrave;nh nhất Trung Quốc. Đi dạo một v&ograve;ng hồ cảm gi&aacute;c cực kỳ thư gi&atilde;n.</p>\n<p>Buổi tối, bạn c&oacute; thể l&ecirc;n trung t&acirc;m triễn l&atilde;m CAEXPO, đ&acirc;y l&agrave; một to&agrave; nh&agrave; h&igrave;nh c&aacute;nh sen đang h&eacute; nở, l&agrave; một biểu tượng cho sự ph&aacute;t triển của th&agrave;nh phố Nam Ninh.</p>\n<div id=\"attachment_134454\" class=\"wp-caption aligncenter\"><a href=\"https://cdn3.ivivu.com/2016/04/nam-ninh-ivivu1.jpg\"><img class=\"wp-image-134454 size-full\" title=\"Một c&acirc;y cầu ở th&agrave;nh phố Nam Ninh. Ảnh: Nguyễn Trường Sơn\" src=\"https://cdn3.ivivu.com/2016/04/nam-ninh-ivivu1.jpg\" alt=\"Một c&acirc;y cầu ở th&agrave;nh phố Nam Ninh. Ảnh: Nguyễn Trường Sơn\" width=\"960\" height=\"540\"></a>\n<p class=\"wp-caption-text\">Một c&acirc;y cầu ở th&agrave;nh phố Nam Ninh. Ảnh: Nguyễn Trường Sơn</p>\n</div>\n<p><strong>* Ng&agrave;y 3</strong></p>\n<p>D&ugrave;ng bữa s&aacute;ng xong xu&ocirc;i, bạn ra bến xe mua v&eacute; đi th&aacute;c Bản Giốc (tiếng trung l&agrave; &nbsp;德天－板約, phi&ecirc;n &acirc;m l&agrave; D&eacute;tian-Bănyua).</p>\n<p>Xe chạy từ Nam Ninh đến th&aacute;c Bản Giốc khoảng 4 tiếng. Tới nơi c&aacute;c bạn mua v&eacute; v&agrave;o cổng mất 80 tệ, trong khi th&aacute;c Bản Giốc b&ecirc;n ph&iacute;a Việt Nam v&agrave;o cửa miễn ph&iacute;. V&agrave;o b&ecirc;n trong, c&aacute;c bạn n&ecirc;n ăn mỳ hoặc cơm cho no, rồi đi xuống ph&iacute;a dưới th&aacute;c. Cảnh ở đ&acirc;y rất đẹp v&agrave; h&ugrave;ng vĩ. Để cảm nhận hết vẻ h&ugrave;ng vĩ của th&aacute;c, c&aacute;c bạn n&ecirc;n mua v&eacute; đi b&egrave; ra giữa th&aacute;c để chụp ảnh, gi&aacute; v&eacute; đi b&egrave; l&agrave; 30 tệ.</p>\n<div id=\"attachment_134396\" class=\"wp-caption aligncenter\"><a href=\"https://cdn3.ivivu.com/2016/04/thac-ban-gioc-ivivu-3.jpg\"><img class=\"wp-image-134396 size-full\" title=\"Th&aacute;c Bản Giốc l&agrave; một th&aacute;c nước nằm tr&ecirc;n bi&ecirc;n giới Việt Nam v&agrave; Trung Quốc; ph&iacute;a Việt Nam, th&aacute;c thuộc x&atilde; Đ&agrave;m Thuỷ, huyện Tr&ugrave;ng Kh&aacute;nh, tỉnh Cao Bằng, ph&iacute;a Trung Quốc, th&aacute;c thuộc tỉnh Quảng T&acirc;y. Th&aacute;c nước n&agrave;y c&aacute;ch huyện lỵ Tr&ugrave;ng Kh&aacute;nh khoảng 20 km về ph&iacute;a đ&ocirc;ng bắc.Th&aacute;c Bản Giốc hiện nay l&agrave; th&aacute;c nước lớn thứ nh&igrave; thế giới trong c&aacute;c th&aacute;c nước nằm tr&ecirc;n một đường bi&ecirc;n giới. Ph&iacute;a Trung Quốc gọi th&aacute;c n&agrave;y l&agrave; th&aacute;c Đức Thi&ecirc;n. Ảnh: Nguyễn Trường Sơn\" src=\"https://cdn3.ivivu.com/2016/04/thac-ban-gioc-ivivu-3.jpg\" alt=\"Th&aacute;c Bản Giốc l&agrave; một th&aacute;c nước nằm tr&ecirc;n bi&ecirc;n giới Việt Nam v&agrave; Trung Quốc; ph&iacute;a Việt Nam, th&aacute;c thuộc x&atilde; Đ&agrave;m Thuỷ, huyện Tr&ugrave;ng Kh&aacute;nh, tỉnh Cao Bằng, ph&iacute;a Trung Quốc, th&aacute;c thuộc tỉnh Quảng T&acirc;y. Th&aacute;c nước n&agrave;y c&aacute;ch huyện lỵ Tr&ugrave;ng Kh&aacute;nh khoảng 20 km về ph&iacute;a đ&ocirc;ng bắc.Th&aacute;c Bản Giốc hiện nay l&agrave; th&aacute;c nước lớn thứ nh&igrave; thế giới trong c&aacute;c th&aacute;c nước nằm tr&ecirc;n một đường bi&ecirc;n giới. Ph&iacute;a Trung Quốc gọi th&aacute;c n&agrave;y l&agrave; th&aacute;c Đức Thi&ecirc;n. Ảnh: Nguyễn Trường Sơn\" width=\"960\" height=\"639\"></a>\n<p class=\"wp-caption-text\">Th&aacute;c Bản Giốc l&agrave; một th&aacute;c nước nằm tr&ecirc;n bi&ecirc;n giới Việt Nam v&agrave; Trung Quốc; ph&iacute;a Việt Nam, th&aacute;c thuộc x&atilde; Đ&agrave;m Thuỷ, huyện Tr&ugrave;ng Kh&aacute;nh, tỉnh Cao Bằng, ph&iacute;a Trung Quốc, th&aacute;c thuộc tỉnh Quảng T&acirc;y. Th&aacute;c nước n&agrave;y c&aacute;ch huyện lỵ Tr&ugrave;ng Kh&aacute;nh khoảng 20 km về ph&iacute;a Đ&ocirc;ng Bắc. Th&aacute;c Bản Giốc hiện nay l&agrave; th&aacute;c nước lớn thứ nh&igrave; thế giới trong c&aacute;c th&aacute;c nước nằm tr&ecirc;n một đường bi&ecirc;n giới. Ph&iacute;a Trung Quốc gọi th&aacute;c n&agrave;y l&agrave; th&aacute;c Đức Thi&ecirc;n. Ảnh: Nguyễn Trường Sơn</p>\n</div>\n<p>Sau khi chụp h&igrave;nh dưới th&aacute;c, c&aacute;c bạn c&oacute; thể đi bộ l&ecirc;n tr&ecirc;n cột mốc bi&ecirc;n giới Trung Quốc ngay tr&ecirc;n th&aacute;c để chụp ảnh, tuy nhi&ecirc;n tuyệt đối kh&ocirc;ng được c&oacute; h&agrave;nh vi bất lịch sự hay l&agrave;m tổn hại đến cột mốc. V&agrave; đặc biệt bạn n&ecirc;n ch&uacute; &yacute;, kh&ocirc;ng nhảy l&ecirc;n bờ l&ecirc;n ph&iacute;a đất Việt Nam, bởi việc n&agrave;y c&oacute; thể g&acirc;y ra hậu quả nghi&ecirc;m trọng. Ở cột mốc c&oacute; s&oacute;ng điện thoại d&ugrave;ng được tốt, n&ecirc;n bạn c&oacute; thể x&agrave;i 3G check-in, up ảnh l&ecirc;n facebook thoải m&aacute;i.</p>\n<p>Trước 2h chiều, c&aacute;c bạn n&ecirc;n trở l&ecirc;n lại ph&iacute;a bến xe, v&igrave; chiều sẽ kh&ocirc;ng c&oacute; xe về lại Nam Ninh.&nbsp;Trở về Nam Ninh, bạn n&ecirc;n đi dạo nếm thử c&aacute;c m&oacute;n ăn ngon ở đ&acirc;y. Nền ẩm thực Trung Quốc v&ocirc; c&ugrave;ng phong ph&uacute;, trong việc kh&aacute;m ph&aacute; đất nước n&agrave;y th&igrave; ẩm thực c&oacute; lẽ l&agrave; điều hấp dẫn m&igrave;nh nhất.</p>\n<div id=\"attachment_134404\" class=\"wp-caption aligncenter\"><a href=\"https://cdn3.ivivu.com/2016/04/thac-ban-gioc-ivivu-4.jpg\"><img class=\"wp-image-134404 size-full\" title=\"H&igrave;nh chụp th&aacute;c từ ph&iacute;a Trung Quốc, nh&igrave;n về Việt Nam. Ảnh: Nguyễn Trường Sơn\" src=\"https://cdn3.ivivu.com/2016/04/thac-ban-gioc-ivivu-4.jpg\" alt=\"H&igrave;nh chụp th&aacute;c từ ph&iacute;a Trung Quốc, nh&igrave;n về Việt Nam. Ảnh: Nguyễn Trường Sơn\" width=\"960\" height=\"639\"></a>\n<p class=\"wp-caption-text\">H&igrave;nh chụp th&aacute;c từ ph&iacute;a Trung Quốc, nh&igrave;n về Việt Nam. Ảnh: Nguyễn Trường Sơn</p>\n</div>\n<div id=\"attachment_134405\" class=\"wp-caption aligncenter\"><a href=\"https://cdn3.ivivu.com/2016/04/thac-ban-gioc-ivivu-2.jpg\"><img class=\"wp-image-134405 size-full\" title=\"Du kh&aacute;ch thu&ecirc; b&egrave; ngắm th&aacute;c. Ảnh: Nguyễn Trường Sơn\" src=\"https://cdn3.ivivu.com/2016/04/thac-ban-gioc-ivivu-2.jpg\" alt=\"Du kh&aacute;ch thu&ecirc; b&egrave; ngắm th&aacute;c. Ảnh: Nguyễn Trường Sơn\" width=\"960\" height=\"640\"></a>\n<p class=\"wp-caption-text\">Du kh&aacute;ch thu&ecirc; b&egrave; ngắm th&aacute;c. Ảnh: Nguyễn Trường Sơn</p>\n</div>\n<p><strong>* Ng&agrave;y 4</strong></p>\n<p>C&aacute;c bạn n&ecirc;n đi dọc con s&ocirc;ng ở Nam Ninh ngắm cảnh, đi bộ dạo quanh th&agrave;nh phố n&agrave;y. Nếu biết tiếng Trung, c&aacute;c bạn c&oacute; thể lang thang cả ng&agrave;y ở đ&acirc;y m&agrave; kh&ocirc;ng thấy ch&aacute;n. Sau một ng&agrave;y mệt ở Bản Giốc, c&aacute;c bạn cần phải nghỉ ngơi một ch&uacute;t.</p>\n<p>4h chiều, bạn ra ga t&agrave;u Nam Ninh, mua v&eacute; đi Trương Gia Giới. 5h30 t&agrave;u chạy. Trước khi l&ecirc;n t&agrave;u, bạn n&ecirc;n mua một &iacute;t mỳ ly, đồ ăn đ&oacute;ng hộp,&hellip; để sử dụng.</p>\n<p>&nbsp;</p>\n<div class=\"ltt-contentbox white\"><strong>Tham khảo th&ecirc;m&nbsp;<a href=\"https://www.ivivu.com/du-lich/tour-trung-quoc-7n-6d-ha-noi-bac-kinh-thuong-hai/357?utm_source=blog_ivivu&amp;utm_medium=post_link&amp;utm_campaign=internal\" target=\"_blank\" rel=\"noopener\">Tour Trung Quốc 7N6D: H&agrave; Nội &ndash; Bắc Kinh &ndash; Thượng Hải</a>&nbsp;gi&aacute; ưu đ&atilde;i cực tốt từ iVIVU.com</strong></div>\n<p>&nbsp;</p>\n<p><strong>* Ng&agrave;y 5</strong></p>\n<p>10h00 s&aacute;ng t&agrave;u tới ga, ra khỏi ga sẽ c&oacute; rất nhiều c&ograve; nhận đưa c&aacute;c bạn l&ecirc;n Trương Gia Giới. C&aacute;c bạn nếu h&agrave;nh l&yacute; nhiều, n&ecirc;n gửi mang l&ecirc;n trước, v&igrave; sẽ đi bộ cả ng&agrave;y d&agrave;i v&agrave; leo n&uacute;i. Sau khi gửi h&agrave;nh l&yacute;, chọn cho m&igrave;nh người dẫn đường. C&aacute;c bạn sẽ đi xe &ocirc; t&ocirc; v&agrave;o ch&acirc;n n&uacute;i. Tới nơi c&aacute;c bạn mua v&eacute; v&agrave;o cổng gi&aacute; 80 tệ.</p>', '2023-11-19', 'Cùng iVIVU.com theo chân bạn Nguyễn Trường Sơn khám phá lịch trình du lịch Trung Quốc trong 15 ngày chỉ với 10 triệu đồng, qua những thắng cảnh đẹp của thành phố Nam Ninh, Trương Gia Giới và Phượng Hoàng Cổ Trấn nhé!', 'http://res.cloudinary.com/dxqh3xpza/image/upload/v1700367293/qeqaa5w3esrm0c6arxwc.jpg', 1, b'0', 'Lịch trình du lịch Trung Quốc trong 15 ngày chỉ với 10 triệu đồng', 1),
(8, '<p>Mỗi v&ugrave;ng miền, cơm g&agrave; được chế biến theo nhiều c&aacute;ch kh&aacute;c nhau, với gia vị cũng kh&ocirc;ng giống nhau, để tạo n&ecirc;n những m&oacute;n ăn &ldquo;ngon b&aacute; ch&aacute;y&rdquo;.</p>\n<p><strong>Cơm đảo g&agrave; rang xứ Bắc</strong></p>\n<p>L&acirc;u nay, người miền Bắc ăn g&agrave; theo kiểu chế biến th&agrave;nh một loại thực phẩm ăn k&egrave;m hoặc kh&ocirc;ng k&egrave;m cơm, chứ kh&ocirc;ng gắn thẳng v&agrave;o cơm để th&agrave;nh một thứ cơm g&agrave;. Song, độ khoảng v&agrave;i năm trở lại đ&acirc;y, ở H&agrave; Nội xuất hiện loại cơm k&egrave;m g&agrave;: Cơm đảo g&agrave; rang.</p>\n<div id=\"attachment_133190\" class=\"wp-caption aligncenter\"><img class=\"wp-image-133190 size-full\" title=\"Cơm đảo g&agrave; rang mới nổi ở H&agrave; Nội. Ảnh: Ho&agrave;ng Nhi\" src=\"https://cdn3.ivivu.com/2016/04/com-ga-tu-bac-vao-nam-an-o-dau-cung-ngon-ivivu-1.jpg\" alt=\"Cơm đảo g&agrave; rang mới nổi ở H&agrave; Nội. Ảnh: Ho&agrave;ng Nhi\" width=\"600\" height=\"400\">\n<p class=\"wp-caption-text\">Cơm đảo g&agrave; rang mới nổi ở H&agrave; Nội. Ảnh: Ho&agrave;ng Nhi</p>\n</div>\n<p>M&oacute;n ăn kết hợp thịt g&agrave; rang gừng với cơm trắng rang săn trong chảo to, lửa lớn, li&ecirc;n tục được đảo bằng v&aacute; để đảm bảo hạt cơm được tiếp x&uacute;c nhiệt đều. Cơm trắng rang săn vốn chẳng lạ với người Bắc, nhất l&agrave; với những người thuộc thế hệ 7x, thường xuy&ecirc;n phải ăn s&aacute;ng bằng cơm rang với t&oacute;p mỡ, nước mắm. G&agrave; rang gừng cũng thế, thường được chế biến th&agrave;nh một m&oacute;n ăn &ldquo;tuyệt c&uacute; m&egrave;o&rdquo; mỗi khi trời trở lạnh. Kết hợp hai loại thực phẩm n&agrave;y với nhau, &ldquo;cơm đảo g&agrave; rang&rdquo; nghe kh&aacute; lạ tai v&agrave; ngon miệng.</p>\n<p>Nơi khởi ph&aacute;t của m&oacute;n cơm đảo g&agrave; rang c&oacute; thể kể đến khu ẩm thực Tống Duy T&acirc;n (Ho&agrave;n Kiếm, H&agrave; Nội), sau đ&oacute; lan khắp hang c&ugrave;ng ng&otilde; hẻm ở khu phố cổ. Cơm được nấu từ loại gạo ngon như nấu cơm b&igrave;nh thường sao cho hạt kh&ocirc; v&agrave; săn. Khi c&oacute; kh&aacute;ch, người phục vụ mới bật bếp gas, l&agrave;m n&oacute;ng chảo rồi đổ cơm v&agrave;o đảo. V&aacute; chạm chảo tạo th&agrave;nh những &acirc;m thanh loẻng xoẻng li&ecirc;n hồi nghe kh&aacute; vui tai.</p>\n<p>T&ugrave;y từng nơi m&agrave; người ta c&oacute; cho th&ecirc;m trứng g&agrave; v&agrave;o tạo m&agrave;u v&agrave;ng hấp dẫn hay chỉ để hạt cơm trắng x&eacute;m n&acirc;u. Khi cơm được đảo săn đều, tỏa m&ugrave;i gạo, bốc kh&oacute;i nghi ng&uacute;t, th&igrave; tr&uacute;t ra đĩa. Thịt g&agrave; được rang trong nồi to, lu&ocirc;n n&oacute;ng hổi, thơm phức m&ugrave;i gừng, lấp lo&aacute;ng những &aacute;nh mỡ g&agrave;, được m&uacute;c ra t&ocirc; nhỏ, đặt cạnh đĩa cơm. Chưa hết, phải th&ecirc;m một đĩa dưa cải v&agrave;ng, muối chua, gi&ograve;n gi&ograve;n cay cay nữa mới đủ bộ lệ.</p>\n<p>Chỉ khoảng 15 ph&uacute;t từ l&uacute;c k&ecirc;u m&oacute;n bạn sẽ được phục vụ một khay n&agrave;o cơm, n&agrave;o g&agrave;, n&agrave;o dưa chua nghi ng&uacute;t kh&oacute;i. C&aacute;i hạt cơm săn cực ăn &yacute; với miếng g&agrave; rang mềm, b&eacute;o ngậy, lẫn miếng gừng th&aacute;i chỉ vừa n&oacute;ng vừa thơm, đưa đẩy bằng gắp dưa chua. C&aacute;i nước g&agrave; ấy rưới l&ecirc;n cơm th&igrave; hợp vị hơn mọi loại nước sốt, nước chấm n&agrave;o.</p>\n<p>Ăn no căng, ph&iacute; tổn mới chỉ ngang một b&aacute;t phở, khoảng 40.000 đồng. Bạn c&oacute; thể ăn ở rất nhiều nơi như khu Tống Duy T&acirc;n, H&agrave;ng Buồm, M&atilde; M&acirc;y, Đ&agrave;o Duy Từ&hellip;</p>\n<p>&nbsp;</p>\n<div class=\"ltt-contentbox white\"><strong>Xem th&ecirc;m&nbsp;<a href=\"https://www.ivivu.com/du-lich/tour-ha-noi-1n-ha-noi-bai-dinh-trang-an/436?utm_source=blog_ivivu&amp;utm_medium=post_link&amp;utm_campaign=internal\" target=\"_blank\" rel=\"noopener\">Tour H&agrave; Nội 1N: H&agrave; Nội &ndash; B&aacute;i Đ&iacute;nh &ndash; Tr&agrave;ng An</a>&nbsp;gi&aacute; ưu đ&atilde;i từ iVIVU.com</strong></div>\n<p>&nbsp;</p>\n<p><strong>Cơm g&agrave; Quảng Nam</strong></p>\n<div id=\"attachment_133191\" class=\"wp-caption aligncenter\"><img class=\"wp-image-133191 size-full\" title=\"Cơm g&agrave; B&agrave; Buội nổi tiếng ở Hội An. Ảnh: Tuấn Đ&agrave;o\" src=\"https://cdn3.ivivu.com/2016/04/com-ga-tu-bac-vao-nam-an-o-dau-cung-ngon-ivivu-2.jpg\" alt=\"Cơm g&agrave; B&agrave; Buội nổi tiếng ở Hội An. Ảnh: Tuấn Đ&agrave;o\" width=\"600\" height=\"400\">\n<p class=\"wp-caption-text\">Cơm g&agrave; B&agrave; Buội nổi tiếng ở Hội An. Ảnh: Tuấn Đ&agrave;o</p>\n</div>\n<div><iframe src=\"https://www.ivivu.com/hothotel/?placeslug=an-choi-am-thuc&amp;number=1&amp;width=481\" width=\"100%\" height=\"312\" scrolling=\"no\"></iframe></div>\n<p>Quảng Nam chả c&oacute; g&igrave; ngo&agrave;i cơm g&agrave;. G&agrave; ở đất Quảng Nam thịt thơm, ngọt m&agrave; lại kh&ocirc;ng bở như &ldquo;g&agrave; ta&rdquo; t&agrave;o lao trong si&ecirc;u thị. V&igrave; thế, c&aacute;i xứ Quảng Nam &ldquo;chưa mưa đ&atilde; nắng&rdquo; c&oacute; tới hai loại cơm g&agrave; nổi tiếng gắp nơi: Hội An v&agrave; Tam Kỳ. Hai trường ph&aacute;i n&agrave;y chắc thỉnh thoảng cũng &ldquo;c&atilde;i nhau&rdquo; xem cơm g&agrave; ai ch&iacute;nh hiệu Quảng Nam, khi m&oacute;n ăn c&oacute; sự kh&aacute;c biệt nằm ở h&igrave;nh thức thịt g&agrave;.</p>\n<p>Cơm g&agrave; Hội An lấy g&agrave; ngon, l&agrave;m sạch, luộc ch&iacute;n tới, kh&ocirc;ng dai kh&ocirc;ng mềm. Lọc g&agrave; thịt ra thịt, xương ra xương rồi lại đưa xương v&agrave;o nồi ninh tiếp để lấy th&ecirc;m chất ngọt, vị thơm. Gạo ngon của vụ trước hoặc trước nữa c&agrave;ng tốt đem ng&acirc;m nghệ cho v&agrave;ng rồi trộn đều, vo sạch rồi cho v&agrave;o nồi dước d&ugrave;ng g&agrave;, canh sao cho nước lấp xấp mặt gạo th&igrave; hạt cơm ch&iacute;n mới kh&ocirc; v&agrave; săn. Chưa xong, phần mỡ g&agrave; cũng cho v&agrave;o nốt, để rồi dưới t&aacute;c động nhiệt, từng giọt mỡ mới ứa ra, thấm v&agrave;o hạt cơm, đem th&ecirc;m m&ugrave;i thơm kh&oacute; tả.</p>\n<p>Cơm ch&iacute;n, đơm ra đĩa, tạo h&igrave;nh cho đẹp, đặng chụp ảnh đăng Facebook, lấy phần thịt g&agrave; đ&atilde; tước mảnh kia ra, t&ugrave;y theo khẩu vị m&agrave; đ&ograve;i thịt trắng, thịt đ&ugrave;i hay nhiều da&hellip; phủ l&ecirc;n phần cơm. C&oacute; hai thứ rau thơm ăn k&egrave;m bất biến l&agrave; rau răm v&agrave; rau quế. Kh&ocirc;ng c&oacute; 2 thứ rau n&agrave;y, ăn cơm g&agrave; Quảng Nam cứ&hellip; sao sao.</p>\n<p>Nếu ăn được cay th&igrave; n&ecirc;n cắn đ&ocirc;i v&agrave;i quả ớt xanh cay cay, thơm thơm cho th&ecirc;m dịch vị. Ăn cơm g&agrave; m&agrave; c&oacute; th&ecirc;m tiếng h&iacute;t h&agrave;, xu&yacute;t xoa kể cũng th&uacute;, như uống rượu phải cụng ly. C&oacute; thể ăn k&egrave;m dưa m&oacute;n chua chua để đưa đẩy cho trơn lưỡi, bon miệng. Cơm g&agrave; Hội An tất nhi&ecirc;n ăn ở Hội An, t&igrave;m được qu&aacute;n b&agrave; Buội th&igrave; tốt kh&ocirc;ng th&igrave; ăn ở một qu&aacute;n kh&ocirc;ng t&ecirc;n nằm m&eacute; h&ocirc;ng đ&igrave;nh.</p>\n<div id=\"attachment_133192\" class=\"wp-caption aligncenter\"><img class=\"wp-image-133192 size-full\" title=\"Một m&acirc;m cơm g&agrave; Tam Kỳ. Ảnh: Hồng Li&ecirc;n\" src=\"https://cdn3.ivivu.com/2016/04/com-ga-tu-bac-vao-nam-an-o-dau-cung-ngon-ivivu-3.jpg\" alt=\"Một m&acirc;m cơm g&agrave; Tam Kỳ. Ảnh: Hồng Li&ecirc;n\" width=\"600\" height=\"400\">\n<p class=\"wp-caption-text\">Một m&acirc;m cơm g&agrave; Tam Kỳ. Ảnh: Hồng Li&ecirc;n</p>\n</div>\n<p>Với cơm g&agrave; Tam Kỳ, kh&acirc;u chế biến cũng tương tự, gia vị n&ecirc;m nếm cũng tương tự, chỉ kh&aacute;c l&agrave; thịt g&agrave; kh&ocirc;ng x&eacute; m&agrave; chặt th&agrave;nh từng miếng đ&ugrave;i, miếng lườn, miếng c&aacute;nh. Cơm g&agrave; Tam Kỳ c&ograve;n kh&aacute;c ở chỗ c&oacute; th&ecirc;m b&aacute;t nước d&ugrave;ng g&agrave; nấu l&aacute; giang chua chua, d&igrave;u dịu v&agrave; nhiều m&oacute;n ăn k&egrave;m như dưa m&oacute;n, dưa cải chua, c&agrave; chua xắt l&aacute;t&hellip; l&agrave;m cho đĩa cơm sinh động hơn.</p>\n<p>&nbsp;</p>\n<div class=\"ltt-contentbox white\"><strong>Click xem th&ecirc;m&nbsp;<a href=\"https://www.ivivu.com/khach-san-quang-nam/khach-san-muong-thanh-grand-quang-nam?utm_source=blog_ivivu&amp;utm_medium=post_link&amp;utm_campaign=internal\" target=\"_blank\" rel=\"nofollow noopener\">Kh&aacute;ch Sạn Mường Thanh Grand Quảng Nam</a>&nbsp;đang khuyến m&atilde;i giảm gi&aacute; từ iVIVU.com</strong></div>\n<p>&nbsp;</p>\n<p><strong>Cơm g&agrave; xối mỡ v&agrave; cơm g&agrave; Hải Nam S&agrave;i G&ograve;n</strong></p>\n<p>S&agrave;i G&ograve;n c&aacute;i g&igrave; cũng c&oacute;, nhất l&agrave; m&oacute;n cơm g&agrave;. Nổi tiếng l&acirc;u đời l&agrave; cơm g&agrave; Hải Nam của người T&agrave;u v&ugrave;ng Hải Nam truyền sang Singapore, Malaysia, Indonesia v&agrave; S&agrave;i G&ograve;n v&agrave; m&oacute;n cơm g&agrave; n&agrave;y nổi tiếng như cơm rang Dương Ch&acirc;u.</p>\n<p>Truyền b&aacute; rộng r&atilde;i v&agrave; phổ biến như thế, cho n&ecirc;n cơm g&agrave; Hải Nam c&oacute; rất nhiều t&ocirc;ng ph&aacute;i. Nhưng tựu chung, n&eacute;t ti&ecirc;u biểu của m&oacute;n ăn l&agrave; thịt g&agrave; luộc ch&iacute;n tới, ăn k&egrave;m cơm nấu bằng ch&iacute;nh nước luộc g&agrave;, hơi giống cơm g&agrave; Quảng Nam, nhưng giống về kiểu c&aacute;ch th&ocirc;i, chứ bản chất rất kh&aacute;c nhau.</p>\n<div id=\"attachment_133193\" class=\"wp-caption aligncenter\"><img class=\"wp-image-133193 size-full\" title=\"Cơm g&agrave; Hải Nam S&agrave;i G&ograve;n. Ảnh: Amthucsaigon\" src=\"https://cdn3.ivivu.com/2016/04/com-ga-tu-bac-vao-nam-an-o-dau-cung-ngon-ivivu-4.jpg\" alt=\"Cơm g&agrave; Hải Nam S&agrave;i G&ograve;n. Ảnh: Amthucsaigon\" width=\"450\" height=\"565\">\n<p class=\"wp-caption-text\">Cơm g&agrave; Hải Nam S&agrave;i G&ograve;n. Ảnh: Amthucsaigon</p>\n</div>\n<p>G&agrave; ngon luộc ch&iacute;n tới, c&ugrave;ng muối hạt v&agrave; gừng cắt l&aacute;t. Trong l&uacute;c luộc g&agrave;, bắc chảo l&ecirc;n bếp, đun n&oacute;ng, phi thơm đầu h&agrave;nh, tỏi xắt với dầu ăn cho v&agrave;ng. Cho gạo v&agrave; mỡ g&agrave; v&agrave;o x&agrave;o cho thật săn. G&agrave; đ&atilde; luộc xong, nhấc ra, rồi chắt nước d&ugrave;ng v&agrave;o chảo cơm, canh sao cho nước lấp xấp mặt gạo v&agrave; nấu ch&iacute;n.</p>\n<p>Trong l&uacute;c đ&oacute;, xối g&agrave; luộc dưới nước thật lạnh để phần da th&igrave; gi&ograve;n, phần thịt th&igrave; mềm, ngọt m&agrave; kh&ocirc;ng n&aacute;t bở. D&ugrave;ng dao sắc chặt g&agrave; th&agrave;nh những miếng vừa ăn, l&agrave;nh lặn, đủ cả da, thịt, xương rồi sắp ra đĩa. B&agrave;y th&ecirc;m dưa leo, dưa m&oacute;n, c&agrave; chua cho th&ecirc;m đẹp v&agrave; vitamin lẫn chất xơ. Cơm ch&iacute;n, xới đổ khu&ocirc;n ở g&oacute;c đĩa sao bắt mắt.</p>\n<p>Chưa xong, chảo đang n&oacute;ng lập tức cho gừng v&agrave; đầu h&agrave;nh băm nhỏ v&agrave;o phi thơm, n&ecirc;m 2 muỗng dầu h&agrave;o, 2 muỗng x&igrave; dầu, ch&uacute;t đường, v&agrave;i muỗng nước g&agrave; v&agrave; bột bắp. Đun s&ocirc;i, tr&uacute;t ra b&aacute;t nhỏ l&agrave;m nước sốt. Chỉ cần nếm miếng đầu ti&ecirc;n đ&atilde; thấy kh&aacute;c cơm g&agrave; Quảng Nam. M&ugrave;i gừng tr&ecirc;n thịt g&agrave; v&agrave; nước sốt l&agrave; thứ đặc trưng của người T&agrave;u. Đ&oacute; cũng ch&iacute;nh l&agrave; n&eacute;t độc đ&aacute;o cho cơm g&agrave; Hải Nam.</p>\n<p>S&agrave;i G&ograve;n c&ograve;n cơm g&agrave; xối mỡ, thứ cơm g&agrave; nhiều nhan nhản giống như cơm đảo g&agrave; rang ngo&agrave;i H&agrave; Nội. Giống nhau cả c&aacute;ch rang cơm nữa. Cơm kh&ocirc;ng nấu bằng nước d&ugrave;ng g&agrave; m&agrave; nấu b&igrave;nh thường, sau đ&oacute; đem rang săn với thứ nguy&ecirc;n liệu g&igrave; đ&oacute; tạo th&agrave;nh cơm m&agrave;u cam, tr&ocirc;ng kh&aacute; l&agrave; lạ.</p>\n<p>C&ograve;n g&agrave; được sơ chế bằng c&aacute;ch chặt tư, chặt t&aacute;m như ở KFC, chi&ecirc;n sơ sơ. Khi kh&aacute;ch k&ecirc;u m&oacute;n, chủ qu&aacute;n liền gắp từng miếng g&agrave; to tướng cho v&agrave;o d&agrave;n m&aacute;y. Sau đ&oacute;, mỡ n&oacute;ng bắt đầu xối th&agrave;nh d&ograve;ng xuống miếng g&agrave; như mưa, khiến cho lớp da g&agrave; v&agrave;ng ruộm l&ecirc;n, gi&ograve;n nhưng kh&ocirc;ng đọng mỡ.</p>\n<p>Cơm n&eacute;n th&agrave;nh b&aacute;t tr&ecirc;n đĩa, b&ecirc;n cạnh l&agrave; miếng g&agrave; xối v&agrave;ng &oacute;ng ả, ngo&agrave;i gi&ograve;n trong mềm ứa nước miếng. Tất nhi&ecirc;n, giống như những loại cơm g&agrave; kh&aacute;c, kh&ocirc;ng thể thiếu m&oacute;n dưa m&oacute;n, dưa leo hay c&agrave; chua ng&acirc;m giấm. Thế th&ocirc;i, m&agrave; ai cũng m&ecirc; mẩn cơm g&agrave; xối mỡ.</p>\n<p>&nbsp;</p>\n<div class=\"ltt-contentbox white\"><strong>Click xem th&ecirc;m&nbsp;&nbsp;<a href=\"https://www.ivivu.com/khach-san-ho-chi-minh/khach-san-rex-sai-gon?utm_source=blog_ivivu&amp;utm_medium=post_link&amp;utm_campaign=internal\" target=\"_blank\" rel=\"noopener\">Kh&aacute;ch sạn Rex S&agrave;i G&ograve;n</a>&nbsp;đang khuyến m&atilde;i giảm gi&aacute; từ iVIVU.com</strong></div>\n<p>&nbsp;</p>\n<p><strong>Cơm g&agrave; v&ugrave;ng sơn cước</strong></p>\n<div id=\"attachment_133194\" class=\"wp-caption aligncenter\"><a href=\"https://cdn3.ivivu.com/2016/04/com-ga-tu-bac-vao-nam-an-o-dau-cung-ngon-ivivu-5.jpg\"><img class=\"wp-image-133194 size-full\" title=\"Cơm lam g&agrave; nướng nổi tiếng ở Pleiku. Ảnh: Phương Linh Nguyễn\" src=\"https://cdn3.ivivu.com/2016/04/com-ga-tu-bac-vao-nam-an-o-dau-cung-ngon-ivivu-5.jpg\" alt=\"Cơm lam g&agrave; nướng nổi tiếng ở Pleiku. Ảnh: Phương Linh Nguyễn\" width=\"600\" height=\"400\"></a>\n<p class=\"wp-caption-text\">Cơm lam g&agrave; nướng nổi tiếng ở Pleiku. Ảnh: Phương Linh Nguyễn</p>\n</div>\n<p>Nếu bạn đi chơi v&ugrave;ng T&acirc;y Bắc hay Cao nguy&ecirc;n, chắc chắn kh&ocirc;ng thể bỏ qua m&oacute;n cơm lam &ndash; g&agrave; nướng đậm đ&agrave; hương vị n&uacute;i rừng. Cơm lam chế biến đơn giản, gạo nương ngon cho v&agrave;o ống tre, tra hay kh&ocirc;ng tra nước t&ugrave;y theo lượng nước c&oacute; sẵn trong th&acirc;n tre, n&uacute;t lại rồi v&ugrave;i tro n&oacute;ng hay dựng cạnh bếp lửa.</p>\n<p>G&agrave; l&agrave;m sạch, ướp gia vị n&uacute;i rừng như mắc kh&eacute;n, hạt dổi ở T&acirc;y Bắc hoặc mật ong, ti&ecirc;u sọ, tỏi, vỏ cam xắt&hellip; ở Pleiku rồi xi&ecirc;n hoặc kẹp que tre tươi, rồi cũng dựng quanh l&ograve; than hồng rực. Khi g&agrave; chuyển m&agrave;u n&acirc;u &oacute;ng, thơm thơm, ngọt ngọt l&agrave; bỏ ra mẹt l&oacute;t l&aacute; chuối. Rồi cứ thế tay miền n&uacute;i, tay miền xu&ocirc;i thoải m&aacute;i th&ograve; v&agrave;o x&eacute; g&agrave; th&agrave;nh miếng, rồi chấm muối hạt gi&atilde; mắc kh&eacute;n (T&acirc;y Bắc) hoặc muối hầm gi&atilde; với hạt &eacute;, ớt xanh (Pleiku) v&agrave; đưa v&agrave;o miệng nhai th&igrave; chẳng c&oacute; kiểu g&agrave; n&agrave;o địch được.</p>\n<p>C&aacute;i vị g&agrave; nướng mọi than hồng d&ugrave; của người Th&aacute;i ở T&acirc;y Bắc hay Jrai ở Pleiku ngon một c&aacute;ch kh&oacute; tả. Mọi thứ đều nguy&ecirc;n sơ từ vị ngọt của thịt, m&ugrave;i nồng của mắc kh&eacute;n hay hạt &eacute;, đến m&ugrave;i than củi. Kh&ocirc;ng gian n&uacute;i rừng lại c&agrave;ng l&agrave;m đượm vị, c&agrave;ng khiến những ch&eacute;n rượu hết cạn lại đầy.</p>\n<p>Khi đ&atilde; ch&iacute;n, những ống cơm lam tỏa m&ugrave;i gạo nương thơm phức, len lỏi qua l&oacute;t l&aacute; chuối bay ra. Cầm dao tước ống tre, thấy nắm cơm nằm mịn m&agrave;ng sau lớp lụa tre v&ocirc; c&ugrave;ng hấp dẫn. Hạt cơm dẻo, trong trong, &oacute;ng &oacute;ng như trứng kiến, thơm như x&ocirc;i, ng&aacute;i ng&aacute;i vị nước tre quyện với miếng g&agrave; nướng thật tột c&ugrave;ng. C&oacute; thể ăn cơm lam với nhiều thứ thịt nướng kh&aacute;c, nhưng chẳng c&oacute; g&igrave; s&aacute;nh bằng g&agrave;. Như thế mới l&agrave; đặc sản khi đến với n&uacute;i rừng.</p>', '2023-11-19', 'Gà vốn là thứ dễ tính, có thể solo nào luộc, hấp, gỏi, xé phay… hay kết hợp với phở, bún, miến, cháo, mỳ… và ‘đỉnh’ nhất có lẽ là với cơm.', 'http://res.cloudinary.com/dxqh3xpza/image/upload/v1700367395/rb9vgwl9r5u9wce3e4ni.jpg', 1, b'0', 'Cơm gà từ Bắc vào Nam ăn ở đâu cũng ngon', 1),
(9, '<p>Trước mỗi chuyến đi, bạn n&ecirc;n t&igrave;m hiểu kỹ th&ocirc;ng tin về địa phương, đất nước m&igrave;nh sắp đến để c&oacute; kế hoạch chi tiết. Cụ thể, bạn sẽ nghỉ ngơi tại đ&acirc;u, di chuyển ra sao, ăn uống c&aacute;c bữa ch&iacute;nh v&agrave; phụ như thế n&agrave;o, gi&aacute; cả tại đ&oacute; đắt đỏ hay rẻ hơn so với nơi bạn đang sinh sống&hellip; Từ đ&oacute;, bạn sẽ chủ động hơn trong chuyến đi v&agrave; kh&ocirc;ng gặp phải phiền to&aacute;i ngo&agrave;i mong muốn. Hiện nay, nhiều diễn đ&agrave;n với chủ đề về du lịch đều c&oacute; th&ocirc;ng tin hữu &iacute;ch. Đ&acirc;y l&agrave; c&aacute;ch bạn trở th&agrave;nh người du lịch th&ocirc;ng minh, tr&aacute;nh những điểm thường l&agrave;m gi&aacute;, chặt ch&eacute;m kh&aacute;ch h&agrave;ng.</p>\n<div id=\"attachment_133960\" class=\"wp-caption aligncenter\"><a href=\"https://cdn3.ivivu.com/2016/04/chat-chem-du-lich-IVIVU.jpg\"><img class=\"wp-image-133960 size-full\" title=\"T&igrave;m hiểu kỹ th&ocirc;ng tin trước chuyến đi sẽ gi&uacute;p bạn tr&aacute;nh khỏi những chi&ecirc;u &quot;chặt ch&eacute;m&quot; kh&ocirc;ng đ&aacute;ng c&oacute;. Ảnh minh họa: Leo\" src=\"https://cdn3.ivivu.com/2016/04/chat-chem-du-lich-IVIVU.jpg\" alt=\"T&igrave;m hiểu kỹ th&ocirc;ng tin trước chuyến đi sẽ gi&uacute;p bạn tr&aacute;nh khỏi những chi&ecirc;u &quot;chặt ch&eacute;m&quot; kh&ocirc;ng đ&aacute;ng c&oacute;. Ảnh: ST\" width=\"500\" height=\"281\"></a>\n<p class=\"wp-caption-text\">T&igrave;m hiểu kỹ th&ocirc;ng tin trước chuyến đi sẽ gi&uacute;p bạn tr&aacute;nh khỏi những chi&ecirc;u &ldquo;chặt ch&eacute;m&rdquo; kh&ocirc;ng đ&aacute;ng c&oacute;. Ảnh minh họa: Leo</p>\n</div>\n<p><strong>Thuộc số điện thoại đường d&acirc;y n&oacute;ng</strong></p>\n<p>Phần lớn du kh&aacute;ch khi bị chặt ch&eacute;m thường ngậm bồ h&ograve;n l&agrave;m ngọt, hoặc tức qu&aacute; về nh&agrave; đăng l&ecirc;n facebook xả giận bởi kh&ocirc;ng muốn đ&ocirc;i co, c&atilde;i nhau tại nơi xa lạ. Để tr&aacute;nh t&igrave;nh trạng n&agrave;y, bạn n&ecirc;n ghi lại số điện thoại đường d&acirc;y n&oacute;ng của c&aacute;c cơ quan chức năng địa phương nơi bạn tới. Họ sẽ gi&uacute;p bạn giải quyết nếu c&oacute; tranh chấp, x&ocirc; x&aacute;t, đồng thời bảo vệ an to&agrave;n cho bạn.</p>\n<div id=\"attachment_133948\" class=\"wp-caption aligncenter\"><a href=\"https://cdn3.ivivu.com/2016/04/sam-son-ivivu.jpeg\"><img class=\"wp-image-133948 size-large\" title=\"Ảnh: songtre.tv\" src=\"https://cdn3.ivivu.com/2016/04/sam-son-ivivu-1024x575.jpeg\" alt=\"Ảnh: songtre.tv\" width=\"1024\" height=\"575\"></a>\n<p class=\"wp-caption-text\">Ảnh: songtre.tv</p>\n</div>\n<div><iframe src=\"https://www.ivivu.com/hothotel/?placeslug=meo-du-lich&amp;number=1&amp;width=481\" width=\"100%\" height=\"312\" scrolling=\"no\"></iframe></div>\n<p><strong>Chọn qu&aacute;n ăn đ&ocirc;ng người địa phương</strong></p>\n<p>Trong những ng&agrave;y lễ, dịch vụ ăn uống tại một số nh&agrave; h&agrave;ng, qu&aacute;n ăn ở cụm du lịch thường c&oacute; gi&aacute; kh&aacute; cao. V&igrave; vậy, kh&ocirc;ng cần phải v&agrave;o những địa chỉ sang trọng, bạn c&oacute; thể ngồi ở những qu&aacute;n ăn b&igrave;nh d&acirc;n, vừa bớt tốn k&eacute;m vừa l&agrave; cơ hội để bạn trải nghiệm cảm gi&aacute;c ăn uống như những người d&acirc;n bản xứ. Đặc biệt, nếu muốn thưởng thức hương vị đặc sản ở nơi m&igrave;nh đến, bạn n&ecirc;n lựa chọn những qu&aacute;n ăn c&oacute; đ&ocirc;ng người d&acirc;n địa phương, cũng như qu&aacute;n được nhiều người đ&aacute;nh gi&aacute; cao với c&aacute;c m&oacute;n đặc trưng.</p>\n<div id=\"attachment_133947\" class=\"wp-caption aligncenter\"><a href=\"https://cdn3.ivivu.com/2016/04/quan-an-dong-khach-ivivu.jpg\"><img class=\"wp-image-133947 size-full\" title=\"Bạn n&ecirc;n lựa chọn những qu&aacute;n ăn c&oacute; đ&ocirc;ng người d&acirc;n địa phương, cũng như qu&aacute;n được nhiều người đ&aacute;nh gi&aacute; cao với c&aacute;c m&oacute;n đặc trưng. Ảnh: Mai Uy&ecirc;n\" src=\"https://cdn3.ivivu.com/2016/04/quan-an-dong-khach-ivivu.jpg\" alt=\"Bạn n&ecirc;n lựa chọn những qu&aacute;n ăn c&oacute; đ&ocirc;ng người d&acirc;n địa phương, cũng như qu&aacute;n được nhiều người đ&aacute;nh gi&aacute; cao với c&aacute;c m&oacute;n đặc trưng. Ảnh: Mai Uy&ecirc;n\" width=\"660\" height=\"469\"></a>\n<p class=\"wp-caption-text\">Bạn n&ecirc;n lựa chọn những qu&aacute;n ăn c&oacute; đ&ocirc;ng người d&acirc;n địa phương, cũng như qu&aacute;n được nhiều người đ&aacute;nh gi&aacute; cao với c&aacute;c m&oacute;n đặc trưng. Ảnh: Mai Uy&ecirc;n</p>\n</div>\n<p><strong>Chọn m&oacute;n tr&ecirc;n thực đơn c&oacute; gi&aacute; ni&ecirc;m yết</strong></p>\n<p>Khi đến nh&agrave; h&agrave;ng, qu&aacute;n ăn nhỏ tại c&aacute;c điểm du lịch, bạn n&ecirc;n xem thực đơn v&agrave; gi&aacute; tiền ni&ecirc;m yết cụ thể trước. Với những nơi kh&ocirc;ng ghi r&otilde; gi&aacute;, bạn n&ecirc;n chủ động hỏi nh&acirc;n vi&ecirc;n hoặc chủ cửa h&agrave;ng, tr&aacute;nh t&igrave;nh trạng gọi &iacute;t, thanh to&aacute;n nhiều. Ngo&agrave;i ra, khi gọi m&oacute;n ăn bạn cũng n&ecirc;n chọn c&aacute;c m&oacute;n ph&ugrave; hợp với nơi m&igrave;nh đến, v&iacute; dụ như nếu đi du lịch biển th&igrave; n&ecirc;n gọi c&aacute;c m&oacute;n hải sản, chứ đừng gọi c&aacute;c m&oacute;n đặc sản rừng,&hellip;</p>\n<p>Đặc biệt, c&aacute;c bạn kh&ocirc;ng n&ecirc;n qu&aacute; tin tưởng v&agrave;o h&oacute;a đơn của nh&agrave; h&agrave;ng bởi sai s&oacute;t lu&ocirc;n c&oacute; thể xảy ra. C&aacute;c chủ h&agrave;ng tại c&aacute;c điểm du lịch thường c&oacute; chi&ecirc;u tr&ograve; kh&aacute;c nhau để b&agrave;n ăn của bạn tăng th&ecirc;m v&agrave;i m&oacute;n, d&ugrave; bạn kh&ocirc;ng gọi. V&igrave; vậy, bạn n&ecirc;n kiểm tra lại h&oacute;a đơn trước khi thanh to&aacute;n.</p>\n<div id=\"attachment_133953\" class=\"wp-caption aligncenter\"><a href=\"https://cdn3.ivivu.com/2016/04/niem-yet-gia-ivivu.jpg\"><img class=\"wp-image-133953 size-full\" title=\"Khi đến nh&agrave; h&agrave;ng, qu&aacute;n ăn nhỏ tại c&aacute;c điểm du lịch, bạn n&ecirc;n xem thực đơn v&agrave; gi&aacute; tiền ni&ecirc;m yết cụ thể trước. Ảnh: catba.com.vn\" src=\"https://cdn3.ivivu.com/2016/04/niem-yet-gia-ivivu.jpg\" alt=\"Khi đến nh&agrave; h&agrave;ng, qu&aacute;n ăn nhỏ tại c&aacute;c điểm du lịch, bạn n&ecirc;n xem thực đơn v&agrave; gi&aacute; tiền ni&ecirc;m yết cụ thể trước. Ảnh: catba.com.vn\" width=\"542\" height=\"610\"></a>\n<p class=\"wp-caption-text\">Khi đến nh&agrave; h&agrave;ng, qu&aacute;n ăn nhỏ tại c&aacute;c điểm du lịch, bạn n&ecirc;n xem thực đơn v&agrave; gi&aacute; tiền ni&ecirc;m yết cụ thể trước. Ảnh: catba.com.vn</p>\n</div>\n<p><a href=\"https://www.ivivu.com/?utm_source=blog_ivivu&amp;utm_medium=post_link&amp;utm_campaign=internal\" target=\"_blank\" rel=\"noopener\"><strong>Đặt ph&ograve;ng kh&aacute;ch sạn sớm</strong></a></p>\n<p>C&aacute;c trang website thường c&oacute; ưu đ&atilde;i đối với kh&aacute;ch book ph&ograve;ng sớm. Do đ&oacute;, đặt sớm khoảng 60 ng&agrave;y rất l&yacute; tưởng để chọn được ph&ograve;ng nghỉ gi&aacute; rẻ v&agrave; chất lượng tốt. Nếu đặt qu&aacute; muộn, đặc biệt khi s&aacute;t với ng&agrave;y nghỉ lễ, bạn sẽ phải trả ph&iacute; cao hơn nhiều lần. Bạn cũng lưu &yacute; t&igrave;m hiểu thật kỹ th&ocirc;ng tin kh&aacute;ch sạn, đừng để c&aacute;c bức ảnh đẹp đ&aacute;nh lừa, thay v&agrave;o đ&oacute; đừng ngần ngại hỏi c&aacute;c nh&acirc;n vi&ecirc;n tư vấn những g&igrave; bạn c&ograve;n chưa r&otilde; về ph&ograve;ng nghỉ như: phụ thu, nước uống miễn ph&iacute; hay kh&aacute;ch sạn c&oacute; gần những địa điểm vui chơi nổi tiếng kh&ocirc;ng&hellip;</p>\n<p><a href=\"https://www.ivivu.com/blog/2014/05/nhung-meo-mac-ca-khi-di-du-lich/\" target=\"_blank\" rel=\"noopener\"><strong>Kh&ocirc;ng ngại mặc cả</strong></a></p>\n<p>Điều cần thiết kh&aacute;c khi bạn đi du lịch l&agrave; kh&ocirc;ng ngại mặc cả. Th&ocirc;ng thường c&aacute;c du kh&aacute;ch l&agrave; nam giới c&oacute; thể ngại trả gi&aacute; n&ecirc;n họ thường mua sản phẩm đắt hơn v&agrave; người b&aacute;n cũng kh&ocirc;ng tiếc ra gi&aacute; cao. C&ograve;n phụ nữ n&ecirc;n lưu &yacute; để c&oacute; thể đưa ra gi&aacute; ph&ugrave; hợp để c&oacute; thể sở hữu m&oacute;n đồ ưng &yacute;.</p>\n<p>Ở một số nơi, bạn phải trả gi&aacute; giảm 50-70% so với gi&aacute; người b&aacute;n, nhưng c&oacute; nơi chỉ cần giảm 10-20%,&hellip; Nếu bạn kh&ocirc;ng tinh &yacute; sẽ rất dễ bị mua m&oacute;n h&agrave;ng với gi&aacute; &ldquo;tr&ecirc;n trời&rdquo; v&agrave; rơi v&agrave;o cảm gi&aacute;c đang bị lừa.</p>\n<div id=\"attachment_133958\" class=\"wp-caption aligncenter\"><a href=\"https://cdn3.ivivu.com/2016/04/chieu-mac-ca-ivivu.jpg\"><img class=\"wp-image-133958 size-full\" title=\"Ảnh: ST\" src=\"https://cdn3.ivivu.com/2016/04/chieu-mac-ca-ivivu.jpg\" alt=\"Ảnh: ST\" width=\"580\" height=\"608\"></a>\n<p class=\"wp-caption-text\">Ảnh: ST</p>\n</div>\n<p><a href=\"https://www.ivivu.com/?utm_source=blog_ivivu&amp;utm_medium=post_link&amp;utm_campaign=internal\" target=\"_blank\" rel=\"noopener\"><strong>Đặt ph&ograve;ng kh&aacute;ch sạn qua c&aacute;c c&ocirc;ng ty nội địa</strong></a></p>\n<p>Đừng nghĩ rằng bạn sẽ lu&ocirc;n săn được ph&ograve;ng gi&aacute; rẻ khi đặt tr&ecirc;n những website quốc tế như agoda, booking, bởi điều n&agrave;y sẽ l&agrave; sai lầm khi bạn đặt kh&aacute;ch sạn nội địa, đặc biệt l&agrave; những chuỗi hệ thống kh&aacute;ch sạn lớn như Vinpearl, Six Senses, Victoria, Mường Thanh&hellip; Đ&oacute; l&agrave; v&igrave; c&aacute;c c&ocirc;ng ty đặt ph&ograve;ng kh&aacute;ch sạn ở Việt Nam đều c&oacute; những hợp đồng ri&ecirc;ng biệt với từng kh&aacute;ch sạn cho những mức gi&aacute; ưu đ&atilde;i đặc biệt kh&aacute;c nhau. Do đ&oacute;, đừng bất ngờ khi đặt qua website đặt kh&aacute;ch sạn trong nước, gi&aacute; c&oacute; thể rẻ hơn từ 25% &ndash; 50% so với gi&aacute; từ những nơi kh&aacute;c.</p>', '2023-11-19', 'Chỉ với một số mẹo đơn giản mà iVIVU.com giới thiệu dưới đây, chuyến du lịch của bạn sẽ giảm được những rủi ro đáng kể bởi nạn “chặt chém”.', 'http://res.cloudinary.com/dxqh3xpza/image/upload/v1700367476/v7kq2geba5ce3uycpyvy.jpg', 9, b'0', 'Mách bạn 7 mẹo hay giúp tránh ‘chặt chém’ trên đường du lịch', 1);
INSERT INTO `blog` (`id`, `content`, `created_date`, `description`, `image_banner`, `num_view`, `primary_blog`, `title`, `user_id`) VALUES
(10, '<p>TripAdvisor vừa c&ocirc;ng bố danh s&aacute;ch thời điểm l&yacute; tưởng nhất để đặt ph&ograve;ng kh&aacute;ch sạn ở 25 th&agrave;nh phố nổi tiếng tr&ecirc;n thế giới. Trong đ&oacute; nếu muốn đến thủ đ&ocirc; H&agrave; Nội du lịch th&igrave; bạn n&ecirc;n đặt ph&ograve;ng kh&aacute;ch sạn trước &iacute;t nhất 3 th&aacute;ng, việc n&agrave;y sẽ gi&uacute;p bạn tiết kiệm tiền được khoảng 16% tiền ph&ograve;ng.</p>\n<div id=\"attachment_133751\" class=\"wp-caption aligncenter\"><img class=\"wp-image-133751 size-full\" title=\"H&agrave; Nội, Việt Nam: N&ecirc;n đặt ph&ograve;ng trước chuyến đi khoảng 3 th&aacute;ng, bạn sẽ tiết kiệm được 16% tiền ph&ograve;ng kh&aacute;ch sạn. Ảnh: Shutterstock / Hanoi Photography\" src=\"https://cdn3.ivivu.com/2016/04/nen-dat-phong-khach-san-truoc-bao-lau-khi-di-du-lich-ivivu-1.jpg\" alt=\"H&agrave; Nội, Việt Nam: N&ecirc;n đặt ph&ograve;ng trước chuyến đi khoảng 3 th&aacute;ng, bạn sẽ tiết kiệm được 16% tiền ph&ograve;ng kh&aacute;ch sạn. Ảnh: Shutterstock / Hanoi Photography\" width=\"1200\" height=\"900\">\n<p class=\"wp-caption-text\"><a href=\"https://www.ivivu.com/khach-san-ha-noi?utm_source=blog_ivivu&amp;utm_medium=post_link&amp;utm_campaign=internal\" target=\"_blank\" rel=\"noopener\"><strong>H&agrave; Nội, Việt Nam:</strong>&nbsp;</a>N&ecirc;n đặt ph&ograve;ng trước chuyến đi khoảng 3 th&aacute;ng, bạn sẽ tiết kiệm được 16% tiền ph&ograve;ng kh&aacute;ch sạn. Ảnh: Shutterstock / Hanoi Photography</p>\n</div>\n<p>&nbsp;</p>\n<div class=\"ltt-contentbox white\"><strong>Tham khảo th&ecirc;m&nbsp;<a href=\"https://www.ivivu.com/du-lich/tour-ha-noi-1n-ha-noi-bai-dinh-trang-an/436?utm_source=blog_ivivu&amp;utm_medium=post_link&amp;utm_campaign=internal\" target=\"_blank\" rel=\"noopener\">Tour H&agrave; Nội 1N: H&agrave; Nội &ndash; B&aacute;i Đ&iacute;nh &ndash; Tr&agrave;ng An</a>&nbsp;với mức gi&aacute; tốt nhất thị trường</strong></div>\n<p>&nbsp;</p>\n<div id=\"attachment_133754\" class=\"wp-caption aligncenter\"><a href=\"https://cdn3.ivivu.com/2016/04/nen-dat-phong-khach-san-truoc-bao-lau-khi-di-du-lich-ivivu-2.jpg\"><img class=\"wp-image-133754 size-full\" title=\"Bangkok, Th&aacute;i Lan: Cũng giống như H&agrave; Nội, nếu bạn đến du lịch Bangkok cũng n&ecirc;n đặt ph&ograve;ng trước chuyến đi khoảng 3 th&aacute;ng. Ảnh: Shutterstock.com\" src=\"https://cdn3.ivivu.com/2016/04/nen-dat-phong-khach-san-truoc-bao-lau-khi-di-du-lich-ivivu-2.jpg\" alt=\"Bangkok, Th&aacute;i Lan: Cũng giống như H&agrave; Nội, nếu bạn đến du lịch Bangkok cũng n&ecirc;n đặt ph&ograve;ng trước chuyến đi khoảng 3 th&aacute;ng. Ảnh: Shutterstock.com\" width=\"1200\" height=\"900\"></a>\n<p class=\"wp-caption-text\"><a href=\"https://www.ivivu.com/khach-san-bangkok?utm_source=blog_ivivu&amp;utm_medium=post_link&amp;utm_campaign=internal\" target=\"_blank\" rel=\"noopener\"><strong>Bangkok, Th&aacute;i Lan:</strong></a>&nbsp;Cũng giống như H&agrave; Nội, nếu bạn đến du lịch Bangkok cũng n&ecirc;n đặt ph&ograve;ng trước chuyến đi khoảng 3 th&aacute;ng. Ảnh: Shutterstock.com</p>\n</div>\n<div><iframe src=\"https://www.ivivu.com/hothotel/?placeslug=meo-du-lich&amp;number=1&amp;width=481\" width=\"100%\" height=\"312\" scrolling=\"no\"></iframe></div>\n<p>&nbsp;</p>\n<div class=\"ltt-contentbox white\"><strong>Tham khảo th&ecirc;m&nbsp;<a href=\"https://www.ivivu.com/du-lich/tour-thai-lan-5n4d-bangkok-pattaya-vtt/524?utm_source=blog_ivivu&amp;utm_medium=post_link&amp;utm_campaign=internal\" target=\"_blank\" rel=\"noopener\">Tour Th&aacute;i Lan 5N4Đ: Bangkok &ndash; Pattaya</a>&nbsp;đang khuyến m&atilde;i giảm gi&aacute; tại iVIVU.com</strong></div>\n<p>&nbsp;</p>\n<div id=\"attachment_133755\" class=\"wp-caption aligncenter\"><a href=\"https://cdn3.ivivu.com/2016/04/nen-dat-phong-khach-san-truoc-bao-lau-khi-di-du-lich-ivivu-3.jpg\"><img class=\"wp-image-133755 size-full\" title=\"Barcelona, T&acirc;y Ban Nha: N&ecirc;n đặt ph&ograve;ng trước chuyến đi khoảng từ 3 - 6 th&aacute;ng, bạn sẽ tiết kiệm được 27%