--

CREATE TABLE `room` (
  `id` bigint NOT NULL,
  `description` longtext ,
  `image` varchar(255) DEFAULT NULL,
  `name` varchar(255) DEFAULT NULL,
  `num_bed` int DEFAULT NULL,
  `price` double DEFAULT NULL,
  `category_id` bigint DEFAULT NULL,
  `max_people` int DEFAULT NULL
) ;

--
-- Đang đổ dữ liệu cho bảng `room`
--

INSERT INTO `room` (`id`, `description`, `image`, `name`, `num_bed`, `price`, `category_id`, `max_people`) VALUES
(1, '<p><strong>Vị tr&iacute;</strong><br>Millennium Hanoi Hotel l&agrave; một kh&aacute;ch sạn nằm trong khu vực an ninh, toạ lạc tại Cửa Nam.</p>\n<p>kh&aacute;ch sạn nằm c&aacute;ch Hanoi Train Street 0,25 km.</p>\n<p>Kh&ocirc;ng chỉ sở hữu vị tr&iacute; đắc địa, Millennium Hanoi Hotel c&ograve;n l&agrave; một trong những kh&aacute;ch sạn nằm c&aacute;ch Đ&igrave;nh L&agrave;ng Nghĩa Lộ chưa đầy 33,12 km v&agrave; Nh&agrave; thờ Thường Lệ chưa đầy 19,92 km.</p>\n<p><strong>Th&ocirc;ng tin về Millennium Hanoi Hotel</strong></p>\n<p>Kh&ocirc;ng chỉ sở hữu vị tr&iacute; gi&uacute;p qu&yacute; kh&aacute;ch dễ d&agrave;ng gh&eacute; thăm những địa điểm l&yacute; th&uacute; trong chuyến h&agrave;nh tr&igrave;nh, Millennium Hanoi Hotel cũng sẽ mang đến cho qu&yacute; kh&aacute;ch trải nghiệm lưu tr&uacute; mỹ m&atilde;n.</p>\n<p>Millennium Hanoi Hotel l&agrave; đề xuất h&agrave;ng đầu d&agrave;nh cho những t&iacute;n đồ du lịch \"bụi\" mong muốn được nghỉ tại một kh&aacute;ch sạn vừa thoải m&aacute;i lại hợp t&uacute;i tiền.</p>\n<p>D&agrave;nh cho những du kh&aacute;ch muốn du lịch thoải m&aacute;i c&ugrave;ng ng&acirc;n s&aacute;ch tiết kiệm, Millennium Hanoi Hotel sẽ l&agrave; lựa chọn lưu tr&uacute; ho&agrave;n hảo, nơi cung cấp c&aacute;c tiện nghi chất lượng v&agrave; dịch vụ tuyệt vời.</p>\n<p>Kh&aacute;ch sạn n&agrave;y l&agrave; lựa chọn ho&agrave;n hảo cho c&aacute;c kỳ nghỉ m&aacute;t l&atilde;ng mạn hay tuần trăng mật của c&aacute;c cặp đ&ocirc;i. Qu&yacute; kh&aacute;ch h&atilde;y tận hưởng những đ&ecirc;m đ&aacute;ng nhớ nhất c&ugrave;ng người thương của m&igrave;nh tại Millennium Hanoi Hotel</p>\n<p>Millennium Hanoi Hotel l&agrave; lựa chọn s&aacute;ng gi&aacute; d&agrave;nh cho những ai đang t&igrave;m kiếm một trải nghiệm xa hoa đầy th&uacute; vị trong kỳ nghỉ của m&igrave;nh. Lưu tr&uacute; tại đ&acirc;y cũng l&agrave; c&aacute;ch để qu&yacute; kh&aacute;ch chiều chuộng bản th&acirc;n với những dịch vụ xuất sắc nhất v&agrave; khiến kỳ nghỉ của m&igrave;nh trở n&ecirc;n thật đ&aacute;ng nhớ.</p>\n<p>Bạn c&oacute; phải l&agrave; t&iacute;n đồ mua sắm? Lưu tr&uacute; tại Millennium Hanoi Hotel chắc chắn sẽ thoả m&atilde;n bạn với h&agrave;ng loạt c&aacute;c trung t&acirc;m mua sắm kề cận.</p>\n<p>Khi lưu tr&uacute; tại kh&aacute;ch sạn th&igrave; nội thất v&agrave; kiến tr&uacute;c hẳn l&agrave; hai yếu tố quan trọng khiến qu&yacute; kh&aacute;ch m&atilde;n nh&atilde;n. Với thiết kế độc đ&aacute;o, Millennium Hanoi Hotel mang đến kh&ocirc;ng gian lưu tr&uacute; l&agrave;m h&agrave;i l&ograve;ng qu&yacute; kh&aacute;ch.</p>\n<p>Từ sự kiện doanh nghiệp đến họp mặt c&ocirc;ng ty, Millennium Hanoi Hotel cung cấp đầy đủ c&aacute;c dịch vụ v&agrave; tiện nghi đ&aacute;p ứng mọi nhu cầu của qu&yacute; kh&aacute;ch v&agrave; đồng nghiệp.</p>\n<p>H&atilde;y tận hưởng thời gian vui vẻ c&ugrave;ng cả gia đ&igrave;nh với h&agrave;ng loạt tiện nghi giải tr&iacute; tại Millennium Hanoi Hotel, một kh&aacute;ch sạn tuyệt vời ph&ugrave; hợp cho mọi kỳ nghỉ b&ecirc;n người th&acirc;n.</p>\n<p>H&atilde;y tận hưởng trải nghiệm lưu tr&uacute; c&oacute; một kh&ocirc;ng hai tại to&agrave; nh&agrave; mang đậm dấu ấn lịch sử của Millennium Hanoi Hotel, điều qu&yacute; kh&aacute;ch kh&oacute; c&oacute; thể t&igrave;m thấy tại bất kỳ đ&acirc;u.</p>\n<p>Nếu dự định c&oacute; một kỳ nghỉ d&agrave;i, th&igrave; Millennium Hanoi Hotel ch&iacute;nh l&agrave; lựa chọn d&agrave;nh cho qu&yacute; kh&aacute;ch. Với đầy đủ tiện nghi với chất lượng dịch vụ tuyệt vời, Millennium Hanoi Hotel sẽ khiến qu&yacute; kh&aacute;ch cảm thấy thoải m&aacute;i như đang ở nh&agrave; vậy.</p>\n<p>Du lịch một m&igrave;nh cũng kh&ocirc;ng hề k&eacute;m phần th&uacute; vị v&agrave; Millennium Hanoi Hotel l&agrave; nơi th&iacute;ch hợp d&agrave;nh ri&ecirc;ng cho những ai đề cao sự ri&ecirc;ng tư trong kỳ lưu tr&uacute;.</p>\n<p>Dịch vụ tuyệt vời, cơ sở vật chất ho&agrave;n chỉnh v&agrave; c&aacute;c tiện nghi kh&aacute;ch sạn cung cấp sẽ khiến qu&yacute; kh&aacute;ch kh&ocirc;ng thể ph&agrave;n n&agrave;n trong suốt kỳ lưu tr&uacute; tại Millennium Hanoi Hotel.</p>\n<p>Quầy tiếp t&acirc;n 24 giờ lu&ocirc;n sẵn s&agrave;ng phục vụ qu&yacute; kh&aacute;ch từ thủ tục nhận ph&ograve;ng đến trả ph&ograve;ng hay bất kỳ y&ecirc;u cầu n&agrave;o. Nếu cần gi&uacute;p đỡ xin h&atilde;y li&ecirc;n hệ đội ngũ tiếp t&acirc;n, ch&uacute;ng t&ocirc;i lu&ocirc;n sẵn s&agrave;ng hỗ trợ qu&yacute; kh&aacute;ch.</p>\n<p>Tận hưởng những m&oacute;n ăn y&ecirc;u th&iacute;ch với phong c&aacute;ch ẩm thực đặc biệt từ Millennium Hanoi Hotel chỉ d&agrave;nh ri&ecirc;ng cho qu&yacute; kh&aacute;ch.</p>\n<p>S&oacute;ng WiFi phủ khắp c&aacute;c khu vực chung của kh&aacute;ch sạn cho ph&eacute;p qu&yacute; kh&aacute;ch lu&ocirc;n kết nối với gia đ&igrave;nh v&agrave; b&egrave; bạn.</p>\n<p>Millennium Hanoi Hotel l&agrave; kh&aacute;ch sạn sở hữu đầy đủ tiện nghi v&agrave; dịch vụ xuất sắc theo nhận định của hầu hết kh&aacute;ch lưu tr&uacute;.</p>\n<p>Với những tiện nghi sẵn c&oacute; Millennium Hanoi Hotel thực sự l&agrave; một nơi lưu tr&uacute; ho&agrave;n hảo.</p>', 'http://res.cloudinary.com/dxqh3xpza/image/upload/v1700453379/yambl4qb5nodmri4cywc.webp', 'phòng Millennium 101', 2, 250000, 4, 4),
(2, '<p><strong>Vị tr&iacute;</strong><br>Muong Thanh Hanoi Centre Hotel l&agrave; một kh&aacute;ch sạn nằm trong khu vực an ninh, toạ lạc tại Trần Hưng Đạo.</p>\n<p>kh&aacute;ch sạn nằm c&aacute;ch Hanoi Railway Station 0,69 km.</p>\n<p>Kh&ocirc;ng chỉ sở hữu vị tr&iacute; đắc địa, Muong Thanh Hanoi Centre Hotel c&ograve;n l&agrave; một trong những kh&aacute;ch sạn nằm c&aacute;ch Hồ L&aacute;c chưa đầy 49,77 km v&agrave; Khu Sinh Th&aacute;i Thi&ecirc;n Ph&uacute; L&acirc;m chưa đầy 30,16 km.</p>\n<p><strong>Th&ocirc;ng tin về Muong Thanh Hanoi Centre Hotel</strong></p>\n<p>Muong Thanh Hanoi Centre Hotel l&agrave; đề xuất h&agrave;ng đầu d&agrave;nh cho những t&iacute;n đồ du lịch \"bụi\" mong muốn được nghỉ tại một kh&aacute;ch sạn vừa thoải m&aacute;i lại hợp t&uacute;i tiền.</p>\n<p>D&ugrave; qu&yacute; kh&aacute;ch muốn tổ chức một sự kiện hay c&aacute;c dịp kỷ niệm đặc biệt kh&aacute;c, Muong Thanh Hanoi Centre Hotel l&agrave; lựa chọn tuyệt vời cho qu&yacute; kh&aacute;ch với ph&ograve;ng chức năng rộng lớn, được trang bị đầy đủ để sẵn s&agrave;ng đ&aacute;p ứng mọi y&ecirc;u cầu.</p>\n<p>Kh&aacute;ch sạn n&agrave;y l&agrave; lựa chọn ho&agrave;n hảo cho c&aacute;c kỳ nghỉ m&aacute;t l&atilde;ng mạn hay tuần trăng mật của c&aacute;c cặp đ&ocirc;i. Qu&yacute; kh&aacute;ch h&atilde;y tận hưởng những đ&ecirc;m đ&aacute;ng nhớ nhất c&ugrave;ng người thương của m&igrave;nh tại Muong Thanh Hanoi Centre Hotel</p>\n<p>Muong Thanh Hanoi Centre Hotel l&agrave; lựa chọn s&aacute;ng gi&aacute; d&agrave;nh cho những ai đang t&igrave;m kiếm một trải nghiệm xa hoa đầy th&uacute; vị trong kỳ nghỉ của m&igrave;nh. Lưu tr&uacute; tại đ&acirc;y cũng l&agrave; c&aacute;ch để qu&yacute; kh&aacute;ch chiều chuộng bản th&acirc;n với những dịch vụ xuất sắc nhất v&agrave; khiến kỳ nghỉ của m&igrave;nh trở n&ecirc;n thật đ&aacute;ng nhớ.</p>\n<p>Bạn c&oacute; phải l&agrave; t&iacute;n đồ mua sắm? Lưu tr&uacute; tại Muong Thanh Hanoi Centre Hotel chắc chắn sẽ thoả m&atilde;n bạn với h&agrave;ng loạt c&aacute;c trung t&acirc;m mua sắm kề cận.</p>\n<p>Một trong những đặc điểm ch&iacute;nh của kh&aacute;ch sạn n&agrave;y l&agrave; c&aacute;c liệu ph&aacute;p spa đa dạng. H&atilde;y n&acirc;ng niu bản th&acirc;n bằng c&aacute;c liệu ph&aacute;p thư gi&atilde;n, phục hồi gi&uacute;p qu&yacute; kh&aacute;ch tươi trẻ th&acirc;n, t&acirc;m.</p>\n<p>Khi lưu tr&uacute; tại kh&aacute;ch sạn th&igrave; nội thất v&agrave; kiến tr&uacute;c hẳn l&agrave; hai yếu tố quan trọng khiến qu&yacute; kh&aacute;ch m&atilde;n nh&atilde;n. Với thiết kế độc đ&aacute;o, Muong Thanh Hanoi Centre Hotel mang đến kh&ocirc;ng gian lưu tr&uacute; l&agrave;m h&agrave;i l&ograve;ng qu&yacute; kh&aacute;ch.</p>\n<p>Từ sự kiện doanh nghiệp đến họp mặt c&ocirc;ng ty, Muong Thanh Hanoi Centre Hotel cung cấp đầy đủ c&aacute;c dịch vụ v&agrave; tiện nghi đ&aacute;p ứng mọi nhu cầu của qu&yacute; kh&aacute;ch v&agrave; đồng nghiệp.</p>\n<p>H&atilde;y tận hưởng thời gian vui vẻ c&ugrave;ng cả gia đ&igrave;nh với h&agrave;ng loạt tiện nghi giải tr&iacute; tại Muong Thanh Hanoi Centre Hotel, một kh&aacute;ch sạn tuyệt vời ph&ugrave; hợp cho mọi kỳ nghỉ b&ecirc;n người th&acirc;n.</p>\n<p>Nếu dự định c&oacute; một kỳ nghỉ d&agrave;i, th&igrave; Muong Thanh Hanoi Centre Hotel ch&iacute;nh l&agrave; lựa chọn d&agrave;nh cho qu&yacute; kh&aacute;ch. Với đầy đủ tiện nghi với chất lượng dịch vụ tuyệt vời, Muong Thanh Hanoi Centre Hotel sẽ khiến qu&yacute; kh&aacute;ch cảm thấy thoải m&aacute;i như đang ở nh&agrave; vậy.</p>\n<p>Du lịch một m&igrave;nh cũng kh&ocirc;ng hề k&eacute;m phần th&uacute; vị v&agrave; Muong Thanh Hanoi Centre Hotel l&agrave; nơi th&iacute;ch hợp d&agrave;nh ri&ecirc;ng cho những ai đề cao sự ri&ecirc;ng tư trong kỳ lưu tr&uacute;.</p>\n<p>H&atilde;y sẵn s&agrave;ng đ&oacute;n nhận trải nghiệm kh&oacute; qu&ecirc;n bằng dịch vụ độc đ&aacute;o v&agrave; ho&agrave;n hảo của kh&aacute;ch sạn c&ugrave;ng c&aacute;c tiện nghi đầy đủ, đ&aacute;p ứng mọi nhu cầu của qu&yacute; kh&aacute;ch.</p>\n<p>Nhận ưu đ&atilde;i đặc biệt d&agrave;nh cho c&aacute;c liệu ph&aacute;p spa tinh tu&yacute; nhất gi&uacute;p thư gi&atilde;n tinh thần v&agrave; l&agrave;m tươi trẻ cơ thể.</p>\n<p>Quầy tiếp t&acirc;n 24 giờ lu&ocirc;n sẵn s&agrave;ng phục vụ qu&yacute; kh&aacute;ch từ thủ tục nhận ph&ograve;ng đến trả ph&ograve;ng hay bất kỳ y&ecirc;u cầu n&agrave;o. Nếu cần gi&uacute;p đỡ xin h&atilde;y li&ecirc;n hệ đội ngũ tiếp t&acirc;n, ch&uacute;ng t&ocirc;i lu&ocirc;n sẵn s&agrave;ng hỗ trợ qu&yacute; kh&aacute;ch.</p>\n<p>Tận hưởng những m&oacute;n ăn y&ecirc;u th&iacute;ch với phong c&aacute;ch ẩm thực đặc biệt từ Muong Thanh Hanoi Centre Hotel chỉ d&agrave;nh ri&ecirc;ng cho qu&yacute; kh&aacute;ch.</p>\n<p>S&oacute;ng WiFi phủ khắp c&aacute;c khu vực chung của kh&aacute;ch sạn cho ph&eacute;p qu&yacute; kh&aacute;ch lu&ocirc;n kết nối với gia đ&igrave;nh v&agrave; b&egrave; bạn.</p>\n<p>Muong Thanh Hanoi Centre Hotel l&agrave; kh&aacute;ch sạn sở hữu đầy đủ tiện nghi v&agrave; dịch vụ xuất sắc theo nhận định của hầu hết kh&aacute;ch lưu tr&uacute;.</p>\n<p>H&atilde;y sẵn s&agrave;ng đ&oacute;n nhận những gi&acirc;y ph&uacute;t v&ocirc; gi&aacute; kh&oacute; phai trong suốt kỳ nghỉ của qu&yacute; kh&aacute;ch tại Muong Thanh Hanoi Centre Hotel.</p>', 'http://res.cloudinary.com/dxqh3xpza/image/upload/v1700453576/nboh7ytz0liuuxv6xoij.webp', 'Phòng Medium 102', 1, 250000, 4, 2),
(3, '<p><strong>Vị tr&iacute;</strong><br>Hanoi De Maison Grand Hotel l&agrave; một kh&aacute;ch sạn nằm trong khu vực an ninh, toạ lạc tại H&agrave;ng B&ocirc;ng.</p>\n<p>kh&aacute;ch sạn nằm c&aacute;ch Hanoi Railway Station 0,81 km.</p>\n<p>Kh&ocirc;ng chỉ sở hữu vị tr&iacute; đắc địa, Hanoi De Maison Grand Hotel c&ograve;n l&agrave; một trong những kh&aacute;ch sạn nằm c&aacute;ch Hoang Quoc Vietnam Park chưa đầy 30,1 km v&agrave; Bac Ninh Museum chưa đầy 29,27 km.</p>\n<p><strong>Th&ocirc;ng tin về Hanoi De Maison Grand Hotel</strong></p>\n<p>D&agrave;nh cho những du kh&aacute;ch muốn du lịch thoải m&aacute;i c&ugrave;ng ng&acirc;n s&aacute;ch tiết kiệm, Hanoi De Maison Grand Hotel sẽ l&agrave; lựa chọn lưu tr&uacute; ho&agrave;n hảo, nơi cung cấp c&aacute;c tiện nghi chất lượng v&agrave; dịch vụ tuyệt vời.</p>\n<p>Kh&aacute;ch sạn n&agrave;y l&agrave; lựa chọn ho&agrave;n hảo cho c&aacute;c kỳ nghỉ m&aacute;t l&atilde;ng mạn hay tuần trăng mật của c&aacute;c cặp đ&ocirc;i. Qu&yacute; kh&aacute;ch h&atilde;y tận hưởng những đ&ecirc;m đ&aacute;ng nhớ nhất c&ugrave;ng người thương của m&igrave;nh tại Hanoi De Maison Grand Hotel</p>\n<p>Hanoi De Maison Grand Hotel l&agrave; lựa chọn s&aacute;ng gi&aacute; d&agrave;nh cho những ai đang t&igrave;m kiếm một trải nghiệm xa hoa đầy th&uacute; vị trong kỳ nghỉ của m&igrave;nh. Lưu tr&uacute; tại đ&acirc;y cũng l&agrave; c&aacute;ch để qu&yacute; kh&aacute;ch chiều chuộng bản th&acirc;n với những dịch vụ xuất sắc nhất v&agrave; khiến kỳ nghỉ của m&igrave;nh trở n&ecirc;n thật đ&aacute;ng nhớ.</p>\n<p>Bạn c&oacute; phải l&agrave; t&iacute;n đồ mua sắm? Lưu tr&uacute; tại Hanoi De Maison Grand Hotel chắc chắn sẽ thoả m&atilde;n bạn với h&agrave;ng loạt c&aacute;c trung t&acirc;m mua sắm kề cận.</p>\n<p>Khi lưu tr&uacute; tại kh&aacute;ch sạn th&igrave; nội thất v&agrave; kiến tr&uacute;c hẳn l&agrave; hai yếu tố quan trọng khiến qu&yacute; kh&aacute;ch m&atilde;n nh&atilde;n. Với thiết kế độc đ&aacute;o, Hanoi De Maison Grand Hotel mang đến kh&ocirc;ng gian lưu tr&uacute; l&agrave;m h&agrave;i l&ograve;ng qu&yacute; kh&aacute;ch.</p>\n<p>Từ sự kiện doanh nghiệp đến họp mặt c&ocirc;ng ty, Hanoi De Maison Grand Hotel cung cấp đầy đủ c&aacute;c dịch vụ v&agrave; tiện nghi đ&aacute;p ứng mọi nhu cầu của qu&yacute; kh&aacute;ch v&agrave; đồng nghiệp.</p>\n<p>H&atilde;y tận hưởng thời gian vui vẻ c&ugrave;ng cả gia đ&igrave;nh với h&agrave;ng loạt tiện nghi giải tr&iacute; tại Hanoi De Maison Grand Hotel, một kh&aacute;ch sạn tuyệt vời ph&ugrave; hợp cho mọi kỳ nghỉ b&ecirc;n người th&acirc;n.</p>\n<p>Nếu dự định c&oacute; một kỳ nghỉ d&agrave;i, th&igrave; Hanoi De Maison Grand Hotel ch&iacute;nh l&agrave; lựa chọn d&agrave;nh cho qu&yacute; kh&aacute;ch. Với đầy đủ tiện nghi với chất lượng dịch vụ tuyệt vời, Hanoi De Maison Grand Hotel sẽ khiến qu&yacute; kh&aacute;ch cảm thấy thoải m&aacute;i như đang ở nh&agrave; vậy.</p>\n<p>Du lịch một m&igrave;nh cũng kh&ocirc;ng hề k&eacute;m phần th&uacute; vị v&agrave; Hanoi De Maison Grand Hotel l&agrave; nơi th&iacute;ch hợp d&agrave;nh ri&ecirc;ng cho những ai đề cao sự ri&ecirc;ng tư trong kỳ lưu tr&uacute;.</p>\n<p>Dịch vụ tuyệt vời, cơ sở vật chất ho&agrave;n chỉnh v&agrave; c&aacute;c tiện nghi kh&aacute;ch sạn cung cấp sẽ khiến qu&yacute; kh&aacute;ch kh&ocirc;ng thể ph&agrave;n n&agrave;n trong suốt kỳ lưu tr&uacute; tại Hanoi De Maison Grand Hotel.</p>\n<p>Quầy tiếp t&acirc;n 24 giờ lu&ocirc;n sẵn s&agrave;ng phục vụ qu&yacute; kh&aacute;ch từ thủ tục nhận ph&ograve;ng đến trả ph&ograve;ng hay bất kỳ y&ecirc;u cầu n&agrave;o. Nếu cần gi&uacute;p đỡ xin h&atilde;y li&ecirc;n hệ đội ngũ tiếp t&acirc;n, ch&uacute;ng t&ocirc;i lu&ocirc;n sẵn s&agrave;ng hỗ trợ qu&yacute; kh&aacute;ch.</p>\n<p>Tận hưởng những m&oacute;n ăn y&ecirc;u th&iacute;ch với phong c&aacute;ch ẩm thực đặc biệt từ Hanoi De Maison Grand Hotel chỉ d&agrave;nh ri&ecirc;ng cho qu&yacute; kh&aacute;ch.</p>\n<p>S&oacute;ng WiFi phủ khắp c&aacute;c khu vực chung của kh&aacute;ch sạn cho ph&eacute;p qu&yacute; kh&aacute;ch lu&ocirc;n kết nối với gia đ&igrave;nh v&agrave; b&egrave; bạn.</p>\n<p>Hanoi De Maison Grand Hotel l&agrave; kh&aacute;ch sạn sở hữu đầy đủ tiện nghi v&agrave; dịch vụ xuất sắc theo nhận định của hầu hết kh&aacute;ch lưu tr&uacute;.</p>\n<p>Với những tiện nghi sẵn c&oacute; Hanoi De Maison Grand Hotel thực sự l&agrave; một nơi lưu tr&uacute; ho&agrave;n hảo.</p>', 'http://res.cloudinary.com/dxqh3xpza/image/upload/v1700453723/ob5nobvwj5eeifvfyyeb.webp', 'Phòng 103 VIP ', 3, 700000, 5, 6),
(4, '<p><strong>Vị tr&iacute;</strong><br>The Herriott Hotel &amp; Suite toạ lạc tại khu vực / th&agrave;nh phố An Hải Bắc.</p>\n<p>kh&aacute;ch sạn sở hữu vị tr&iacute; đắc địa c&aacute;ch s&acirc;n bay S&acirc;n bay quốc tế Đ&agrave; Nẵng (DAD) 5,24 km.</p>\n<p>C&oacute; rất nhiều điểm tham quan l&acirc;n cận như C&agrave; ph&ecirc; Mỹ Hạnh ở khoảng c&aacute;ch 45,57 km, v&agrave; N&uacute;i Bạch M&atilde; ở khoảng c&aacute;ch 44,56 km.</p>\n<p><strong>Th&ocirc;ng tin về The Herriott Hotel &amp; Suite</strong></p>\n<p>Kh&aacute;ch sạn n&agrave;y l&agrave; lựa chọn ho&agrave;n hảo cho c&aacute;c kỳ nghỉ m&aacute;t l&atilde;ng mạn hay tuần trăng mật của c&aacute;c cặp đ&ocirc;i. Qu&yacute; kh&aacute;ch h&atilde;y tận hưởng những đ&ecirc;m đ&aacute;ng nhớ nhất c&ugrave;ng người thương của m&igrave;nh tại The Herriott Hotel &amp; Suite</p>\n<p>Một trong những đặc điểm ch&iacute;nh của kh&aacute;ch sạn n&agrave;y l&agrave; c&aacute;c liệu ph&aacute;p spa đa dạng. H&atilde;y n&acirc;ng niu bản th&acirc;n bằng c&aacute;c liệu ph&aacute;p thư gi&atilde;n, phục hồi gi&uacute;p qu&yacute; kh&aacute;ch tươi trẻ th&acirc;n, t&acirc;m.</p>\n<p>Từ sự kiện doanh nghiệp đến họp mặt c&ocirc;ng ty, The Herriott Hotel &amp; Suite cung cấp đầy đủ c&aacute;c dịch vụ v&agrave; tiện nghi đ&aacute;p ứng mọi nhu cầu của qu&yacute; kh&aacute;ch v&agrave; đồng nghiệp.</p>\n<p>H&atilde;y tận hưởng thời gian vui vẻ c&ugrave;ng cả gia đ&igrave;nh với h&agrave;ng loạt tiện nghi giải tr&iacute; tại The Herriott Hotel &amp; Suite, một kh&aacute;ch sạn tuyệt vời ph&ugrave; hợp cho mọi kỳ nghỉ b&ecirc;n người th&acirc;n.</p>\n<p>Nếu dự định c&oacute; một kỳ nghỉ d&agrave;i, th&igrave; The Herriott Hotel &amp; Suite ch&iacute;nh l&agrave; lựa chọn d&agrave;nh cho qu&yacute; kh&aacute;ch. Với đầy đủ tiện nghi với chất lượng dịch vụ tuyệt vời, The Herriott Hotel &amp; Suite sẽ khiến qu&yacute; kh&aacute;ch cảm thấy thoải m&aacute;i như đang ở nh&agrave; vậy.</p>\n<p>Dịch vụ tuyệt vời, cơ sở vật chất ho&agrave;n chỉnh v&agrave; c&aacute;c tiện nghi kh&aacute;ch sạn cung cấp sẽ khiến qu&yacute; kh&aacute;ch kh&ocirc;ng thể ph&agrave;n n&agrave;n trong suốt kỳ lưu tr&uacute; tại The Herriott Hotel &amp; Suite.</p>\n<p>Hưởng thụ một ng&agrave;y thư th&aacute;i đầy th&uacute; vị tại hồ bơi d&ugrave; qu&yacute; kh&aacute;ch đang du lịch một m&igrave;nh hay c&ugrave;ng người th&acirc;n.</p>\n<p>Quầy tiếp t&acirc;n 24 giờ lu&ocirc;n sẵn s&agrave;ng phục vụ qu&yacute; kh&aacute;ch từ thủ tục nhận ph&ograve;ng đến trả ph&ograve;ng hay bất kỳ y&ecirc;u cầu n&agrave;o. Nếu cần gi&uacute;p đỡ xin h&atilde;y li&ecirc;n hệ đội ngũ tiếp t&acirc;n, ch&uacute;ng t&ocirc;i lu&ocirc;n sẵn s&agrave;ng hỗ trợ qu&yacute; kh&aacute;ch.</p>\n<p>Tận hưởng những m&oacute;n ăn y&ecirc;u th&iacute;ch với phong c&aacute;ch ẩm thực đặc biệt từ The Herriott Hotel &amp; Suite chỉ d&agrave;nh ri&ecirc;ng cho qu&yacute; kh&aacute;ch.</p>\n<p>S&oacute;ng WiFi phủ khắp c&aacute;c khu vực chung của kh&aacute;ch sạn cho ph&eacute;p qu&yacute; kh&aacute;ch lu&ocirc;n kết nối với gia đ&igrave;nh v&agrave; b&egrave; bạn.</p>\n<p>The Herriott Hotel &amp; Suite l&agrave; kh&aacute;ch sạn sở hữu đầy đủ tiện nghi v&agrave; dịch vụ xuất sắc theo nhận định của hầu hết kh&aacute;ch lưu tr&uacute;.</p>\n<p>Với những tiện nghi sẵn c&oacute; The Herriott Hotel &amp; Suite thực sự l&agrave; một nơi lưu tr&uacute; ho&agrave;n hảo.</p>', 'http://res.cloudinary.com/dxqh3xpza/image/upload/v1700453897/uc4sgliq1hzhqfbixfhc.webp', 'Phòng Cheap 104', 1, 170000, 2, 2),
(5, '<p>&nbsp;</p>\n<h3 class=\"css-4rbku5 css-901oao r-cwxd7f r-t1w4ow r-ubezar r-b88u0q r-rjixqe r-fdjqy7\" dir=\"auto\" role=\"heading\" aria-level=\"3\">Vị tr&iacute;</h3>\n<p><br>Zalo Sea Hotel l&agrave; một kh&aacute;ch sạn nằm trong khu vực an ninh, toạ lạc tại Phước Mỹ.</p>\n<p>&nbsp;</p>\n<p>kh&aacute;ch sạn sở hữu vị tr&iacute; đắc địa c&aacute;ch s&acirc;n bay S&acirc;n bay quốc tế Đ&agrave; Nẵng (DAD) 5,23 km.</p>\n<p>Kh&ocirc;ng chỉ sở hữu vị tr&iacute; đắc địa, Zalo Sea Hotel c&ograve;n l&agrave; một trong những kh&aacute;ch sạn nằm c&aacute;ch C&agrave; ph&ecirc; Mỹ Hạnh chưa đầy 45,24 km v&agrave; N&uacute;i Bạch M&atilde; chưa đầy 45,69 km.</p>\n<p>&nbsp;</p>\n<h3 class=\"css-4rbku5 css-901oao r-cwxd7f r-t1w4ow r-ubezar r-b88u0q r-rjixqe r-fdjqy7\" dir=\"auto\" role=\"heading\" aria-level=\"3\">Th&ocirc;ng tin về Zalo Sea Hotel</h3>\n<p>&nbsp;</p>\n<p>Zalo Sea Hotel l&agrave; đề xuất h&agrave;ng đầu d&agrave;nh cho những t&iacute;n đồ du lịch \"bụi\" mong muốn được nghỉ tại một kh&aacute;ch sạn vừa thoải m&aacute;i lại hợp t&uacute;i tiền.</p>\n<p>D&agrave;nh cho những du kh&aacute;ch muốn du lịch thoải m&aacute;i c&ugrave;ng ng&acirc;n s&aacute;ch tiết kiệm, Zalo Sea Hotel sẽ l&agrave; lựa chọn lưu tr&uacute; ho&agrave;n hảo, nơi cung cấp c&aacute;c tiện nghi chất lượng v&agrave; dịch vụ tuyệt vời.</p>\n<p>H&atilde;y tận hưởng thời gian vui vẻ c&ugrave;ng cả gia đ&igrave;nh với h&agrave;ng loạt tiện nghi giải tr&iacute; tại Zalo Sea Hotel, một kh&aacute;ch sạn tuyệt vời ph&ugrave; hợp cho mọi kỳ nghỉ b&ecirc;n người th&acirc;n.</p>\n<p>Nếu dự định c&oacute; một kỳ nghỉ d&agrave;i, th&igrave; Zalo Sea Hotel ch&iacute;nh l&agrave; lựa chọn d&agrave;nh cho qu&yacute; kh&aacute;ch. Với đầy đủ tiện nghi với chất lượng dịch vụ tuyệt vời, Zalo Sea Hotel sẽ khiến qu&yacute; kh&aacute;ch cảm thấy thoải m&aacute;i như đang ở nh&agrave; vậy.</p>\n<p>Du lịch một m&igrave;nh cũng kh&ocirc;ng hề k&eacute;m phần th&uacute; vị v&agrave; Zalo Sea Hotel l&agrave; nơi th&iacute;ch hợp d&agrave;nh ri&ecirc;ng cho những ai đề cao sự ri&ecirc;ng tư trong kỳ lưu tr&uacute;.</p>\n<p>&nbsp;</p>\n<p>Quầy tiếp t&acirc;n 24 giờ lu&ocirc;n sẵn s&agrave;ng phục vụ qu&yacute; kh&aacute;ch từ thủ tục nhận ph&ograve;ng đến trả ph&ograve;ng hay bất kỳ y&ecirc;u cầu n&agrave;o. Nếu cần gi&uacute;p đỡ xin h&atilde;y li&ecirc;n hệ đội ngũ tiếp t&acirc;n, ch&uacute;ng t&ocirc;i lu&ocirc;n sẵn s&agrave;ng hỗ trợ qu&yacute; kh&aacute;ch.</p>\n<p>S&oacute;ng WiFi phủ khắp c&aacute;c khu vực chung của kh&aacute;ch sạn cho ph&eacute;p qu&yacute; kh&aacute;ch lu&ocirc;n kết nối với gia đ&igrave;nh v&agrave; b&egrave; bạn.</p>\n<p>Zalo Sea Hotel l&agrave; kh&aacute;ch sạn sở hữu đầy đủ tiện nghi v&agrave; dịch vụ xuất sắc theo nhận định của hầu hết kh&aacute;ch lưu tr&uacute;.</p>\n<p>Với những tiện nghi sẵn c&oacute; Zalo Sea Hotel thực sự l&agrave; một nơi lưu tr&uacute; ho&agrave;n hảo.</p>', 'http://res.cloudinary.com/dxqh3xpza/image/upload/v1700454194/dfnn9uzmejyd3bpetfxd.webp', 'Phòng VIP 201', 3, 800000, 5, 6),
(6, '<p><strong>Vị tr&iacute;</strong><br>Millennium Hanoi Hotel l&agrave; một kh&aacute;ch sạn nằm trong khu vực an ninh, toạ lạc tại Cửa Nam.</p>\n<p>kh&aacute;ch sạn nằm c&aacute;ch Hanoi Train Street 0,25 km.</p>\n<p>Kh&ocirc;ng chỉ sở hữu vị tr&iacute; đắc địa, Millennium Hanoi Hotel c&ograve;n l&agrave; một trong những kh&aacute;ch sạn nằm c&aacute;ch Đ&igrave;nh L&agrave;ng Nghĩa Lộ chưa đầy 33,12 km v&agrave; Nh&agrave; thờ Thường Lệ chưa đầy 19,92 km.</p>\n<p><strong>Th&ocirc;ng tin về Millennium Hanoi Hotel</strong></p>\n<p>Kh&ocirc;ng chỉ sở hữu vị tr&iacute; gi&uacute;p qu&yacute; kh&aacute;ch dễ d&agrave;ng gh&eacute; thăm những địa điểm l&yacute; th&uacute; trong chuyến h&agrave;nh tr&igrave;nh, Millennium Hanoi Hotel cũng sẽ mang đến cho qu&yacute; kh&aacute;ch trải nghiệm lưu tr&uacute; mỹ m&atilde;n.</p>\n<p>Millennium Hanoi Hotel l&agrave; đề xuất h&agrave;ng đầu d&agrave;nh cho những t&iacute;n đồ du lịch \"bụi\" mong muốn được nghỉ tại một kh&aacute;ch sạn vừa thoải m&aacute;i lại hợp t&uacute;i tiền.</p>\n<p>D&agrave;nh cho những du kh&aacute;ch muốn du lịch thoải m&aacute;i c&ugrave;ng ng&acirc;n s&aacute;ch tiết kiệm, Millennium Hanoi Hotel sẽ l&agrave; lựa chọn lưu tr&uacute; ho&agrave;n hảo, nơi cung cấp c&aacute;c tiện nghi chất lượng v&agrave; dịch vụ tuyệt vời.</p>\n<p>Kh&aacute;ch sạn n&agrave;y l&agrave; lựa chọn ho&agrave;n hảo cho c&aacute;c kỳ nghỉ m&aacute;t l&atilde;ng mạn hay tuần trăng mật của c&aacute;c cặp đ&ocirc;i. Qu&yacute; kh&aacute;ch h&atilde;y tận hưởng những đ&ecirc;m đ&aacute;ng nhớ nhất c&ugrave;ng người thương của m&igrave;nh tại Millennium Hanoi Hotel</p>\n<p>Millennium Hanoi Hotel l&agrave; lựa chọn s&aacute;ng gi&aacute; d&agrave;nh cho những ai đang t&igrave;m kiếm một trải nghiệm xa hoa đầy th&uacute; vị trong kỳ nghỉ của m&igrave;nh. Lưu tr&uacute; tại đ&acirc;y cũng l&agrave; c&aacute;ch để qu&yacute; kh&aacute;ch chiều chuộng bản th&acirc;n với những dịch vụ xuất sắc nhất v&agrave; khiến kỳ nghỉ của m&igrave;nh trở n&ecirc;n thật đ&aacute;ng nhớ.</p>\n<p>Bạn c&oacute; phải l&agrave; t&iacute;n đồ mua sắm? Lưu tr&uacute; tại Millennium Hanoi Hotel chắc chắn sẽ thoả m&atilde;n bạn với h&agrave;ng loạt c&aacute;c trung t&acirc;m mua sắm kề cận.</p>\n<p>Khi lưu tr&uacute; tại kh&aacute;ch sạn th&igrave; nội thất v&agrave; kiến tr&uacute;c hẳn l&agrave; hai yếu tố quan trọng khiến qu&yacute; kh&aacute;ch m&atilde;n nh&atilde;n. Với thiết kế độc đ&aacute;o, Millennium Hanoi Hotel mang đến kh&ocirc;ng gian lưu tr&uacute; l&agrave;m h&agrave;i l&ograve;ng qu&yacute; kh&aacute;ch.</p>\n<p>Từ sự kiện doanh nghiệp đến họp mặt c&ocirc;ng ty, Millennium Hanoi Hotel cung cấp đầy đủ c&aacute;c dịch vụ v&agrave; tiện nghi đ&aacute;p ứng mọi nhu cầu của qu&yacute; kh&aacute;ch v&agrave; đồng nghiệp.</p>\n<p>H&atilde;y tận hưởng thời gian vui vẻ c&ugrave;ng cả gia đ&igrave;nh với h&agrave;ng loạt tiện nghi giải tr&iacute; tại Millennium Hanoi Hotel, một kh&aacute;ch sạn tuyệt vời ph&ugrave; hợp cho mọi kỳ nghỉ b&ecirc;n người th&acirc;n.</p>\n<p>H&atilde;y tận hưởng trải nghiệm lưu tr&uacute; c&oacute; một kh&ocirc;ng hai tại to&agrave; nh&agrave; mang đậm dấu ấn lịch sử của Millennium Hanoi Hotel, điều qu&yacute; kh&aacute;ch kh&oacute; c&oacute; thể t&igrave;m thấy tại bất kỳ đ&acirc;u.</p>\n<p>Nếu dự định c&oacute; một kỳ nghỉ d&agrave;i, th&igrave; Millennium Hanoi Hotel ch&iacute;nh l&agrave; lựa chọn d&agrave;nh cho qu&yacute; kh&aacute;ch. Với đầy đủ tiện nghi với chất lượng dịch vụ tuyệt vời, Millennium Hanoi Hotel sẽ khiến qu&yacute; kh&aacute;ch cảm thấy thoải m&aacute;i như đang ở nh&agrave; vậy.</p>\n<p>Du lịch một m&igrave;nh cũng kh&ocirc;ng hề k&eacute;m phần th&uacute; vị v&agrave; Millennium Hanoi Hotel l&agrave; nơi th&iacute;ch hợp d&agrave;nh ri&ecirc;ng cho những ai đề cao sự ri&ecirc;ng tư trong kỳ lưu tr&uacute;.</p>\n<p>Dịch vụ tuyệt vời, cơ sở vật chất ho&agrave;n chỉnh v&agrave; c&aacute;c tiện nghi kh&aacute;ch sạn cung cấp sẽ khiến qu&yacute; kh&aacute;ch kh&ocirc;ng thể ph&agrave;n n&agrave;n trong suốt kỳ lưu tr&uacute; tại Millennium Hanoi Hotel.</p>\n<p>Quầy tiếp t&acirc;n 24 giờ lu&ocirc;n sẵn s&agrave;ng phục vụ qu&yacute; kh&aacute;ch từ thủ tục nhận ph&ograve;ng đến trả ph&ograve;ng hay bất kỳ y&ecirc;u cầu n&agrave;o. Nếu cần gi&uacute;p đỡ xin h&atilde;y li&ecirc;n hệ đội ngũ tiếp t&acirc;n, ch&uacute;ng t&ocirc;i lu&ocirc;n sẵn s&agrave;ng hỗ trợ qu&yacute; kh&aacute;ch.</p>\n<p>Tận hưởng những m&oacute;n ăn y&ecirc;u th&iacute;ch với phong c&aacute;ch ẩm thực đặc biệt từ Millennium Hanoi Hotel chỉ d&agrave;nh ri&ecirc;ng cho qu&yacute; kh&aacute;ch.</p>\n<p>S&oacute;ng WiFi phủ khắp c&aacute;c khu vực chung của kh&aacute;ch sạn cho ph&eacute;p qu&yacute; kh&aacute;ch lu&ocirc;n kết nối với gia đ&igrave;nh v&agrave; b&egrave; bạn.</p>\n<p>Millennium Hanoi Hotel l&agrave; kh&aacute;ch sạn sở hữu đầy đủ tiện nghi v&agrave; dịch vụ xuất sắc theo nhận định của hầu hết kh&aacute;ch lưu tr&uacute;.</p>\n<p>Với những tiện nghi sẵn c&oacute; Millennium Hanoi Hotel thực sự l&agrave; một nơi lưu tr&uacute; ho&agrave;n hảo.</p>', 'http://res.cloudinary.com/dxqh3xpza/image/upload/v1700454495/xl5wq2ra1dqewvz5qckn.jpg', 'Phòng Medium 202', 1, 300000, 4, 3),
