--
-- Đang đổ dữ liệu cho bảng `services`
--

INSERT INTO `services` (`id`, `description`, `image`, `name`, `price`, `quantity`, `category_id`) VALUES
(4, '<p data-cursor=\"-color-purple\"><strong>Buffet hải sản H&agrave; Nội</strong><span data-cursor=\"-color-green\">&nbsp;&ndash; một tinh hoa ẩm thực độc đ&aacute;o, đang chờ đ&oacute;n du kh&aacute;ch trong h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; Thủ đ&ocirc;. Với sự h&ograve;a quyện tinh tế giữa biển cả v&agrave; đất liền, 25 qu&aacute;n dưới đ&acirc;y kh&ocirc;ng chỉ l&agrave; nơi kh&aacute;m ph&aacute; ẩm thực, m&agrave; c&ograve;n l&agrave; trải nghiệm đậm chất&nbsp;</span><a href=\"https://vinwonders.com/vi/bai-viet-du-lich/cam-nang-du-lich-ha-noi-2023/\" target=\"_blank\" rel=\"noopener\" data-cursor=\"-color-blue\"><strong>du lịch H&agrave; Nội</strong></a><span data-cursor=\"-color-green\">.</span></p>\n<h2 data-cursor=\"-color-green\"><span id=\"1_buffet_hai_san_ha_noi_poseidon_menu_hon_200_mon\" data-cursor=\"-color-green\">1. Buffet hải sản H&agrave; Nội Poseidon menu hơn 200 m&oacute;n</span></h2>\n<ul>\n<li aria-level=\"1\"><strong>Địa chỉ:&nbsp;</strong><span data-cursor=\"-color-green\">Poseidon c&oacute; c&aacute;c cơ sở như: B2 &ndash; R4 &ndash; 18 Royal City (72A đường Nguyễn Tr&atilde;i, Thanh Xu&acirc;n), tầng 4 Hồ Gươm Plaza (số 102 đường Trần Ph&uacute;, H&agrave; Đ&ocirc;ng), B1 Vincom Mega Mall Times City (số 458 Minh Khai, Hai B&agrave; Trưng)&hellip;</span></li>\n</ul>\n<ul>\n<li aria-level=\"1\"><strong>Giờ mở cửa:&nbsp;</strong><span data-cursor=\"-color-green\">08h00 &ndash; 22h00</span></li>\n</ul>\n<ul>\n<li aria-level=\"1\"><strong>Khoảng gi&aacute;:&nbsp;</strong><span data-cursor=\"-color-green\">388.000 VNĐ &ndash; 398.000 VNĐ</span></li>\n</ul>\n<p data-cursor=\"-color-purple\"><span data-cursor=\"-color-green\">Nh&agrave; h&agrave;ng&nbsp;</span><em data-cursor=\"-color-purple\"><span data-cursor=\"-color-green\">buffet hải sản H&agrave; Nội</span></em><span data-cursor=\"-color-green\">&nbsp;Poseidon l&agrave; điểm đến l&yacute; tưởng cho những người y&ecirc;u th&iacute;ch hải sản với hơn 200 m&oacute;n ăn đa dạng trong thực đơn. Với kh&ocirc;ng gian sang trọng v&agrave; đội ngũ đầu bếp t&agrave;i ba, Poseidon tự h&agrave;o cung cấp những m&oacute;n hải sản tươi ngon nhất v&agrave; hải sản sẽ được nướng ngay trước mặt thực kh&aacute;ch, đảm bảo chất lượng tươi ngon.</span></p>\n<div id=\"attachment_99901\" class=\"wp-caption aligncenter\"><a href=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-2.jpg\" data-cursor=\"-color-blue\"><img class=\"wp-image-99901 size-full\" title=\"Buffet hải sản H&agrave; Nội\" src=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-2.jpg\" sizes=\"(max-width: 1095px) 100vw, 1095px\" srcset=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-2-330x220.jpg 330w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-2-459x306.jpg 459w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-2-504x336.jpg 504w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-2-677x451.jpg 677w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-2-768x512.jpg 768w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-2.jpg 1095w\" alt=\"Poseidon l&agrave; một trong những nh&agrave; h&agrave;ng buffet hải sản ngon nhất H&agrave; Nội\" width=\"1095\" height=\"730\" loading=\"lazy\" aria-describedby=\"caption-attachment-99901\" data-cursor=\"-color-blue\"></a>\n<p id=\"caption-attachment-99901\" class=\"wp-caption-text\" data-cursor=\"-color-purple\">Poseidon l&agrave; một trong những nh&agrave; h&agrave;ng buffet hải sản ngon nhất H&agrave; Nội (Ảnh: Sưu tầm)</p>\n</div>\n<h2 data-cursor=\"-color-green\"><span id=\"2_cuu_van_long_8211_nha_hang_buffet_hai_san_cau_giay_ha_noi_chat_luong\" data-cursor=\"-color-green\">2. Cửu V&acirc;n Long &ndash; nh&agrave; h&agrave;ng buffet hải sản Cầu Giấy H&agrave; Nội chất lượng</span></h2>\n<ul>\n<li aria-level=\"1\"><strong>Địa chỉ:&nbsp;</strong><span data-cursor=\"-color-green\">tầng 6 Discovery Complex, số 302 Cầu Giấy</span></li>\n</ul>\n<ul>\n<li aria-level=\"1\"><strong>Giờ mở cửa:&nbsp;</strong><span data-cursor=\"-color-green\">11h00 &ndash; 22h30</span></li>\n</ul>\n<ul>\n<li aria-level=\"1\"><strong>Khoảng gi&aacute;:&nbsp;</strong><span data-cursor=\"-color-green\">398.000 VNĐ/người</span></li>\n</ul>\n<p data-cursor=\"-color-purple\"><span data-cursor=\"-color-green\">Cửu V&acirc;n Long l&agrave; một nh&agrave; h&agrave;ng buffet hải sản H&agrave; Nội nổi bật với chất lượng đẳng cấp v&agrave; sự đa dạng trong thực đơn. Với kh&ocirc;ng gian rộng r&atilde;i v&agrave; phong c&aacute;ch Trung Hoa tinh tế, nh&agrave; h&agrave;ng tạo n&ecirc;n kh&ocirc;ng kh&iacute; ấm c&uacute;ng v&agrave; sang trọng cho thực kh&aacute;ch. Với hải sản cao cấp v&agrave; thực đơn nổi bật với những m&oacute;n dimsum độc đ&aacute;o, Cửu V&acirc;n Long hứa hẹn mang đến những trải nghiệm ẩm thực đặc biệt v&agrave; đ&aacute;ng nhớ cho những t&iacute;n đồ của hải sản.</span></p>\n<div id=\"attachment_99902\" class=\"wp-caption aligncenter\"><a href=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-3.jpg\" data-cursor=\"-color-blue\"><img class=\"wp-image-99902 size-full\" title=\"Buffet hải sản H&agrave; Nội\" src=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-3.jpg\" sizes=\"(max-width: 1500px) 100vw, 1500px\" srcset=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-3-336x192.jpg 336w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-3-473x271.jpg 473w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-3-560x321.jpg 560w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-3-753x431.jpg 753w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-3-768x440.jpg 768w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-3.jpg 1500w\" alt=\"ng buffet hải sản H&agrave; Nội c&oacute; t&ocirc;m h&ugrave;m\" width=\"1500\" height=\"859\" loading=\"lazy\" aria-describedby=\"caption-attachment-99902\" data-cursor=\"-color-blue\"></a>\n<p id=\"caption-attachment-99902\" class=\"wp-caption-text\" data-cursor=\"-color-purple\">Nh&agrave; h&agrave;ng buffet hải sản H&agrave; Nội c&oacute; t&ocirc;m h&ugrave;m (Ảnh: Sưu tầm)</p>\n</div>\n<div class=\"ins-wrapper-c12\"><form class=\"ins-form-c12 ins-lead-container-c12\">\n<div class=\"ins-title-c12\">Nhận th&ocirc;ng tin ưu đ&atilde;i sớm nhất từ VinKE &amp; Vinpearl Aquarium Times City v&agrave; VinWonders H&agrave; Nội</div>\n<div class=\"ins-rounder-c12\"><input class=\"ins-name-c12 ins-input-class-c12\" required=\"\" type=\"text\" placeholder=\"Họ t&ecirc;n\"><input class=\"ins-mail-c12 ins-input-class-c12\" name=\"email\" required=\"\" type=\"email\" placeholder=\"Nhập email của bạn\"><button class=\"ins-submit-button-c12\" type=\"submit\">Đăng k&yacute;</button></div>\n<div class=\"ins-gdpr-c12\"><input class=\"ins-checkbox-c12\" required=\"\" type=\"checkbox\">T&ocirc;i đồng &yacute; với c&aacute;c ch&iacute;nh s&aacute;ch bảo mật v&agrave; điều kiện điều khoản của VinWonders.</div>\n</form></div>\n<h2 data-cursor=\"-color-green\"><span id=\"3_nha_hang_fe_8211_dia_chi_an_buffet_hai_san_ngon_gia_re_o_ha_noi\" data-cursor=\"-color-green\">3. Nh&agrave; h&agrave;ng F&ecirc; &ndash; địa chỉ ăn buffet hải sản ngon, gi&aacute; rẻ ở H&agrave; Nội</span></h2>\n<ul>\n<li aria-level=\"1\"><strong>Địa chỉ:&nbsp;</strong><span data-cursor=\"-color-green\">306 Đ&ecirc; La Th&agrave;nh, Đống Đa</span></li>\n</ul>\n<ul>\n<li aria-level=\"1\"><strong>Giờ mở cửa:&nbsp;</strong><span data-cursor=\"-color-green\">09h30 &ndash; 14h00 v&agrave; 17h00 &ndash; 23h00</span></li>\n</ul>\n<ul>\n<li aria-level=\"1\"><strong>Khoảng gi&aacute;:&nbsp;</strong><span data-cursor=\"-color-green\">69.000 VNĐ &ndash; 250.000 VNĐ</span></li>\n</ul>\n<p data-cursor=\"-color-purple\"><span data-cursor=\"-color-green\">Nh&agrave; h&agrave;ng F&ecirc; l&agrave; địa chỉ buffet hải sản H&agrave; Nội ngon v&agrave; gi&aacute; rẻ được y&ecirc;u th&iacute;ch tại Thủ đ&ocirc;. Với kh&ocirc;ng gian thoải m&aacute;i v&agrave; ấm c&uacute;ng, F&ecirc; tạo điểm đến l&yacute; tưởng cho những t&iacute;n đồ ẩm thực muốn thưởng thức hải sản tươi ngon với mức gi&aacute; hợp l&yacute;. Thực đơn đa dạng của nh&agrave; h&agrave;ng F&ecirc; chứa đựng những m&oacute;n hải sản đa sắc m&agrave;u, từ t&ocirc;m h&ugrave;m, cua, s&ograve; điệp cho đến c&aacute;c loại hải sản tươi sống hấp dẫn kh&aacute;c.</span></p>\n<div id=\"attachment_99903\" class=\"wp-caption aligncenter\"><a href=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-4.jpg\" data-cursor=\"-color-blue\"><img class=\"wp-image-99903 size-full\" title=\"Buffet hải sản H&agrave; Nội\" src=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-4.jpg\" sizes=\"(max-width: 2048px) 100vw, 2048px\" srcset=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-4-336x194.jpg 336w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-4-473x273.jpg 473w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-4-560x324.jpg 560w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-4-753x435.jpg 753w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-4-768x444.jpg 768w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-4-1536x888.jpg 1536w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-4.jpg 2048w\" alt=\"Kh&ocirc;ng gian rộng r&atilde;i, ấm c&uacute;ng tại nh&agrave; h&agrave;ng\" width=\"2048\" height=\"1184\" loading=\"lazy\" aria-describedby=\"caption-attachment-99903\" data-cursor=\"-color-blue\"></a>\n<p id=\"caption-attachment-99903\" class=\"wp-caption-text\" data-cursor=\"-color-purple\">Kh&ocirc;ng gian rộng r&atilde;i, ấm c&uacute;ng tại nh&agrave; h&agrave;ng (Ảnh: Sưu tầm)</p>\n</div>\n<p data-cursor=\"-color-purple\"><strong><em data-cursor=\"-color-purple\">&gt;&gt;&gt; Dắt t&uacute;i:&nbsp;</em></strong><a href=\"https://vinwonders.com/vi/bai-viet-du-lich/quan-lau-nuong-ha-noi-ngon/\" target=\"_blank\" rel=\"noopener\" data-cursor=\"-color-blue\"><strong><em data-cursor=\"-color-purple\">No căng bụng với 13 qu&aacute;n lẩu nướng H&agrave; Nội ngon, đ&ocirc;ng kh&aacute;ch nhất 2023</em></strong></a><strong><em data-cursor=\"-color-purple\">&nbsp;</em></strong></p>\n<h2 data-cursor=\"-color-green\"><span id=\"4_nha_hang_buffet_sea_star\" data-cursor=\"-color-green\">4. Nh&agrave; h&agrave;ng Buffet Sea Star</span></h2>\n<ul>\n<li aria-level=\"1\"><strong>Địa chỉ:&nbsp;</strong><span data-cursor=\"-color-green\">số 3 đường Nguyễn Ho&agrave;ng, Mỹ Đ&igrave;nh 2, Nam Từ Li&ecirc;m</span></li>\n</ul>\n<ul>\n<li aria-level=\"1\"><strong>Giờ mở cửa:&nbsp;</strong><span data-cursor=\"-color-green\">09h00 &ndash; 14h00 v&agrave; 17h00 &ndash; 22h00</span></li>\n</ul>\n<ul>\n<li aria-level=\"1\"><strong>Khoảng gi&aacute;:&nbsp;</strong><span data-cursor=\"-color-green\">199.000 VNĐ &ndash; 289.000 VNĐ</span></li>\n</ul>\n<p data-cursor=\"-color-purple\"><span data-cursor=\"-color-green\">Nh&agrave; h&agrave;ng Buffet Sea Star l&agrave; một địa chỉ ăn buffet hải sản ở H&agrave; Nội ngon, với thực đơn đa dạng bao gồm buffet nướng v&agrave; lẩu hấp dẫn. Kh&ocirc;ng chỉ c&oacute; những m&oacute;n ăn hấp dẫn từ nhiều loại thịt v&agrave; rau củ, m&agrave; c&ograve;n c&oacute; sự xuất hiện đ&aacute;ng ch&uacute; &yacute; của nhiều loại hải sản tươi sống như bề bề, c&aacute; hồi, t&ocirc;m c&agrave;ng xanh, h&agrave;u sữa, mực mai, v&agrave; nhiều loại hải sản kh&aacute;c.&nbsp;</span></p>\n<div id=\"attachment_99904\" class=\"wp-caption aligncenter\"><a href=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-5.jpg\" data-cursor=\"-color-blue\"><img class=\"wp-image-99904 size-full\" title=\"Buffet hải sản H&agrave; Nội\" src=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-5.jpg\" sizes=\"(max-width: 1280px) 100vw, 1280px\" srcset=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-5-293x220.jpg 293w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-5-408x306.jpg 408w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-5-448x336.jpg 448w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-5-601x451.jpg 601w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-5-768x576.jpg 768w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-5.jpg 1280w\" alt=\"Review buffet hải sản H&agrave; Nội\" width=\"1280\" height=\"960\" loading=\"lazy\" aria-describedby=\"caption-attachment-99904\" data-cursor=\"-color-blue\"></a>\n<p id=\"caption-attachment-99904\" class=\"wp-caption-text\" data-cursor=\"-color-purple\">Review buffet hải sản H&agrave; Nội (Ảnh: Sưu tầm)</p>\n</div>\n<h2 data-cursor=\"-color-green\"><span id=\"5_an_buffet_hai_san_o_dau_ngon_ha_noi_nha_hang_sen_tay_ho\" data-cursor=\"-color-green\">5. Ăn buffet hải sản ở đ&acirc;u ngon H&agrave; Nội? Nh&agrave; h&agrave;ng Sen T&acirc;y Hồ</span></h2>\n<ul>\n<li aria-level=\"1\"><strong>Địa chỉ:&nbsp;</strong><span data-cursor=\"-color-green\">614 đường Lạc Long Qu&acirc;n, T&acirc;y Hồ</span></li>\n</ul>\n<ul>\n<li aria-level=\"1\"><strong>Giờ mở cửa:&nbsp;</strong><span data-cursor=\"-color-green\">10h30 &ndash; 20h30</span></li>\n</ul>\n<ul>\n<li aria-level=\"1\"><strong>Khoảng gi&aacute;:&nbsp;</strong><span data-cursor=\"-color-green\">230.000 VNĐ &ndash; 420.000 VNĐ</span></li>\n</ul>\n<p data-cursor=\"-color-purple\"><span data-cursor=\"-color-green\">Nếu bạn chưa biết&nbsp;</span><a href=\"https://vinwonders.com/vi/bai-viet-du-lich/an-gi-o-ho-tay/\" target=\"_blank\" rel=\"noopener\" data-cursor=\"-color-blue\"><strong>ăn g&igrave; ở Hồ T&acirc;y</strong></a><span data-cursor=\"-color-green\">&nbsp;hay đang t&igrave;m kiếm c&aacute;c qu&aacute;n buffet hải sản ngon ở H&agrave; Nội, kh&ocirc;ng thể kh&ocirc;ng nhắc đến nh&agrave; h&agrave;ng Sen T&acirc;y Hồ. Với kh&ocirc;ng gian rộng r&atilde;i, Sen T&acirc;y Hồ tạo n&ecirc;n kh&ocirc;ng kh&iacute; ấm c&uacute;ng v&agrave; thoải m&aacute;i cho thực kh&aacute;ch. Được biết đến l&agrave; địa chỉ quen thuộc của d&acirc;n s&agrave;nh ăn hải sản, Sen T&acirc;y Hồ tự h&agrave;o sở hữu thực đơn đa dạng với hơn 150 m&oacute;n hải sản hấp dẫn.</span></p>\n<div id=\"attachment_99931\" class=\"wp-caption aligncenter\"><a href=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-6.jpg\" data-cursor=\"-color-blue\"><img class=\"wp-image-99931 size-full\" title=\"Buffet hải sản H&agrave; Nội\" src=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-6.jpg\" sizes=\"(max-width: 900px) 100vw, 900px\" srcset=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-6-293x220.jpg 293w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-6-408x306.jpg 408w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-6-448x336.jpg 448w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-6-601x451.jpg 601w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-6-768x576.jpg 768w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-6.jpg 900w\" alt=\"Nh&agrave; h&agrave;ng buffet hải sản ngon H&agrave; Nội\" width=\"900\" height=\"675\" loading=\"lazy\" aria-describedby=\"caption-attachment-99931\" data-cursor=\"-color-blue\"></a>\n<p id=\"caption-attachment-99931\" class=\"wp-caption-text\" data-cursor=\"-color-purple\">Nh&agrave; h&agrave;ng buffet hải sản ngon H&agrave; Nội (Ảnh: Sưu tầm)</p>\n</div>\n<h2 data-cursor=\"-color-green\"><span id=\"6_nha_hang_seon_mang_phong_cach_han_quoc\" data-cursor=\"-color-green\">6. Nh&agrave; h&agrave;ng Seon mang phong c&aacute;ch H&agrave;n Quốc</span></h2>\n<ul>\n<li aria-level=\"1\"><strong>Địa chỉ:&nbsp;</strong><span data-cursor=\"-color-green\">số 3, ng&otilde; 216 Trung K&iacute;nh, Cầu Giấy</span></li>\n</ul>\n<ul>\n<li aria-level=\"1\"><strong>Giờ mở cửa:&nbsp;</strong><span data-cursor=\"-color-green\">08h30 &ndash; 22h00</span></li>\n</ul>\n<ul>\n<li aria-level=\"1\"><strong>Khoảng gi&aacute;:&nbsp;</strong><span data-cursor=\"-color-green\">50.000 VNĐ &ndash; 100.000 VNĐ</span></li>\n</ul>\n<p data-cursor=\"-color-purple\"><span data-cursor=\"-color-green\">Nh&agrave; h&agrave;ng Seon l&agrave; top nh&agrave; h&agrave;ng buffet hải sản H&agrave; Nội ngon, mang phong c&aacute;ch H&agrave;n Quốc độc đ&aacute;o. Với kh&ocirc;ng gian trang tr&iacute; ấm c&uacute;ng v&agrave; phong c&aacute;ch truyền thống H&agrave;n Quốc, Seon mang đến cho thực kh&aacute;ch một trải nghiệm ẩm thực đặc biệt. Thực đơn của nh&agrave; h&agrave;ng Seon hấp dẫn với 4 vị nước lẩu độc đ&aacute;o cho bạn tha hồ lựa chọn.</span></p>\n<div id=\"attachment_99905\" class=\"wp-caption aligncenter\"><a href=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-7.jpg\" data-cursor=\"-color-blue\"><img class=\"wp-image-99905 size-full\" title=\"Buffet hải sản H&agrave; Nội\" src=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-7.jpg\" sizes=\"(max-width: 957px) 100vw, 957px\" srcset=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-7-336x207.jpg 336w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-7-473x291.jpg 473w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-7-546x336.jpg 546w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-7-733x451.jpg 733w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-7-768x473.jpg 768w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-7.jpg 957w\" alt=\"C&aacute;c nh&agrave; h&agrave;ng buffet hải sản ngon ở H&agrave; Nội \" width=\"957\" height=\"589\" loading=\"lazy\" aria-describedby=\"caption-attachment-99905\" data-cursor=\"-color-blue\"></a>\n<p id=\"caption-attachment-99905\" class=\"wp-caption-text\" data-cursor=\"-color-purple\">C&aacute;c nh&agrave; h&agrave;ng buffet hải sản ngon ở H&agrave; Nội (Ảnh: Sưu tầm)</p>\n</div>\n<h2 data-cursor=\"-color-green\"><span id=\"7_buffet_hai_san_ha_noi_cao_cap_8211_bay_seafood_buffet\" data-cursor=\"-color-green\">7. Buffet hải sản H&agrave; Nội cao cấp &ndash; Bay Seafood Buffet</span></h2>\n<ul>\n<li aria-level=\"1\"><strong>Địa chỉ:&nbsp;</strong><span data-cursor=\"-color-green\">số 167 Ho&agrave;ng Ng&acirc;n, quận Cầu Giấy, số 14 T&ocirc;ng Đản, quận Ho&agrave;n Kiếm, số 214 H&agrave;o Nam, quận Đống Đa (tầng 6 t&ograve;a NHS)</span></li>\n</ul>\n<ul>\n<li aria-level=\"1\"><strong>Giờ mở cửa:&nbsp;</strong><span data-cursor=\"-color-green\">11h00 &ndash; 13h00 v&agrave; 18h00 &ndash; 20h00</span></li>\n</ul>\n<ul>\n<li aria-level=\"1\"><strong>Khoảng gi&aacute;:&nbsp;</strong><span data-cursor=\"-color-green\">398.000 VNĐ &ndash; 428.000 VNĐ</span></li>\n</ul>\n<p data-cursor=\"-color-purple\"><span data-cursor=\"-color-green\">Thực đơn Bay Seafood đa dạng với nhiều m&oacute;n hải sản tươi sống hấp dẫn như ghẹ, ốc hương, mực, t&ocirc;m, h&agrave;u, ốc m&oacute;ng tay, ngao hai cồi&hellip; Hải sản được chế biến đầy hấp dẫn như hấp, nướng mỡ h&agrave;nh,&nbsp; bỏ l&ograve; ph&ocirc; mai, rang muối, mang đến cho thực kh&aacute;ch một bữa tiệc biển ngon miệng v&agrave; đậm chất ẩm thực.</span></p>\n<div id=\"attachment_99906\" class=\"wp-caption aligncenter\"><a href=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-8.jpg\" data-cursor=\"-color-blue\"><img class=\"wp-image-99906 size-full\" title=\"Buffet hải sản H&agrave; Nội\" src=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-8.jpg\" sizes=\"(max-width: 1500px) 100vw, 1500px\" srcset=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-8-336x192.jpg 336w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-8-473x271.jpg 473w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-8-560x321.jpg 560w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-8-753x431.jpg 753w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-8-768x440.jpg 768w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-8.jpg 1500w\" alt=\"Top buffet hải sản H&agrave; Nội cao cấp nhất\" width=\"1500\" height=\"859\" loading=\"lazy\" aria-describedby=\"caption-attachment-99906\" data-cursor=\"-color-blue\"></a>\n<p id=\"caption-attachment-99906\" class=\"wp-caption-text\" data-cursor=\"-color-purple\">Top buffet hải sản H&agrave; Nội cao cấp nhất (Ảnh: Sưu tầm)</p>\n</div>\n<h2 data-cursor=\"-color-green\"><span id=\"8_set_buffet_8211_hai_san_tuoi_ngon_duoc_long_thuc_khach\" data-cursor=\"-color-green\">8. SET Buffet &ndash; hải sản tươi ngon, được l&ograve;ng thực kh&aacute;ch</span></h2>\n<ul>\n<li aria-level=\"1\"><strong>Địa chỉ:&nbsp;</strong><span data-cursor=\"-color-green\">tầng 6 t&ograve;a NHS, 214 H&agrave;o Nam, Đống Đa</span></li>\n</ul>\n<ul>\n<li aria-level=\"1\"><strong>Giờ mở cửa:&nbsp;</strong><span data-cursor=\"-color-green\">11h00 &ndash; 14h00 v&agrave; 18h00 &ndash; 22h00</span></li>\n</ul>\n<ul>\n<li aria-level=\"1\"><strong>Khoảng gi&aacute;:&nbsp;</strong><span data-cursor=\"-color-green\">300.000 VNĐ &ndash; 400.000 VNĐ</span></li>\n</ul>\n<p data-cursor=\"-color-purple\"><span data-cursor=\"-color-green\">SET Buffet l&agrave; một qu&aacute;n buffet hải sản H&agrave; Nội hấp dẫn thực kh&aacute;ch bởi sự đa dạng v&agrave; hương vị tuyệt hảo. Với hơn 200 loại hải sản tươi ngon, SET Buffet mang đến một bữa tiệc biển độc đ&aacute;o v&agrave; phong ph&uacute;. Ngo&agrave;i những m&oacute;n hải sản tươi ngon, nh&agrave; h&agrave;ng SET Buffet c&ograve;n chinh phục thực kh&aacute;ch bằng c&aacute;c m&oacute;n ăn đa dạng từ ch&acirc;u &Aacute; đến &Acirc;u.</span></p>\n<div id=\"attachment_99907\" class=\"wp-caption aligncenter\"><a href=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-9.jpg\" data-cursor=\"-color-blue\"><img class=\"wp-image-99907 size-full\" title=\"Buffet hải sản H&agrave; Nội\" src=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-9.jpg\" sizes=\"(max-width: 1500px) 100vw, 1500px\" srcset=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-9-336x192.jpg 336w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-9-473x271.jpg 473w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-9-560x321.jpg 560w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-9-753x431.jpg 753w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-9-768x440.jpg 768w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-9.jpg 1500w\" alt=\"SET buffet sở hữu kh&ocirc;ng gian rộng r&atilde;i, thoải m&aacute;i\" width=\"1500\" height=\"859\" loading=\"lazy\" aria-describedby=\"caption-attachment-99907\" data-cursor=\"-color-blue\"></a>\n<p id=\"caption-attachment-99907\" class=\"wp-caption-text\" data-cursor=\"-color-purple\">SET buffet sở hữu kh&ocirc;ng gian rộng r&atilde;i, thoải m&aacute;i (Ảnh: Sưu tầm)</p>\n</div>\n<p data-cursor=\"-color-purple\"><strong><em data-cursor=\"-color-purple\">&gt;&gt;&gt; Lưu ngay:&nbsp;</em></strong><a href=\"https://vinwonders.com/vi/bai-viet-du-lich/quan-lau-ngon-ha-noi/\" target=\"_blank\" rel=\"noopener\" data-cursor=\"-color-blue\"><strong><em data-cursor=\"-color-purple\">Địa chỉ 23 qu&aacute;n lẩu ngon H&agrave; Nội, tụ tập bạn b&egrave; ng&agrave;y lạnh ngon hết sảy</em></strong></a><strong><em data-cursor=\"-color-purple\">&nbsp;</em></strong></p>\n<h2 data-cursor=\"-color-green\"><span id=\"9_nha_hang_buffet_hai_san_ha_noi_ngon_view_dep_8211_adobi_buffet\" data-cursor=\"-color-green\">9. Nh&agrave; h&agrave;ng buffet hải sản H&agrave; Nội ngon, view đẹp &ndash; Adobi Buffet</span></h2>\n<ul>\n<li aria-level=\"1\"><strong>Địa chỉ:&nbsp;</strong><span data-cursor=\"-color-green\">tầng 3 t&ograve;a Golden Field, số 24 Nguyễn Cơ Thạch, quận Nam Từ Li&ecirc;m</span></li>\n</ul>\n<ul>\n<li aria-level=\"1\"><strong>Giờ mở cửa:&nbsp;</strong><span data-cursor=\"-color-green\">11h00 &ndash; 13h00 v&agrave; 18h00 &ndash; 20h30</span></li>\n</ul>\n<ul>\n<li aria-level=\"1\"><strong>Khoảng gi&aacute;:&nbsp;</strong><span data-cursor=\"-color-green\">386.000 VNĐ &ndash; 398.000 VNĐ&nbsp;</span></li>\n</ul>\n<p data-cursor=\"-color-purple\"><span data-cursor=\"-color-green\">Với thực đơn đa dạng bao gồm nhiều loại hải sản tươi ngon như t&ocirc;m h&ugrave;m, cua, s&ograve; điệp, h&agrave;u sữa, mực, v&agrave; nhiều m&oacute;n hải sản hấp dẫn kh&aacute;c, Adobi Buffet mang đến trải nghiệm buffet hải sản H&agrave; Nội đẳng cấp. Đặc biệt, Adobi Buffet c&oacute; kh&ocirc;ng gian được thiết kế sang trọng v&agrave; view đẹp, tạo n&ecirc;n kh&ocirc;ng kh&iacute; ấm c&uacute;ng v&agrave; thoải m&aacute;i cho thực kh&aacute;ch.</span></p>\n<div id=\"attachment_99908\" class=\"wp-caption aligncenter\"><a href=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-10.jpg\" data-cursor=\"-color-blue\"><img class=\"wp-image-99908 size-full\" title=\"Buffet hải sản H&agrave; Nội\" src=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-10.jpg\" sizes=\"(max-width: 2048px) 100vw, 2048px\" srcset=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-10-336x169.jpg 336w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-10-473x238.jpg 473w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-10-560x282.jpg 560w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-10-753x379.jpg 753w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-10-768x387.jpg 768w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-10-1536x774.jpg 1536w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-10.jpg 2048w\" alt=\"C&aacute;c quầy đồ ăn được sắp xếp chỉn chu, đẹp mắt\" width=\"2048\" height=\"1032\" loading=\"lazy\" aria-describedby=\"caption-attachment-99908\" data-cursor=\"-color-blue\"></a>\n<p id=\"caption-attachment-99908\" class=\"wp-caption-text\" data-cursor=\"-color-purple\">C&aacute;c quầy đồ ăn được sắp xếp chỉn chu, đẹp mắt (Ảnh: Sưu tầm)</p>\n</div>\n<h2 data-cursor=\"-color-green\"><span id=\"10_buffet_hai_san_gia_re_ha_noi_hut_khach_8211_chez_manon\" data-cursor=\"-color-green\">10. Buffet hải sản gi&aacute; rẻ H&agrave; Nội h&uacute;t kh&aacute;ch &ndash; Chez Manon</span></h2>\n<ul>\n<li aria-level=\"1\"><strong>Địa chỉ:&nbsp;</strong><span data-cursor=\"-color-green\">số 01 L&ecirc; Th&aacute;nh T&ocirc;ng, Ho&agrave;n Kiếm</span></li>\n</ul>\n<ul>\n<li aria-level=\"1\"><strong>Giờ mở cửa:&nbsp;</strong><span data-cursor=\"-color-green\">06h00 &ndash; 23h30</span></li>\n</ul>\n<ul>\n<li aria-level=\"1\"><strong>Khoảng gi&aacute;:&nbsp;</strong><span data-cursor=\"-color-green\">100.000 VNĐ &ndash; 550.000 VNĐ</span></li>\n</ul>\n<p data-cursor=\"-color-purple\"><span data-cursor=\"-color-green\">Chez Manon l&agrave; một nh&agrave; h&agrave;ng buffet hải sản H&agrave; Nội ngon, lu&ocirc;n h&uacute;t kh&aacute;ch bởi sự hấp dẫn v&agrave; phong ph&uacute; của thực đơn. Với gi&aacute; cả hợp l&yacute;, kh&aacute;ch h&agrave;ng c&oacute; cơ hội thưởng thức từ thịt thăn b&ograve; Mỹ, ba chỉ b&ograve; Mỹ, sườn cừu nướng đến hải sản tươi ngon v&agrave; đa dạng như t&ocirc;m, cua, s&ograve; điệp, h&agrave;u, mực, c&aacute;c loại sashimi thơm ngon.</span></p>\n<div id=\"attachment_99909\" class=\"wp-caption aligncenter\"><a href=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-11.jpg\" data-cursor=\"-color-blue\"><img class=\"wp-image-99909 size-full\" title=\"Buffet hải sản H&agrave; Nội\" src=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-11.jpg\" sizes=\"(max-width: 2048px) 100vw, 2048px\" srcset=\"https://static.vinwonders.com/production/buffet-hai-san-ha-noi-11-293x220.jpg 293w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-11-408x306.jpg 408w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-11-448x336.jpg 448w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-11-601x451.jpg 601w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-11-768x576.jpg 768w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-11-1536x1152.jpg 1536w, https://static.vinwonders.com/production/buffet-hai-san-ha-noi-11.jpg 2048w\" alt=\"Buffet hải sản gi&aacute; rẻ H&agrave; Nội h&uacute;t kh&aacute;ch - Chez Manon\" width=\"2048\" height=\"1536\" loading=\"lazy\" aria-describedby=\"caption-attachment-99909\" data-cursor=\"-color-blue\"></a>\n<p id=\"caption-attachment-99909\" class=\"wp-caption-text\" data-cursor=\"-color-purple\">Địa chỉ ăn buffet hải sản ngon ở H&agrave; Nội (Ảnh: Sưu tầm)</p>\n</div>\n<h2 data-cursor=\"-color-green\"><span id=\"11_buffet_nuong_lau_hai_san_cheap_eats\" data-cursor=\"-color-green\">11. Buffet nướng lẩu hải sản Cheap Eats</span></h2>\n<ul>\n<li aria-level=\"1\"><strong>Địa chỉ:&nbsp;</strong><span data-cursor=\"-color-green\">tầng 4 Mac Plaza, số 10 đường Trần Ph&uacute;, H&agrave; Đ&ocirc;ng</span></li>\n</ul>\n<ul>\n<li aria-level=\"1\"><strong>Giờ mở cửa:&nbsp;</strong><span data-cursor=\"-color-green\">17h00 &ndash; 21h30</span></li>\n</ul>\n<ul>\n<li aria-level=\"1\"><strong>Khoảng gi&aacute;:&nbsp;</strong><span data-cursor=\"-color-green\">200.000 VNĐ &ndash; 280.000 VNĐ</span></li>\n</ul>\n<p data-cursor=\"-color-purple\"><span data-cursor=\"-color-green\">Cheap Eats l&agrave; một nh&agrave; h&agrave;ng buffet nướng lẩu hải sản gi&aacute; rẻ, l&agrave; lựa chọn l&yacute; tưởng cho những t&iacute;n đồ y&ecirc;u th&iacute;ch hải sản v&agrave; ẩm thực đa dạng. Với thực đơn đa dạng, kh&ocirc;ng chỉ hải sản, bạn c&ograve;n được thưởng thức nhiều m&oacute;n ngon hấp dẫn như sườn heo ti&ecirc;u đen, s&ograve; Nhật mỡ h&agrave;nh, b&ograve; Mỹ sốt Bulgogi H&agrave;n Quốc&hellip;</span></p>', 'http://res.cloudinary.com/dxqh3xpza/image/upload/v1700308152/whg0dv6crav7yuqlkhcw.jpg', 'Buffet hải sản', 450000, 1, 9),
(6, '<div><strong>Với một kh&ocirc;ng gian ph&ograve;ng tắm đẹp v&agrave; hiện đại th&igrave; việc mua v&agrave; sử dụng ph&ograve;ng x&ocirc;ng hơi massage l&agrave; hợp l&yacute;. Vậy &ldquo;&nbsp;<a href=\"https://noithatphuongdong.vn/tin-tuc/2072/loi-ich-khi-su-dung-phong-xong-hoi-massage\">Lợi &iacute;ch khi sử dụng ph&ograve;ng x&ocirc;ng hơi massage</a>&nbsp;&ldquo; l&agrave; g&igrave; ? N&oacute; c&oacute; thực sự tốt như những g&igrave; quảng c&aacute;o hay giới thiệu của c&aacute;c nh&acirc;n vi&ecirc;n b&aacute;n h&agrave;ng hay kh&ocirc;ng ? Th&igrave; qu&yacute; kh&aacute;ch h&atilde;y c&ugrave;ng NỘI THẤT PHƯƠNG Đ&Ocirc;NG t&igrave;m hiểu qua b&agrave;i tư vấn n&agrave;y nh&eacute;.</strong></div>\n<p><strong>B&agrave;i tư vấn li&ecirc;n quan :&nbsp;</strong></p>', 'http://res.cloudinary.com/dxqh3xpza/image/upload/v1700497818/fblnofuh9uz5cxvkeddt.jpg', 'Massage xông hơi cao cấp', 700000, 1, 7),
